INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12329, 'nvi', 'Neemias', 'ne', 3, 1, 'O sumo sacerdote Eliasibe e os seus colegas sacerdotes começaram o seu trabalho e reconstruíram a porta das Ovelhas. Eles a consagraram e colocaram as portas no lugar. Depois construíram o muro até a torre dos Cem, que consagraram, e até a torre de Hananeel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12330, 'nvi', 'Neemias', 'ne', 3, 2, 'Os homens de Jericó construíram o trecho seguinte, e Zacur, filho de Inri, construiu logo adiante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12331, 'nvi', 'Neemias', 'ne', 3, 3, 'A porta do Peixe foi reconstruída pelos filhos de Hassenaá. Eles puseram as vigas e colocaram as portas, os ferrolhos e as trancas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12332, 'nvi', 'Neemias', 'ne', 3, 4, 'Meremote, filho de Urias, neto de Coz, fez os reparos do trecho seguinte. Ao lado dele Mesulão, filho de Berequias, neto de Mesezabel, fez os reparos, e ao lado dele Zadoque, filho de Baaná, também fez os reparos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12333, 'nvi', 'Neemias', 'ne', 3, 5, 'O trecho seguinte foi reparado pelos homens de Tecoa, mas os nobres dessa cidade não quiseram se juntar ao serviço, rejeitando a orientação de seus supervisores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12334, 'nvi', 'Neemias', 'ne', 3, 6, 'A porta Jesaná foi consertada por Joiada, filho de Paséia, e por Mesulão, filho de Besodias. Eles puseram as vigas e colocaram as portas, os ferrolhos e as trancas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12335, 'nvi', 'Neemias', 'ne', 3, 7, 'No trecho seguinte, os reparos foram feitos por homens de Gibeom e de Mispá, Melatias de Gibeom e Jadom de Meronote, locais que estavam sob a autoridade do governador da província do Trans-Eufrates.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12336, 'nvi', 'Neemias', 'ne', 3, 8, 'Uziel, filho de Haraías, um dos ourives, fez os reparos do trecho seguinte; e Hananias, um dos perfumistas, fez os reparos ao seu lado. Eles resconstruíram Jerusalém até o muro Largo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12337, 'nvi', 'Neemias', 'ne', 3, 9, 'Refaías, filho de Hur, governador da metade do distrito de Jerusalém, fez os reparos do trecho seguinte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12338, 'nvi', 'Neemias', 'ne', 3, 10, 'Ao seu lado, Jedaías, filho de Harumafe, fez os reparos em frente da sua casa, e Hatus, filho de Hasabnéias, fez os reparos ao lado dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12339, 'nvi', 'Neemias', 'ne', 3, 11, 'Malquias, filho de Harim, e Hassube, filho de Paate-Moabe, repararam outro trecho e a torre dos Fornos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12340, 'nvi', 'Neemias', 'ne', 3, 12, 'Salum, filho de Haloés, governador da outra metade do distrito de Jerusalém, fez os reparos do trecho seguinte com a ajuda de suas filhas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12341, 'nvi', 'Neemias', 'ne', 3, 13, 'A porta do Vale foi reparada por Hanum e pelos moradores de Zanoa. Eles a reconstruíram e colocaram as portas, os ferrolhos e as trancas no lugar. Também repararam quatrocentos e cinqüenta metros do muro, até a porta do Esterco.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12342, 'nvi', 'Neemias', 'ne', 3, 14, 'A porta do Esterco foi reparada por Malquias, filho de Recabe, governador do distrito de Bete-Haquerém. Ele a reconstruiu e colocou as portas, os ferrolhos e as trancas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12343, 'nvi', 'Neemias', 'ne', 3, 15, 'A porta da Fonte foi reparada por Salum, filho de Col-Hozé, governador do distrito de Mispá. Ele a reconstruiu, a cobriu e colocou as portas, os ferrolhos e as trancas no lugar. Também fez os reparos do muro do tanque de Siloé, junto ao jardim do Rei, até aos degraus que descem da cidade de Davi.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12344, 'nvi', 'Neemias', 'ne', 3, 16, 'Além dele, Neemias, filho de Azbuque, governador de meio distrito de Bete-Zur, fez os reparos até em frente dos túmulos de Davi, até o açude artificial e a casa dos soldados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12345, 'nvi', 'Neemias', 'ne', 3, 17, 'Depois dele os reparos foram feitos pelos levitas que estavam sob a responsabilidade de Reum, filho de Bani. Junto a ele Hasabias, governador de meio distrito de Queila, fez os reparos em seu distrito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12346, 'nvi', 'Neemias', 'ne', 3, 18, 'Depois dele os reparos foram feitos pelos seus compatriotas que estavam sob a responsabilidade de Binui, filho de Henadade, governador de meio distrito de Queila.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12347, 'nvi', 'Neemias', 'ne', 3, 19, 'Ao seu lado Ézer, filho de Jesua, governador de Mispá, reconstruiu outro trecho, começando de um ponto que fica em frente da subida para a casa das armas, indo até à esquina do muro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12348, 'nvi', 'Neemias', 'ne', 3, 20, 'Depois dele Baruque, filho de Zabai, reparou com zelo outro trecho, desde a esquina do muro até à entrada da casa do sumo sacerdote Eliasibe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12349, 'nvi', 'Neemias', 'ne', 3, 21, 'Em seguida Meremote, filho de Urias, neto de Coz, reparou outro trecho, desde a entrada da casa de Eliasibe até o fim dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12350, 'nvi', 'Neemias', 'ne', 3, 22, 'Os demais reparos foram feitos pelos sacerdotes das redondezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12351, 'nvi', 'Neemias', 'ne', 3, 23, 'Depois, Benjamim e Hassube fizeram os reparos em frente da sua casa; e ao lado deles Azarias, filho de Maaséias, filho de Ananias, fez os reparos ao lado de sua casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12352, 'nvi', 'Neemias', 'ne', 3, 24, 'Depois dele, Binui, filho de Henadade, reparou outro trecho, desde a casa de Azarias até à esquina do muro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12353, 'nvi', 'Neemias', 'ne', 3, 25, 'e Palal, filho de Uzai, trabalhou em frente da esquina do muro e da torre que sai do palácio superior, perto do pátio da guarda. Junto a ele, Pedaías, filho de Parós,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12354, 'nvi', 'Neemias', 'ne', 3, 26, 'e os servos do templo que viviam na colina de Ofel fizeram os reparos até em frente da porta das Águas, na direção do leste e da torre que ali sobressaía.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12355, 'nvi', 'Neemias', 'ne', 3, 27, 'Depois dele os homens de Tecoa repararam outro trecho, desde a grande torre até o muro de Ofel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12356, 'nvi', 'Neemias', 'ne', 3, 28, 'Acima da porta dos Cavalos, os sacerdotes fizeram os reparos, cada um em frente da sua própria casa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12357, 'nvi', 'Neemias', 'ne', 3, 29, 'Depois deles Zadoque, filho de Imer, fez os reparos em frente da sua casa. Ao seu lado Semaías, filho de Secanias, o guarda da porta Oriental, fez os reparos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12358, 'nvi', 'Neemias', 'ne', 3, 30, 'Depois, Hananias, filho de Selemias, e Hanum, filho de Zalafe, fez os reparos do outro trecho. Ao seu lado, Mesulão, filho de Berequias, fez os reparos em frente da sua moradia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12359, 'nvi', 'Neemias', 'ne', 3, 31, 'Depois dele, Malquias, um ourives, fez os reparos do muro até à casa dos servos do templo e dos comerciantes, em frente à porta da Inspeção, até o posto de vigia da esquina;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12360, 'nvi', 'Neemias', 'ne', 3, 32, 'e entre a sala acima da esquina e a porta das Ovelhas os ourives e os comerciantes fizeram os reparos.');
