INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12403, 'nvi', 'Neemias', 'ne', 6, 1, 'Quando Sambalate, Tobias, Gesém, o árabe, e o restante de nossos inimigos souberam que eu havia reconstruído o muro e que não havia ficado nenhuma brecha, embora até então eu ainda não tivesse colocado as portas nos seus lugares,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12404, 'nvi', 'Neemias', 'ne', 6, 2, 'Sambalate e Gesém mandaram-me a seguinte mensagem: "Venha, vamos nos encontrar num dos povoados da planície de Ono". Eles, contudo, estavam tramando fazer-me mal;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12405, 'nvi', 'Neemias', 'ne', 6, 3, 'por isso enviei-lhes mensageiros com esta resposta: "Estou executando um grande projeto e não posso descer. Por que parar a obra para ir encontrar-me com vocês? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12406, 'nvi', 'Neemias', 'ne', 6, 4, 'Eles me mandaram quatro vezes a mesma mensagem, e em todas elas dei-lhes a mesma resposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12407, 'nvi', 'Neemias', 'ne', 6, 5, 'Então, na quinta vez, Sambalate mandou-me um dos seus homens de confiança com a mesma mensagem; ele tinha na mão uma carta aberta');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12408, 'nvi', 'Neemias', 'ne', 6, 6, 'em que estava escrito: "Dizem entre as nações, e Gesém diz que é verdade, que você e os judeus estão tramando uma revolta e que, por isso, estão reconstruindo o muro. Além do mais, conforme dizem, você está na iminência de se tornar o rei deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12409, 'nvi', 'Neemias', 'ne', 6, 7, 'e até nomeou profetas para fazerem em Jerusalém a seguinte proclamação a seu respeito: ‘Há um rei em Judá! ’ Ora, essa informação será levada ao rei; por isso, vamos conversar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12410, 'nvi', 'Neemias', 'ne', 6, 8, 'Eu lhe mandei esta resposta: Nada disso que você diz está acontecendo; é pura invenção da sua cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12411, 'nvi', 'Neemias', 'ne', 6, 9, 'Estavam todos tentando intimidar-nos, pensando: "Eles serão enfraquecidos e não concluirão a obra". Eu, porém, orei: Agora, fortalece as minhas mãos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12412, 'nvi', 'Neemias', 'ne', 6, 10, 'Um dia fui à casa de Semaías, filho de Delaías, neto de Meetabel, que estava trancado portas adentro. Ele disse: "Vamos encontrar-nos na casa de Deus, no templo, a portas fechadas, pois estão querendo matá-lo; eles virão esta noite".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12413, 'nvi', 'Neemias', 'ne', 6, 11, 'Todavia, eu lhe respondi: Acha que um homem como eu deveria fugir? Alguém como eu deveria entrar no templo para salvar a vida? Não, eu não irei!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12414, 'nvi', 'Neemias', 'ne', 6, 12, 'Percebi que Deus não o tinha enviado, e que ele tinha profetizado contra mim porque Tobias e Sambalate o tinham contratado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12415, 'nvi', 'Neemias', 'ne', 6, 13, 'Ele tinha sido pago para me intimidar, a fim de que eu cometesse um pecado agindo assim, e então eles poderiam difamar-me e desacreditar-me.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12416, 'nvi', 'Neemias', 'ne', 6, 14, 'Lembra-te do que fizeram Tobias e Sambalate, meu Deus, lembra-te também da profetisa Noadia e do restante dos profetas que estão tentando me intimidar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12417, 'nvi', 'Neemias', 'ne', 6, 15, 'O muro ficou pronto no dia vinte e cinco de elul, em cinqüenta e dois dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12418, 'nvi', 'Neemias', 'ne', 6, 16, 'Quando todos os nossos inimigos souberam disso, todas as nações vizinhas ficaram atemorizadas e abateu-se o seu orgulho, pois perceberam que essa obra havia sido executada com a ajuda de nosso Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12419, 'nvi', 'Neemias', 'ne', 6, 17, 'E também, naqueles dias, os nobres de Judá estavam enviando muitas cartas a Tobias, que lhes enviava suas respostas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12420, 'nvi', 'Neemias', 'ne', 6, 18, 'Porque muitos de Judá estavam comprometidos com ele por juramento, visto que era genro de Secanias, filho de Ara, e seu filho Joanã havia se casado com a filha de Mesulão, neto de Berequias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12421, 'nvi', 'Neemias', 'ne', 6, 19, 'Até ousavam elogiá-lo na minha presença e iam contar-lhe o que eu dizia. E Tobias continuou a enviar-me cartas para me intimidar.');
