INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12481, 'nvi', 'Neemias', 'ne', 7, 60, 'Os servos do templo e os descendentes dos servos de Salomão 392.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12482, 'nvi', 'Neemias', 'ne', 7, 61, 'Os que chegaram das cidades de Tel-Melá, Tel-Harsa, Querube, Adom e Imer, mas não puderam provar que suas famílias eram descendentes de Israel:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12483, 'nvi', 'Neemias', 'ne', 7, 62, 'Os descendentes de Delaías, Tobias e Necoda 642.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12484, 'nvi', 'Neemias', 'ne', 7, 63, 'E dentre os sacerdotes: os descendentes de Habaías, Coz e Barzilai, homem que se casou com uma filha de Barzilai, de Gileade, e que era chamado por aquele nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12485, 'nvi', 'Neemias', 'ne', 7, 64, 'Esses procuraram seus registros de família, mas não conseguiram achá-los e, dessa forma, foram considerados impuros para o sacerdócio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12486, 'nvi', 'Neemias', 'ne', 7, 65, 'Por isso o governador determinou que eles não comessem das ofertas santíssimas enquanto não houvesse um sacerdote para consultar o Urim e o Tumim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12487, 'nvi', 'Neemias', 'ne', 7, 66, 'O total de todos os registrados foi 42. 360 homens,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12488, 'nvi', 'Neemias', 'ne', 7, 67, 'além de seus 7. 337 servos e servas; havia entre eles 245 cantores e cantoras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12489, 'nvi', 'Neemias', 'ne', 7, 68, 'Possuíam 736 cavalos, 245 mulas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12490, 'nvi', 'Neemias', 'ne', 7, 69, '435 camelos e 6. 720 jumentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12491, 'nvi', 'Neemias', 'ne', 7, 70, 'Alguns dos chefes das famílias contribuíram para o trabalho. O governador deu à tesouraria oito quilos de ouro, 50 bacias e 530 vestes para os sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12492, 'nvi', 'Neemias', 'ne', 7, 71, 'Alguns dos chefes das famílias deram à tesouraria, para a realização do trabalho, cento e sessenta quilos de ouro e uma tonelada e trezentos e vinte quilos de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12493, 'nvi', 'Neemias', 'ne', 7, 72, 'O total dado pelo restante do povo foi de cento e sessenta quilos de ouro, uma tonelada e duzentos quilos de prata e 67 vestes para os sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (12494, 'nvi', 'Neemias', 'ne', 7, 73, 'Os sacerdotes, os levitas, os porteiros, os cantores e os servidores do templo, e também alguns do povo e os demais israelitas, estabeleceram-se em suas próprias cidades. Quando chegou o sétimo mês e os israelitas tinham se instalado em suas cidades,');
