INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3605, 'nvi', 'Números', 'nm', 1, 1, 'O Senhor falou a Moisés na Tenda do Encontro, no deserto do Sinai, no primeiro dia do segundo mês do segundo ano, depois que os israelitas saíram do Egito. Ele disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3606, 'nvi', 'Números', 'nm', 1, 2, '"Façam um recenseamento de toda a comunidade de Israel, pelos seus clãs e famílias, alistando todos os homens, um a um, pelo nome.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3607, 'nvi', 'Números', 'nm', 1, 3, 'Você e Arão contarão todos os homens que possam servir no exército, de vinte anos para cima, organizados segundo as suas divisões.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3608, 'nvi', 'Números', 'nm', 1, 4, 'Um homem de cada tribo, o chefe dos grupos de famílias, deverá ajudá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3609, 'nvi', 'Números', 'nm', 1, 5, 'Estes são os nomes dos homens que os ajudarão: de Rúben, Elizur, filho de Sedeur;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3610, 'nvi', 'Números', 'nm', 1, 6, 'de Simeão, Selumiel, filho de Zurisadai;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3611, 'nvi', 'Números', 'nm', 1, 7, 'de Judá, Naassom, filho de Aminadabe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3612, 'nvi', 'Números', 'nm', 1, 8, 'de Issacar, Natanael, filho de Zuar;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3613, 'nvi', 'Números', 'nm', 1, 9, 'de Zebulom, Eliabe, filho de Helom;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3614, 'nvi', 'Números', 'nm', 1, 10, 'dos filhos de José: de Efraim, Elisama, filho de Amiúde; de Manassés, Gamaliel, filho de Pedazur;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3615, 'nvi', 'Números', 'nm', 1, 11, 'de Benjamim, Abidã, filho de Gideoni;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3616, 'nvi', 'Números', 'nm', 1, 12, 'de Dã, Aieser, filho de Amisadai;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3617, 'nvi', 'Números', 'nm', 1, 13, 'de Aser, Pagiel, filho de Ocrã;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3618, 'nvi', 'Números', 'nm', 1, 14, 'de Gade, Eliasafe, filho de Deuel;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3619, 'nvi', 'Números', 'nm', 1, 15, 'de Naftali, Aira, filho de Enã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3620, 'nvi', 'Números', 'nm', 1, 16, 'Foram esses os escolhidos dentre a comunidade, líderes das tribos dos seus antepassados, chefes dos clãs de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3621, 'nvi', 'Números', 'nm', 1, 17, 'Moisés e Arão reuniram os homens nomeados');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3622, 'nvi', 'Números', 'nm', 1, 18, 'e convocaram toda a comunidade no primeiro dia do segundo mês. Os homens de vinte anos para cima inscreveram-se conforme os seus clãs e as suas famílias, um a um, pelo nome,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3623, 'nvi', 'Números', 'nm', 1, 19, 'conforme o Senhor tinha ordenado a Moisés. E assim ele os contou no deserto do Sinai, na seguinte ordem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3624, 'nvi', 'Números', 'nm', 1, 20, 'Dos descendentes de Rúben, o filho mais velho de Israel: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3625, 'nvi', 'Números', 'nm', 1, 21, 'O número dos da tribo de Rúben foi 46. 500.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3626, 'nvi', 'Números', 'nm', 1, 22, 'Dos descendentes de Simeão: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.');
