INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3646, 'nvi', 'Números', 'nm', 1, 42, 'Dos descendentes de Naftali: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.');
