INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3648, 'nvi', 'Números', 'nm', 1, 44, 'Esses foram os homens contados por Moisés e por Arão e pelos doze líderes de Israel, cada um representando a sua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3649, 'nvi', 'Números', 'nm', 1, 45, 'Todos os israelitas de vinte anos para cima que podiam servir no exército foram contados de acordo com as suas famílias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3650, 'nvi', 'Números', 'nm', 1, 46, 'O total foi 603. 550 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3651, 'nvi', 'Números', 'nm', 1, 47, 'As famílias da tribo de Levi, porém, não foram contadas juntamente com as outras,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3652, 'nvi', 'Números', 'nm', 1, 48, 'pois o Senhor tinha dito a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3653, 'nvi', 'Números', 'nm', 1, 49, '"Não faça o recenseamento da tribo de Levi nem a relacione entre os demais israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3654, 'nvi', 'Números', 'nm', 1, 50, 'Em vez disso, designe os levitas como responsáveis pelo tabernáculo que guarda as tábuas da aliança, por todos os seus utensílios e por tudo o que pertence a ele. Eles transportarão o tabernáculo e todos os seus utensílios; cuidarão dele e acamparão ao seu redor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3655, 'nvi', 'Números', 'nm', 1, 51, 'Sempre que o tabernáculo tiver que ser removido, os levitas o desmontarão e, sempre que tiver que ser armado, os levitas o farão. Qualquer pessoa não autorizada que se aproximar do tabernáculo terá que ser executada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3656, 'nvi', 'Números', 'nm', 1, 52, 'Os israelitas armarão as suas tendas organizadas segundo as suas divisões, cada um em seu próprio acampamento e junto à sua bandeira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3657, 'nvi', 'Números', 'nm', 1, 53, 'Os levitas, porém, armarão as suas tendas ao redor do tabernáculo que guarda as tábuas da aliança, para que a ira divina não caia sobre a comunidade de Israel. Os levitas terão a responsabilidade de cuidar do tabernáculo que guarda as tábuas da aliança".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3658, 'nvi', 'Números', 'nm', 1, 54, 'Os israelitas fizeram tudo exatamente como o Senhor tinha ordenado a Moisés.');
