INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3989, 'nvi', 'Números', 'nm', 10, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3990, 'nvi', 'Números', 'nm', 10, 2, '"Faça duas cornetas de prata batida a fim de usá-las para reunir a comunidade e para dar aos acampamentos o sinal para partirem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3991, 'nvi', 'Números', 'nm', 10, 3, 'Quando as duas cornetas tocarem, a comunidade inteira se reunirá diante de você, à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3992, 'nvi', 'Números', 'nm', 10, 4, 'Se apenas uma tocar, os líderes, chefes dos clãs de Israel, se reunirão diante de você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3993, 'nvi', 'Números', 'nm', 10, 5, 'Quando a corneta der um toque de alerta, as tribos acampadas a leste deverão partir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3994, 'nvi', 'Números', 'nm', 10, 6, 'Ao som do segundo toque, os acampamentos do lado sul partirão. O toque de alerta será o sinal para partir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3995, 'nvi', 'Números', 'nm', 10, 7, 'Para reunir a assembléia, faça soar as cornetas, mas não com o mesmo toque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3996, 'nvi', 'Números', 'nm', 10, 8, '"Os filhos de Arão, os sacerdotes, tocarão as cornetas. Este é um decreto perpétuo para vocês e para as suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3997, 'nvi', 'Números', 'nm', 10, 9, 'Quando em sua terra vocês entrarem em guerra contra um adversário que os esteja oprimindo, toquem as cornetas; e o Senhor, o Deus de vocês se lembrará de vocês e os libertará dos seus inimigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3998, 'nvi', 'Números', 'nm', 10, 10, 'Também em seus dias festivos, nas festas fixas e no primeiro dia de cada mês, vocês deverão tocar as cornetas por ocasião dos seus holocaustos e das suas ofertas de comunhão, e elas serão um memorial em favor de vocês perante o seu Deus. Eu sou o Senhor, o Deus de vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3999, 'nvi', 'Números', 'nm', 10, 11, 'No vigésimo dia do segundo mês do segundo ano, a nuvem se levantou de cima do tabernáculo que guarda as tábuas da aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4000, 'nvi', 'Números', 'nm', 10, 12, 'Então os israelitas partiram do deserto do Sinai e viajaram por etapas, até que a nuvem pousou no deserto de Parã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4001, 'nvi', 'Números', 'nm', 10, 13, 'Assim partiram pela primeira vez, conforme a ordem do Senhor anunciada por Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4002, 'nvi', 'Números', 'nm', 10, 14, 'Os exércitos do acampamento de Judá partiram primeiro, junto à sua bandeira. Naassom, filho de Aminadabe, estava no comando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4003, 'nvi', 'Números', 'nm', 10, 15, 'Natanael, filho de Zuar, comandava os exércitos da tribo de Issacar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4004, 'nvi', 'Números', 'nm', 10, 16, 'e Eliabe, filho de Helom, chefiava os exércitos da tribo de Zebulom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4005, 'nvi', 'Números', 'nm', 10, 17, 'Quando o tabernáculo era desmontado, os gersonitas e os meraritas o carregavam e partiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4006, 'nvi', 'Números', 'nm', 10, 18, 'Os exércitos do acampamento de Rúben partiram em seguida, junto à sua bandeira. Elizur, filho de Sedeur, estava no comando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4007, 'nvi', 'Números', 'nm', 10, 19, 'Selumiel, filho de Zurisadai, comandava os exércitos da tribo de Simeão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4008, 'nvi', 'Números', 'nm', 10, 20, 'e Eliasafe, filho de Deuel, chefiava os exércitos da tribo de Gade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4009, 'nvi', 'Números', 'nm', 10, 21, 'Então os coatitas partiam carregando as coisas sagradas. Antes que eles chegassem, o tabernáculo já deveria estar armado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4010, 'nvi', 'Números', 'nm', 10, 22, 'Os exércitos do acampamento de Efraim partiram em seguida, junto à sua bandeira. Elisama, filho de Amiúde, estava no comando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4011, 'nvi', 'Números', 'nm', 10, 23, 'Gamaliel, filho de Pedazur, comandava os exércitos da tribo de Manassés,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4012, 'nvi', 'Números', 'nm', 10, 24, 'e Abidã, filho de Gideoni, os exércitos da tribo de Benjamim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4013, 'nvi', 'Números', 'nm', 10, 25, 'Finalmente, partiram os exércitos do acampamento de Dã, junto à sua bandeira, como retaguarda para todos os acampamentos. Aieser, filho de Amisadai, estava no comando.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4014, 'nvi', 'Números', 'nm', 10, 26, 'Pagiel, filho de Ocrã, comandava os exércitos da tribo de Aser,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4015, 'nvi', 'Números', 'nm', 10, 27, 'e Aira, filho de Enã, a divisão da tribo de Naftali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4016, 'nvi', 'Números', 'nm', 10, 28, 'Essa era a ordem que os exércitos israelitas seguiam quando se punham em marcha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4017, 'nvi', 'Números', 'nm', 10, 29, 'Então Moisés disse a Hobabe, filho do midianita Reuel, sogro de Moisés: "Estamos partindo para o lugar sobre o qual o Senhor disse: ‘Eu o darei a vocês’. Venha conosco e lhe trataremos bem, pois o Senhor prometeu boas coisas para Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4018, 'nvi', 'Números', 'nm', 10, 30, 'Ele respondeu: "Não, não irei; voltarei para a minha terra e para o meu povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4019, 'nvi', 'Números', 'nm', 10, 31, 'Moisés, porém, disse: "Por favor, não nos deixe. Você sabe onde devemos acampar no deserto e pode ser o nosso guia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4020, 'nvi', 'Números', 'nm', 10, 32, 'Se vier conosco, partilharemos com você todas as coisas boas que o Senhor nos der".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4021, 'nvi', 'Números', 'nm', 10, 33, 'Então eles partiram do monte do Senhor e viajaram três dias. A arca da aliança do Senhor foi à frente deles durante aqueles três dias para encontrar um lugar para descansarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4022, 'nvi', 'Números', 'nm', 10, 34, 'A nuvem do Senhor estava sobre eles de dia, sempre que partiam de um acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4023, 'nvi', 'Números', 'nm', 10, 35, 'Sempre que a arca partia, Moisés dizia: "Levanta-te, ó Senhor! Sejam espalhados os teus inimigos e fujam de diante de ti os teus adversários".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4024, 'nvi', 'Números', 'nm', 10, 36, 'Sempre que a arca parava, ele dizia: "Volta, ó Senhor, para os incontáveis milhares de Israel".');
