INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4025, 'nvi', 'Números', 'nm', 11, 1, 'Aconteceu que o povo começou a queixar-se das suas dificuldades aos ouvidos do Senhor. Quando ele os ouviu, a sua ira acendeu-se e fogo da parte do Senhor queimou entre eles e consumiu algumas extremidades do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4026, 'nvi', 'Números', 'nm', 11, 2, 'Então o povo clamou a Moisés, este orou ao Senhor, e o fogo extinguiu-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4027, 'nvi', 'Números', 'nm', 11, 3, 'Por isso aquele lugar foi chamado Taberá, porque o fogo da parte do Senhor queimou entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4028, 'nvi', 'Números', 'nm', 11, 4, 'Um bando de estrangeiros que havia no meio deles encheu-se de gula, e até os próprios israelitas tornaram a queixar-se, e diziam: "Ah, se tivéssemos carne para comer!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4029, 'nvi', 'Números', 'nm', 11, 5, 'Nós nos lembramos dos peixes que comíamos de graça no Egito, e também dos pepinos, das melancias, dos alhos porós, das cebolas e dos alhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4030, 'nvi', 'Números', 'nm', 11, 6, 'Mas agora perdemos o apetite; nunca vemos nada, a não ser este maná! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4031, 'nvi', 'Números', 'nm', 11, 7, 'O maná era como semente de coentro e tinha aparência de resina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4032, 'nvi', 'Números', 'nm', 11, 8, 'O povo saía recolhendo o maná nas redondezas, e o moía num moinho manual ou socava-o num pilão; depois cozinhava o maná e com ele fazia bolos. Tinha gosto de bolo amassado com azeite de oliva.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4033, 'nvi', 'Números', 'nm', 11, 9, 'Quando o orvalho caía sobre o acampamento à noite, também caía o maná.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4034, 'nvi', 'Números', 'nm', 11, 10, 'Moisés ouviu gente de todas as famílias se queixando, cada uma à entrada de sua tenda. Então acendeu-se a ira do Senhor, e isso pareceu mal a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4035, 'nvi', 'Números', 'nm', 11, 11, 'E ele perguntou ao Senhor: "Por que trouxeste este mal sobre o teu servo? Foi por não te agradares de mim, que colocaste sobre os meus ombros a responsabilidade de todo esse povo?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4036, 'nvi', 'Números', 'nm', 11, 12, 'Por acaso fui eu quem o concebeu? Fui eu quem o trouxe à luz? Por que me pedes para carregá-lo nos braços, como uma ama carrega um recém-nascido, a levá-lo à terra que prometeste sob juramento aos seus antepassados?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4037, 'nvi', 'Números', 'nm', 11, 13, 'Onde conseguirei carne para todo esse povo? Eles ficam se queixando contra mim, dizendo: ‘Dê-nos carne para comer! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4038, 'nvi', 'Números', 'nm', 11, 14, 'Não posso levar todo esse povo sozinho; essa responsabilidade é grande demais para mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4039, 'nvi', 'Números', 'nm', 11, 15, 'Se é assim que vais me tratar, mata-me agora mesmo; se te agradas de mim, não me deixes ver a minha própria ruína".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4040, 'nvi', 'Números', 'nm', 11, 16, 'E o Senhor disse a Moisés: "Reúna setenta autoridades de Israel, que você sabe que são líderes e supervisores entre o povo. Leve-os à Tenda do Encontro, para que estejam ali com você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4041, 'nvi', 'Números', 'nm', 11, 17, 'Eu descerei e falarei com você; e tirarei do Espírito que está sobre você e o porei sobre eles. Eles o ajudarão na árdua responsabilidade de conduzir o povo, de modo que você não tenha que assumir tudo sozinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4042, 'nvi', 'Números', 'nm', 11, 18, '"Diga ao povo: Consagrem-se para amanhã, pois vocês comerão carne. O Senhor os ouviu quando se queixaram a ele, dizendo: ‘Ah, se tivéssemos carne para comer! Estávamos melhor no Egito! ’ Agora o Senhor lhes dará carne, e vocês a comerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4043, 'nvi', 'Números', 'nm', 11, 19, 'Vocês não comerão carne apenas um dia, ou dois, ou cinco, ou dez ou vinte,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4044, 'nvi', 'Números', 'nm', 11, 20, 'mas um mês inteiro, até que lhes saia carne pelo nariz e vocês tenham nojo dela, porque rejeitaram o Senhor, que está no meio de vocês, e se queixaram a ele, dizendo: ‘Por que saímos do Egito? ’ "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4045, 'nvi', 'Números', 'nm', 11, 21, 'Disse, porém, Moisés: "Aqui estou eu no meio de seiscentos mil homens de pé, e dizes: ‘Darei a eles carne para comerem durante um mês inteiro! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4046, 'nvi', 'Números', 'nm', 11, 22, 'Será que haveria o suficiente para eles se todos os rebanhos fossem abatidos? Será que haveria o suficiente para eles se todos os peixes do mar fossem apanhados? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4047, 'nvi', 'Números', 'nm', 11, 23, 'O Senhor respondeu a Moisés: "Estará limitado o poder do Senhor? Agora você verá se a minha palavra se cumprirá ou não".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4048, 'nvi', 'Números', 'nm', 11, 24, 'Então Moisés saiu e contou ao povo o que o Senhor tinha dito. Reuniu setenta autoridades dentre eles e os dispôs ao redor da Tenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4049, 'nvi', 'Números', 'nm', 11, 25, 'O Senhor desceu na nuvem e lhe falou, e tirou do Espírito que estava sobre ele e o pôs sobre as setenta autoridades. Quando o Espírito veio sobre eles, profetizaram, mas depois nunca mais tornaram a fazê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4050, 'nvi', 'Números', 'nm', 11, 26, 'Entretanto, dois homens, chamados Eldade e Medade, tinham ficado no acampamento. Ambos estavam na lista das autoridades, mas não tinham ido para a Tenda. O Espírito também veio sobre eles, e profetizaram no acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4051, 'nvi', 'Números', 'nm', 11, 27, 'Então, certo jovem correu e contou a Moisés: "Eldade e Medade estão profetizando no acampamento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4052, 'nvi', 'Números', 'nm', 11, 28, 'Josué, filho de Num, que desde jovem era auxiliar de Moisés, interferiu e disse: "Moisés, meu senhor, proíba-os! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4053, 'nvi', 'Números', 'nm', 11, 29, 'Mas Moisés respondeu: "Você está com ciúmes por mim? Quem dera todo o povo do Senhor fosse profeta e que o Senhor pusesse o seu Espírito sobre eles! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4054, 'nvi', 'Números', 'nm', 11, 30, 'Então Moisés e as autoridades de Israel voltaram para o acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4055, 'nvi', 'Números', 'nm', 11, 31, 'Depois disso, veio um vento da parte do Senhor que trouxe codornizes do mar e as fez cair por todo o acampamento, a uma altura de noventa centímetros, espalhando-as em todas as direções até num raio de uma caminhada de um dia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4056, 'nvi', 'Números', 'nm', 11, 32, 'Durante todo aquele dia e aquela noite e durante todo o dia seguinte, o povo saiu e recolheu codornizes. Ninguém recolheu menos de dez barris. Então eles as estenderam para secar ao redor de todo o acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4057, 'nvi', 'Números', 'nm', 11, 33, 'Mas, enquanto a carne ainda estava entre os seus dentes e antes que a ingerissem, a ira do Senhor acendeu-se contra o povo, e ele o feriu com uma praga terrível.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4058, 'nvi', 'Números', 'nm', 11, 34, 'Por isso o lugar foi chamado Quibrote-Hataavá, porque ali foram enterrados os que tinham sido dominados pela gula.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4059, 'nvi', 'Números', 'nm', 11, 35, 'De Quibrote-Hataavá o povo partiu para Hazerote, e lá ficou.');
