INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4076, 'nvi', 'Números', 'nm', 13, 1, 'E o Senhor disse a Moisés: "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4077, 'nvi', 'Números', 'nm', 13, 2, 'Envie alguns homens em missão de reconhecimento à terra de Canaã, terra que dou aos israelitas. Envie um líder de cada tribo dos seus antepassados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4078, 'nvi', 'Números', 'nm', 13, 3, 'Assim Moisés os enviou do deserto de Parã, conforme a ordem do Senhor. Todos eles eram chefes dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4079, 'nvi', 'Números', 'nm', 13, 4, 'São estes os seus nomes: da tribo de Rúben, Samua, filho de Zacur;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4080, 'nvi', 'Números', 'nm', 13, 5, 'da tribo de Simeão, Safate, filho de Hori;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4081, 'nvi', 'Números', 'nm', 13, 6, 'da tribo de Judá, Calebe, filho de Jefoné;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4082, 'nvi', 'Números', 'nm', 13, 7, 'da tribo de Issacar, Jigeal, filho de José;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4083, 'nvi', 'Números', 'nm', 13, 8, 'da tribo de Efraim, Oséias, filho de Num;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4084, 'nvi', 'Números', 'nm', 13, 9, 'da tribo de Benjamim, Palti, filho de Rafu;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4085, 'nvi', 'Números', 'nm', 13, 10, 'da tribo de Zebulom, Gadiel, filho de Sodi;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4086, 'nvi', 'Números', 'nm', 13, 11, 'da tribo de José, isto é, da tribo de Manassés, Gadi, filho de Susi;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4087, 'nvi', 'Números', 'nm', 13, 12, 'da tribo de Dã, Amiel, filho de Gemali;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4088, 'nvi', 'Números', 'nm', 13, 13, 'da tribo de Aser, Setur, filho de Micael;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4089, 'nvi', 'Números', 'nm', 13, 14, 'da tribo de Naftali, Nabi, filho de Vofsi;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4090, 'nvi', 'Números', 'nm', 13, 15, 'da tribo de Gade, Güel, filho de Maqui.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4091, 'nvi', 'Números', 'nm', 13, 16, 'São esses os nomes dos homens que Moisés enviou em missão de reconhecimento do território. ( A Oséias, filho de Num, Moisés deu o nome de Josué. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4092, 'nvi', 'Números', 'nm', 13, 17, 'Quando Moisés os enviou para observarem Canaã, disse: "Subam pelo Neguebe e prossigam até a região montanhosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4093, 'nvi', 'Números', 'nm', 13, 18, 'Vejam como é a terra e se o povo que vive lá é forte ou fraco, se são muitos ou poucos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4094, 'nvi', 'Números', 'nm', 13, 19, 'se a terra em que habitam é boa ou ruim; se as cidades em que vivem são cidades sem muros ou fortificadas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4095, 'nvi', 'Números', 'nm', 13, 20, 'se o solo é fértil ou pobre; se existe ali floresta ou não. Sejam corajosos! Tragam alguns frutos da terra". Era a época do início da colheita das uvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4096, 'nvi', 'Números', 'nm', 13, 21, 'Eles subiram e observaram a terra desde o deserto de Zim até Reobe, na direção de Lebo-Hamate.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4097, 'nvi', 'Números', 'nm', 13, 22, 'Subiram do Neguebe e chegaram a Hebrom, onde viviam Aimã, Sesai e Talmai, descendentes de Enaque. ( Hebrom havia sido construída sete anos antes de Zoã, no Egito. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4098, 'nvi', 'Números', 'nm', 13, 23, 'Quando chegaram ao vale de Escol, cortaram um ramo do qual pendia um único cacho de uvas. Dois deles carregaram o cacho, pendurado numa vara. Colheram também romãs e figos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4099, 'nvi', 'Números', 'nm', 13, 24, 'Aquele lugar foi chamado vale de Escol por causa do cacho de uvas que os israelitas cortaram ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4100, 'nvi', 'Números', 'nm', 13, 25, 'Ao fim de quarenta dias eles voltaram da missão de reconhecimento daquela terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4101, 'nvi', 'Números', 'nm', 13, 26, 'Eles então retornaram a Moisés e a Arão e a toda a comunidade de Israel em Cades, no deserto de Parã, onde prestaram relatório a eles e a toda a comunidade de Israel, e lhes mostraram os frutos da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4102, 'nvi', 'Números', 'nm', 13, 27, 'E deram o seguinte relatório a Moisés: "Entramos na terra à qual você nos enviou, onde manam leite e mel! Aqui estão alguns frutos dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4103, 'nvi', 'Números', 'nm', 13, 28, 'Mas o povo que lá vive é poderoso, e as cidades são fortificadas e muito grandes. Também vimos descendentes de Enaque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4104, 'nvi', 'Números', 'nm', 13, 29, 'Os amalequitas vivem no Neguebe; os hititas, os jebuseus e os amorreus vivem na região montanhosa; os cananeus vivem perto do mar e junto ao Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4105, 'nvi', 'Números', 'nm', 13, 30, 'Então Calebe fez o povo calar-se perante Moisés e disse: "Subamos e tomemos posse da terra. É certo que venceremos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4106, 'nvi', 'Números', 'nm', 13, 31, 'Mas os homens que tinham ido com ele disseram: "Não podemos atacar aquele povo; é mais forte do que nós".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4107, 'nvi', 'Números', 'nm', 13, 32, 'E espalharam entre os israelitas um relatório negativo acerca daquela terra. Disseram: "A terra para a qual fomos em missão de reconhecimento devora os que nela vivem. Todos os que vimos são de grande estatura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4108, 'nvi', 'Números', 'nm', 13, 33, 'Vimos também os gigantes, os descendentes de Enaque, diante de quem parecíamos gafanhotos, a nós e a eles".');
