INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4109, 'nvi', 'Números', 'nm', 14, 1, 'Naquela noite toda a comunidade começou a chorar em alta voz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4110, 'nvi', 'Números', 'nm', 14, 2, 'Todos os israelitas queixaram-se contra Moisés e contra Arão, e toda a comunidade lhes disse: "Quem dera tivéssemos morrido no Egito! Ou neste deserto!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4111, 'nvi', 'Números', 'nm', 14, 3, 'Por que o Senhor está nos trazendo para esta terra? Só para nos deixar cair à espada? Nossas mulheres e nossos filhos serão tomados como despojo de guerra. Não seria melhor voltar para o Egito? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4112, 'nvi', 'Números', 'nm', 14, 4, 'E disseram uns aos outros: "Escolheremos um chefe e voltaremos para o Egito! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4113, 'nvi', 'Números', 'nm', 14, 5, 'Então Moisés e Arão prostraram-se, rosto em terra, diante de toda a assembléia dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4114, 'nvi', 'Números', 'nm', 14, 6, 'Josué, filho de Num, e Calebe, filho de Jefoné, dentre os que haviam observado a terra, rasgaram as suas vestes');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4115, 'nvi', 'Números', 'nm', 14, 7, 'e disseram a toda à comunidade dos israelitas: "A terra que percorremos em missão de reconhecimento é excelente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4116, 'nvi', 'Números', 'nm', 14, 8, 'Se o Senhor se agradar de nós, ele nos fará entrar nessa terra, onde manam leite e mel, e a dará a nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4117, 'nvi', 'Números', 'nm', 14, 9, 'Somente não sejam rebeldes contra o Senhor. E não tenham medo do povo da terra, porque nós os devoraremos como se fossem pão. A proteção deles se foi, mas o Senhor está conosco. Não tenham medo deles! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4118, 'nvi', 'Números', 'nm', 14, 10, 'Mas a comunidade toda falou em apedrejá-los. Então a glória do Senhor apareceu a todos os israelitas na Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4119, 'nvi', 'Números', 'nm', 14, 11, 'E o Senhor disse a Moisés: "Até quando este povo me tratará com pouco caso? Até quando se recusará a crer em mim, apesar de todos os sinais que realizei entre eles?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4120, 'nvi', 'Números', 'nm', 14, 12, 'Eu os ferirei com praga e os destruirei, mas farei de você uma nação maior e mais forte do que eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4121, 'nvi', 'Números', 'nm', 14, 13, 'Moisés disse ao Senhor: "Então os egípcios ouvirão que pelo teu poder fizeste este povo sair dentre eles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4122, 'nvi', 'Números', 'nm', 14, 14, 'e falarão disso aos habitantes desta terra. Eles ouviram que tu, ó Senhor, estás com este povo e que te vêem face a face, Senhor, e que a tua nuvem paira sobre eles, e que vais adiante deles numa coluna de nuvem de dia e numa coluna de fogo de noite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4123, 'nvi', 'Números', 'nm', 14, 15, 'Se exterminares este povo, as nações que ouvirem falar do que fizeste dirão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4124, 'nvi', 'Números', 'nm', 14, 16, '‘O Senhor não conseguiu levar esse povo à terra que lhes prometeu em juramento; por isso os matou no deserto’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4125, 'nvi', 'Números', 'nm', 14, 17, '"Mas agora, que a força do Senhor se manifeste, segundo prometeste:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4126, 'nvi', 'Números', 'nm', 14, 18, '‘O Senhor é muito paciente e grande em fidelidade, e perdoa a iniqüidade e a rebelião, se bem que não deixa o pecado sem punição, e castiga os filhos pela iniqüidade dos pais até a terceira e quarta geração’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4127, 'nvi', 'Números', 'nm', 14, 19, 'Segundo a tua grande fidelidade, perdoa a iniqüidade deste povo, assim como a tens perdoado desde que saíram do Egito até agora".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4128, 'nvi', 'Números', 'nm', 14, 20, 'O Senhor respondeu: "Eu os perdoei, conforme você pediu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4129, 'nvi', 'Números', 'nm', 14, 21, 'No entanto, juro pela glória do Senhor que enche toda a terra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4130, 'nvi', 'Números', 'nm', 14, 22, 'que nenhum dos que viram a minha glória e os sinais miraculosos que realizei no Egito e no deserto, e me puseram à prova e me desobedeceram dez vezes —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4131, 'nvi', 'Números', 'nm', 14, 23, 'nenhum deles chegará a ver a terra que prometi com juramento aos seus antepassados. Ninguém que me tratou com desprezo a verá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4132, 'nvi', 'Números', 'nm', 14, 24, 'Mas, como o meu servo Calebe tem outro espírito e me segue com integridade, eu o farei entrar na terra que foi observar, e seus descendentes a herdarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4133, 'nvi', 'Números', 'nm', 14, 25, 'Visto que os amalequitas e os cananeus habitam nos vales, amanhã dêem meia-volta e partam em direção ao deserto pelo caminho que vai para o mar Vermelho".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4134, 'nvi', 'Números', 'nm', 14, 26, 'Disse mais o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4135, 'nvi', 'Números', 'nm', 14, 27, '"Até quando esta comunidade ímpia se queixará contra mim? Tenho ouvido as queixas desses israelitas murmuradores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4136, 'nvi', 'Números', 'nm', 14, 28, 'Diga-lhes: ‘Juro pelo meu nome, declara o Senhor, que farei a vocês tudo o que pediram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4137, 'nvi', 'Números', 'nm', 14, 29, 'Cairão neste deserto os cadáveres de todos vocês, de vinte anos para cima, que foram contados no recenseamento e que se queixaram contra mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4138, 'nvi', 'Números', 'nm', 14, 30, 'Nenhum de vocês entrará na terra que, com mão levantada, jurei dar-lhes para sua habitação, exceto Calebe, filho de Jefoné, e Josué, filho de Num.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4139, 'nvi', 'Números', 'nm', 14, 31, 'Mas, quanto aos seus filhos, sobre os quais vocês disseram que seriam tomados como despojo de guerra, eu os farei entrar para desfrutarem a terra que vocês rejeitaram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4140, 'nvi', 'Números', 'nm', 14, 32, 'O cadáveres de vocês, porém, cairão neste deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4141, 'nvi', 'Números', 'nm', 14, 33, 'Os filhos de vocês serão pastores aqui durante quarenta anos, sofrendo pela infidelidade de vocês, até que o último cadáver de vocês seja destruído no deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4142, 'nvi', 'Números', 'nm', 14, 34, 'Durante quarenta anos vocês sofrerão a conseqüência dos seus pecados e experimentarão a minha rejeição; cada ano corresponderá a cada um dos quarenta dias em que vocês observaram a terra’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4143, 'nvi', 'Números', 'nm', 14, 35, 'Eu, o Senhor, falei, e certamente farei essas coisas a toda esta comunidade ímpia, que conspirou contra mim. Encontrarão o seu fim neste deserto; aqui morrerão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4144, 'nvi', 'Números', 'nm', 14, 36, 'Os homens enviados por Moisés em missão de reconhecimento daquela terra voltaram e fizeram toda a comunidade queixar-se contra ele ao espalharem um relatório negativo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4145, 'nvi', 'Números', 'nm', 14, 37, 'esses homens responsáveis por espalhar o relatório negativo sobre a terra morreram subitamente de praga perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4146, 'nvi', 'Números', 'nm', 14, 38, 'De todos os que foram observar a terra, somente Josué, filho de Num, e Calebe, filho de Jefoné, sobreviveram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4147, 'nvi', 'Números', 'nm', 14, 39, 'Quando Moisés transmitiu essas palavras a todos os israelitas, eles choraram amargamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4148, 'nvi', 'Números', 'nm', 14, 40, 'Na madrugada seguinte subiram para o alto da região montanhosa, e disseram: "Subiremos ao lugar que o Senhor prometeu, pois cometemos pecado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4149, 'nvi', 'Números', 'nm', 14, 41, 'Moisés, porém, disse: "Por que vocês estão desobedecendo à ordem do Senhor? Isso não terá sucesso!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4150, 'nvi', 'Números', 'nm', 14, 42, 'Não subam, porque o Senhor não está com vocês. Vocês serão derrotados pelos inimigos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4151, 'nvi', 'Números', 'nm', 14, 43, 'pois os amalequitas e os cananeus os enfrentarão ali, e vocês cairão à espada. Visto que deixaram de seguir ao Senhor, ele não estará com vocês".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4152, 'nvi', 'Números', 'nm', 14, 44, 'Apesar disso, eles subiram desafiadoramente ao alto da região montanhosa, mas nem Moisés nem a arca da aliança do Senhor saíram do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4153, 'nvi', 'Números', 'nm', 14, 45, 'Então os amalequitas e os cananeus que lá viviam desceram e os derrotaram e os perseguiram até Hormá.');
