INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4154, 'nvi', 'Números', 'nm', 15, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4155, 'nvi', 'Números', 'nm', 15, 2, '"Diga o seguinte aos israelitas: Quando entrarem na terra que lhes dou para sua habitação,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4156, 'nvi', 'Números', 'nm', 15, 3, 'e apresentarem ao Senhor uma oferta, de bois ou de ovelhas, preparada no fogo como aroma agradável ao Senhor, seja holocausto, seja sacrifício, para cumprir um voto ou como oferta voluntária ou como oferta relativa a uma festa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4157, 'nvi', 'Números', 'nm', 15, 4, 'aquele que trouxer a sua oferta apresentará também ao Senhor uma oferta de cereal de um jarro da melhor farinha amassada com um litro de óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4158, 'nvi', 'Números', 'nm', 15, 5, 'Para cada cordeiro do holocausto ou do sacrifício, prepare um litro de vinho como oferta derramada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4159, 'nvi', 'Números', 'nm', 15, 6, '"Para um carneiro, prepare uma oferta de cereal de dois jarros da melhor farinha com um litro de óleo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4160, 'nvi', 'Números', 'nm', 15, 7, 'e um litro de vinho como oferta derramada. Apresente-a como aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4161, 'nvi', 'Números', 'nm', 15, 8, '"Quando algum de vocês preparar um novilho para holocausto ou para sacrifício para cumprir voto especial, ou como oferta de comunhão ao Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4162, 'nvi', 'Números', 'nm', 15, 9, 'traga com o novilho uma oferta de cereal de três jarros da melhor farinha amassada com meio galão de óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4163, 'nvi', 'Números', 'nm', 15, 10, 'Traga também meio galão de vinho para a oferta derramada. Será uma oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4164, 'nvi', 'Números', 'nm', 15, 11, 'Cada novilho ou carneiro, ou cordeiro ou cabrito, deverá ser preparado dessa maneira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4165, 'nvi', 'Números', 'nm', 15, 12, 'Façam isso com cada animal, com tantos quantos vocês prepararem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4166, 'nvi', 'Números', 'nm', 15, 13, '"Todo o que for natural da terra deverá proceder dessa maneira quando trouxer uma oferta preparada no fogo de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4167, 'nvi', 'Números', 'nm', 15, 14, 'E se um estrangeiro que vive entre vocês, ou entre os descendentes de vocês, apresentar uma oferta preparada no fogo, de aroma agradável ao Senhor, deverá fazer o mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4168, 'nvi', 'Números', 'nm', 15, 15, 'A assembléia deverá ter as mesmas leis, que valerão tanto para vocês como para o estrangeiro que vive entre vocês; este é um decreto perpétuo pelas suas gerações, que, perante o Senhor, valerá tanto para vocês quanto para o estrangeiro residente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4169, 'nvi', 'Números', 'nm', 15, 16, 'A mesma lei e ordenança se aplicará tanto a vocês como ao estrangeiro residente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4170, 'nvi', 'Números', 'nm', 15, 17, 'O Senhor disse ainda a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4171, 'nvi', 'Números', 'nm', 15, 18, '"Diga aos israelitas: Quando vocês entrarem na terra para onde os levo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4172, 'nvi', 'Números', 'nm', 15, 19, 'e comerem do fruto da terra, apresentem uma porção como contribuição ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4173, 'nvi', 'Números', 'nm', 15, 20, 'Apresentem um bolo feito das primícias da farinha de vocês. Apresentem-no como contribuição da sua colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4174, 'nvi', 'Números', 'nm', 15, 21, 'Em todas as suas gerações vocês apresentarão das primícias da farinha uma contribuição ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4175, 'nvi', 'Números', 'nm', 15, 22, '"Mas se vocês pecarem e deixarem de cumprir todos esses mandamentos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4176, 'nvi', 'Números', 'nm', 15, 23, '— tudo o que o Senhor lhes ordenou por meio de Moisés, desde o dia em que os ordenou e pelas suas gerações —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4177, 'nvi', 'Números', 'nm', 15, 24, 'e se isso for feito sem intenção e não for do conhecimento da comunidade, toda a comunidade terá que oferecer um novilho para o holocausto de aroma agradável ao Senhor. Também apresentarão com sua oferta de cereal uma oferta derramada, conforme as prescrições, e um bode como oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4178, 'nvi', 'Números', 'nm', 15, 25, 'O sacerdote fará propiciação por toda a comunidade de Israel, e eles serão perdoados, pois o seu pecado não foi intencional e eles trouxeram ao Senhor uma oferta preparada no fogo e uma oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4179, 'nvi', 'Números', 'nm', 15, 26, 'A comunidade de Israel toda e os estrangeiros residentes entre eles serão perdoados, porque todo o povo esteve envolvido num pecado involuntário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4180, 'nvi', 'Números', 'nm', 15, 27, '"Se, contudo, apenas uma pessoa pecar sem intenção, ela terá que trazer uma cabra de um ano como oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4181, 'nvi', 'Números', 'nm', 15, 28, 'O sacerdote fará propiciação pela pessoa que pecar, cometendo uma falta involuntária perante o Senhor, e ela será perdoada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4182, 'nvi', 'Números', 'nm', 15, 29, 'Somente uma lei haverá para todo aquele que pecar sem intenção, seja ele israelita nato, seja estrangeiro residente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4183, 'nvi', 'Números', 'nm', 15, 30, '"Mas todo aquele que pecar com atitude desafiadora, seja natural da terra, seja estrangeiro residente, insulta ao Senhor, e será eliminado do meio do seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4184, 'nvi', 'Números', 'nm', 15, 31, 'Por ter desprezado a palavra do Senhor e quebrado os seus mandamentos, terá que ser eliminado; sua culpa estará sobre ele".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4185, 'nvi', 'Números', 'nm', 15, 32, 'Certo dia, quando os israelitas estavam no deserto, encontraram um homem recolhendo lenha no dia de sábado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4186, 'nvi', 'Números', 'nm', 15, 33, 'Aqueles que o encontraram recolhendo lenha levaram-no a Moisés, a Arão e a toda a comunidade,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4187, 'nvi', 'Números', 'nm', 15, 34, 'que o prenderam, porque não sabiam o que deveria ser feito com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4188, 'nvi', 'Números', 'nm', 15, 35, 'Então o Senhor disse a Moisés: "O homem terá que ser executado. Toda a comunidade o apedrejará fora do acampamento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4189, 'nvi', 'Números', 'nm', 15, 36, 'Assim, toda a comunidade o levou para fora do acampamento e o apedrejou até à morte, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4190, 'nvi', 'Números', 'nm', 15, 37, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4191, 'nvi', 'Números', 'nm', 15, 38, '"Diga o seguinte aos israelitas: Façam borlas nas extremidades das suas roupas e ponham um cordão azul em cada uma delas; façam isso por todas as suas gerações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4192, 'nvi', 'Números', 'nm', 15, 39, 'Quando virem essas borlas vocês se lembrarão de todos os mandamentos do Senhor, para que lhes obedeçam e não se prostituam nem sigam as inclinações dos seus corações e dos seus olhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4193, 'nvi', 'Números', 'nm', 15, 40, 'Assim vocês se lembrarão de obedecer a todos os meus mandamentos, e para o seu Deus vocês serão um povo consagrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4194, 'nvi', 'Números', 'nm', 15, 41, 'Eu sou o Senhor, o seu Deus, que os trouxe do Egito para ser o Deus de vocês. Eu sou o Senhor, o seu Deus".');
