INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4195, 'nvi', 'Números', 'nm', 16, 1, 'Corá, filho de Isar, neto de Coate, bisneto de Levi, reuniu Datã e Abirão, filhos de Eliabe, e Om, filho de Pelete, todos da tribo de Rúben,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4196, 'nvi', 'Números', 'nm', 16, 2, 'e eles se insurgiram contra Moisés. Com eles estavam duzentos e cinqüenta israelitas, líderes bem conhecidos na comunidade e que haviam sido nomeados membros do concílio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4197, 'nvi', 'Números', 'nm', 16, 3, 'Eles se ajuntaram contra Moisés e Arão, e lhes disseram: "Basta! A assembléia toda é santa, cada um deles é santo, e o Senhor está no meio deles. Então, por que vocês se colocam acima da assembléia do Senhor? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4198, 'nvi', 'Números', 'nm', 16, 4, 'Quando ouviu isso, Moisés prostrou-se, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4199, 'nvi', 'Números', 'nm', 16, 5, 'Depois disse a Corá e a todos os seus seguidores: "Pela manhã o Senhor mostrará quem lhe pertence e fará aproximar-se dele aquele que é santo, o homem a quem ele escolher.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4200, 'nvi', 'Números', 'nm', 16, 6, 'Você, Corá, e todos os seus seguidores deverão fazer o seguinte: peguem incensarios');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4201, 'nvi', 'Números', 'nm', 16, 7, 'e amanhã coloquem neles fogo e incenso perante o Senhor. Quem o Senhor escolher será o homem consagrado. Basta, levitas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4202, 'nvi', 'Números', 'nm', 16, 8, 'Moisés disse também a Corá: "Agora ouçam-me, levitas!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4203, 'nvi', 'Números', 'nm', 16, 9, 'Não lhes é suficiente que o Deus de Israel os tenha separado do restante da comunidade de Israel e os tenha trazido para junto de si a fim de realizarem o trabalho no tabernáculo do Senhor e para estarem preparados para servir a comunidade?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4204, 'nvi', 'Números', 'nm', 16, 10, 'Ele trouxe você e todos os seus irmãos levitas para junto dele, e agora vocês querem também o sacerdócio?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4205, 'nvi', 'Números', 'nm', 16, 11, 'É contra o Senhor que você e todos os seus seguidores se ajuntaram! Quem é Arão, para que se queixem contra ele? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4206, 'nvi', 'Números', 'nm', 16, 12, 'Então Moisés mandou chamar Datã e Abirão, filhos de Eliabe. Mas eles disseram: "Nós não iremos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4207, 'nvi', 'Números', 'nm', 16, 13, 'Não lhe basta nos ter tirado de uma terra onde manam leite e mel para matar-nos no deserto? E ainda quer se fazer chefe sobre nós?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4208, 'nvi', 'Números', 'nm', 16, 14, 'Além disso, você não nos levou a uma terra onde manam leite e mel, nem nos deu uma herança de campos e vinhas. Você pensa que pode cegar os olhos destes homens? Nós não iremos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4209, 'nvi', 'Números', 'nm', 16, 15, 'Moisés indignou-se e disse ao Senhor: "Não aceites a oferta deles. Não tomei deles nem sequer um jumento, nem prejudiquei a nenhum deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4210, 'nvi', 'Números', 'nm', 16, 16, 'Moisés disse a Corá: "Você e todos os seus seguidores terão que apresentar-se amanhã ao Senhor, você, eles e Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4211, 'nvi', 'Números', 'nm', 16, 17, 'Cada homem pegará o seu incensário, nele colocará incenso e o apresentará ao Senhor. Serão duzentos e cinqüenta incensários ao todo. Você e Arão também apresentarão os seus incensários".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4212, 'nvi', 'Números', 'nm', 16, 18, 'Assim, cada um deles pegou o seu incensário, acendeu o incenso, e se colocou com Moisés e com Arão à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4213, 'nvi', 'Números', 'nm', 16, 19, 'Quando Corá reuniu todos os seus seguidores em oposição a eles à entrada da Tenda do Encontro, a glória do Senhor apareceu a toda a comunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4214, 'nvi', 'Números', 'nm', 16, 20, 'E o Senhor disse a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4215, 'nvi', 'Números', 'nm', 16, 21, '"Afastem-se dessa comunidade para que eu acabe com eles imediatamente".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4216, 'nvi', 'Números', 'nm', 16, 22, 'Mas Moisés e Arão prostraram-se, rosto em terra, e disseram: "Ó Deus, Deus que a todos dá vida, ficarás tu irado contra toda a comunidade quando um só homem pecou? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4217, 'nvi', 'Números', 'nm', 16, 23, 'Então o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4218, 'nvi', 'Números', 'nm', 16, 24, '"Diga à comunidade que se afaste das tendas de Corá, Datã e Abirão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4219, 'nvi', 'Números', 'nm', 16, 25, 'Moisés levantou-se e foi para onde estavam Datã e Abirão, e as autoridades de Israel o seguiram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4220, 'nvi', 'Números', 'nm', 16, 26, 'Ele advertiu a comunidade: "Afastem-se das tendas desses ímpios! Não toquem em nada do que pertence a eles, senão vocês serão eliminados por causa dos pecados deles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4221, 'nvi', 'Números', 'nm', 16, 27, 'Eles se afastaram das tendas de Corá, Datã e Abirão. Datã e Abirão tinham saído e estavam de pé, à entrada de suas tendas, junto com suas mulheres, seus filhos e suas crianças pequenas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4222, 'nvi', 'Números', 'nm', 16, 28, 'E disse Moisés: "Assim vocês saberão que o Senhor me enviou para fazer todas essas coisas e que isso não partiu de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4223, 'nvi', 'Números', 'nm', 16, 29, 'Se estes homens tiverem morte natural e experimentarem somente aquilo que normalmente acontece aos homens, então o Senhor não me enviou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4224, 'nvi', 'Números', 'nm', 16, 30, 'Mas, se o Senhor fizer acontecer algo totalmente novo, e a terra abrir a sua boca e os engolir, junto com tudo o que é deles, e eles descerem vivos ao Sheol, então vocês saberão que estes homens desprezaram o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4225, 'nvi', 'Números', 'nm', 16, 31, 'Assim que Moisés acabou de dizer tudo isso, o chão debaixo deles fendeu-se');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4226, 'nvi', 'Números', 'nm', 16, 32, 'e a terra abriu a sua boca e os engoliu juntamente com suas famílias, com todos os seguidores de Corá e com todos os seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4227, 'nvi', 'Números', 'nm', 16, 33, 'Desceram vivos à sepultura, com tudo o que possuíam; a terra fechou-se sobre eles, e pereceram dentre a assembléia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4228, 'nvi', 'Números', 'nm', 16, 34, 'Diante dos seus gritos, todos os israelitas ao redor fugiram, gritando: "A terra vai nos engolir também! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4229, 'nvi', 'Números', 'nm', 16, 35, 'Então veio fogo da parte do Senhor e consumiu os duzentos e cinqüenta homens que ofereciam incenso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4230, 'nvi', 'Números', 'nm', 16, 36, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4231, 'nvi', 'Números', 'nm', 16, 37, '"Diga a Eleazar, filho do sacerdote Arão, que apanhe os incensários dentre os restos fumegastes e espalhe as brasas, porque os incensários são santos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4232, 'nvi', 'Números', 'nm', 16, 38, 'Os incensários dos homens que pelo seu pecado perderam a vida serão batidos em forma de lâminas e servirão de revestimento do altar, pois foram apresentados ao Senhor e se tornaram sagrados. Que sejam um sinal para os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4233, 'nvi', 'Números', 'nm', 16, 39, 'O sacerdote Eleazar juntou os incensários de bronze que tinham sido apresentados pelos que foram consumidos pelo fogo. Os incensários foram batidos e serviram de revestimento do altar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4234, 'nvi', 'Números', 'nm', 16, 40, 'como o Senhor tinha dito por meio de Moisés. Isso foi feito como memorial para os israelitas, a fim de que ninguém que não fosse descendente de Arão, queimasse incenso perante o Senhor, para não sofrer o que Corá e os seus seguidores sofreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4235, 'nvi', 'Números', 'nm', 16, 41, 'No dia seguinte toda a comunidade de Israel começou a queixar-se contra Moisés e Arão, dizendo: "Vocês mataram o povo do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4236, 'nvi', 'Números', 'nm', 16, 42, 'Quando, porém, a comunidade se ajuntou contra Moisés e contra Arão, e eles se voltaram para a Tenda do Encontro, repentinamente a nuvem a cobriu e a glória do Senhor apareceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4237, 'nvi', 'Números', 'nm', 16, 43, 'Então Moisés e Arão foram para a frente da Tenda do Encontro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4238, 'nvi', 'Números', 'nm', 16, 44, 'e o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4239, 'nvi', 'Números', 'nm', 16, 45, '"Saia do meio dessa comunidade para que eu acabe com eles imediatamente". Mas eles se prostraram, rosto em terra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4240, 'nvi', 'Números', 'nm', 16, 46, 'e Moisés disse a Arão: "Pegue o seu incensário e ponha incenso nele, com fogo tirado do altar, e vá depressa até a comunidade para fazer propiciação por eles, porque saiu grande ira da parte do Senhor e a praga começou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4241, 'nvi', 'Números', 'nm', 16, 47, 'Arão fez o que Moisés ordenou e correu para o meio da assembléia. A praga já havia começado entre o povo, mas Arão ofereceu o incenso e fez propiciação por eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4242, 'nvi', 'Números', 'nm', 16, 48, 'Arão se pôs entre os mortos e os vivos, e a praga cessou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4243, 'nvi', 'Números', 'nm', 16, 49, 'Foram catorze mil e setecentos os que morreram daquela praga, além dos que haviam morrido por causa de Corá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4244, 'nvi', 'Números', 'nm', 16, 50, 'Então Arão voltou a Moisés, à entrada da Tenda do Encontro, pois a praga já havia cessado.');
