INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4258, 'nvi', 'Números', 'nm', 18, 1, 'O Senhor disse a Arão: "Você, os seus filhos e a família de seu pai serão responsáveis pelas ofensas contra o santuário; você e seus filhos serão responsáveis pelas ofensas cometidas no exercício do sacerdócio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4259, 'nvi', 'Números', 'nm', 18, 2, 'Traga também os seus irmãos levitas, que pertencem à tribo de seus antepassados, para se unirem a você e o ajudarem quando você e seus filhos ministrarem perante a tenda que guarda as tábuas da aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4260, 'nvi', 'Números', 'nm', 18, 3, 'Eles ficarão a seu serviço e cuidarão também do serviço da Tenda, mas não poderão aproximar-se dos utensílios do santuário ou do altar; se o fizerem morrerão, tanto eles como vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4261, 'nvi', 'Números', 'nm', 18, 4, 'Eles se unirão a vocês e terão a responsabilidade de cuidar da Tenda do Encontro, de todo o trabalho que ali se faz. Ninguém mais poderá aproximar-se de vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4262, 'nvi', 'Números', 'nm', 18, 5, '"Vocês terão a responsabilidade de cuidar do santuário e do altar, para que não torne a cair a ira divina sobre os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4263, 'nvi', 'Números', 'nm', 18, 6, 'Eu mesmo escolhi os seus irmãos, os levitas, dentre os israelitas como um presente para vocês, dedicados ao Senhor para fazerem o trabalho da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4264, 'nvi', 'Números', 'nm', 18, 7, 'Mas somente você e seus filhos poderão servir como sacerdotes em tudo o que se refere ao altar e ao que se encontra além do véu. Dou a vocês o serviço do sacerdócio como um presente. Qualquer pessoa não autorizada que se aproximar do santuário terá que ser executada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4265, 'nvi', 'Números', 'nm', 18, 8, 'Então o Senhor disse a Arão: "Eu mesmo o tornei responsável pelas contribuições trazidas a mim; todas as ofertas sagradas que os israelitas me derem, eu as dou como porção a você e a seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4266, 'nvi', 'Números', 'nm', 18, 9, 'Das ofertas santíssimas vocês terão a parte que é poupada do fogo. Dentre todas as dádivas que me trouxerem como ofertas santíssimas, seja oferta de cereal, seja pelo pecado, seja de reparação, tal parte pertence a você e a seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4267, 'nvi', 'Números', 'nm', 18, 10, 'Comam-na como algo santíssimo; todos os do sexo masculino a comerão. Considerem-na santa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4268, 'nvi', 'Números', 'nm', 18, 11, '"Também dou a você, e a seus filhos e filhas, por decreto perpétuo, as contribuições de todas as ofertas ritualmente movidas apresentadas pelos israelitas. Todos os da sua família que estiverem cerimonialmente puros poderão comê-las.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4269, 'nvi', 'Números', 'nm', 18, 12, '"Dou a você o melhor azeite e o melhor vinho novo e o melhor trigo que eles apresentarem ao Senhor como primeiros frutos da colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4270, 'nvi', 'Números', 'nm', 18, 13, 'Todos os primeiros frutos da terra que trouxerem ao Senhor serão de vocês. Todos os da sua família que estiverem cerimonialmente puros, poderão comê-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4271, 'nvi', 'Números', 'nm', 18, 14, '"Tudo o que em Israel for consagrado a Deus pertencerá a você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4272, 'nvi', 'Números', 'nm', 18, 15, 'O primeiro nascido de todo ventre, oferecido ao Senhor, seja homem, seja animal, será seu. Mas você deverá resgatar todo filho mais velho, como também toda primeira cria de animais impuros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4273, 'nvi', 'Números', 'nm', 18, 16, 'Quando tiverem um mês de idade, você deverá resgatá-los pelo preço de resgate estabelecido em sessenta gramas de prata, com base no peso padrão do santuário, que são doze gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4274, 'nvi', 'Números', 'nm', 18, 17, '"Não resgate, porém, a primeira cria de uma vaca, de uma ovelha ou de uma cabra. Derrame o sangue deles sobre o altar e queime a sua gordura como uma oferta preparada no fogo, de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4275, 'nvi', 'Números', 'nm', 18, 18, 'A carne desses animais pertence a você, como também o peito da oferta movida e a coxa direita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4276, 'nvi', 'Números', 'nm', 18, 19, 'Tudo aquilo que for separado dentre todas as dádivas sagradas que os israelitas apresentarem ao Senhor eu dou a você e a seus filhos e filhas como decreto perpétuo. É uma aliança de sal perpétua perante o Senhor, para você e para os seus descendentes".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4277, 'nvi', 'Números', 'nm', 18, 20, 'Disse ainda o Senhor a Arão: "Você não terá herança na terra deles, nem terá porção entre eles; eu sou a sua porção e a sua herança entre os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4278, 'nvi', 'Números', 'nm', 18, 21, '"Dou aos levitas todos os dízimos em Israel como retribuição pelo trabalho que fazem ao servirem na Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4279, 'nvi', 'Números', 'nm', 18, 22, 'De agora em diante os israelitas não poderão aproximar-se da Tenda do Encontro, caso contrário, sofrerão as conseqüências do seu pecado e morrerão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4280, 'nvi', 'Números', 'nm', 18, 23, 'É dever dos levitas fazer o trabalho na Tenda do Encontro e assumir a responsabilidade pelas ofensas contra ela. Este é um decreto perpétuo pelas suas gerações. Eles não receberão herança alguma entre os israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4281, 'nvi', 'Números', 'nm', 18, 24, 'Em vez disso, dou como herança aos levitas os dízimos que os israelitas apresentarem como contribuição ao Senhor. É por isso que eu disse que eles não teriam herança alguma entre os israelitas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4282, 'nvi', 'Números', 'nm', 18, 25, 'O Senhor disse depois a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4283, 'nvi', 'Números', 'nm', 18, 26, '"Diga o seguinte aos levitas: Quando receberem dos israelitas o dízimo que lhes dou como herança, vocês deverão apresentar um décimo daquele dízimo como contribuição pertencente ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4284, 'nvi', 'Números', 'nm', 18, 27, 'Essa contribuição será à do trigo tirado da eira e do vinho do tanque de prensar uvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4285, 'nvi', 'Números', 'nm', 18, 28, 'Assim, vocês apresentarão uma contribuição ao Senhor de todos os dízimos que receberam dos israelitas. Desses dízimos vocês darão a contribuição do Senhor ao sacerdote Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4286, 'nvi', 'Números', 'nm', 18, 29, 'E deverão apresentar como contribuição ao Senhor a melhor parte, a parte sagrada de tudo o que for dado a vocês.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4287, 'nvi', 'Números', 'nm', 18, 30, '"Diga aos levitas: Quando vocês apresentarem a melhor parte, ela será considerada equivalente ao produto da eira e do tanque de prensar uvas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4288, 'nvi', 'Números', 'nm', 18, 31, 'Vocês e suas famílias poderão comer dessa porção em qualquer lugar, pois é o salário pelo trabalho de vocês na Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4289, 'nvi', 'Números', 'nm', 18, 32, 'Ao apresentarem a melhor parte, vocês não se tornarão culpados e não profanarão as ofertas sagradas dos israelitas, para que não morram".');
