INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4290, 'nvi', 'Números', 'nm', 19, 1, 'Disse também o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4291, 'nvi', 'Números', 'nm', 19, 2, '"Esta é uma exigência da lei que o Senhor ordenou: Mande os israelitas trazerem uma novilha vermelha, sem defeito e sem mancha, sobre a qual nunca tenha sido colocada uma canga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4292, 'nvi', 'Números', 'nm', 19, 3, 'Vocês a darão ao sacerdote Eleazar; ela será levada para fora do acampamento e sacrificada na presença dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4293, 'nvi', 'Números', 'nm', 19, 4, 'Então o sacerdote Eleazar pegará um pouco do sangue com o dedo e o aspergirá sete vezes, na direção da entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4294, 'nvi', 'Números', 'nm', 19, 5, 'Na presença dele a novilha será queimada: o couro, a carne, o sangue e o excremento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4295, 'nvi', 'Números', 'nm', 19, 6, 'O sacerdote apanhará um pedaço de madeira de cedro, hissopo e lã vermelha e os atirará ao fogo que estiver queimando a novilha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4296, 'nvi', 'Números', 'nm', 19, 7, 'Depois disso o sacerdote lavará as suas roupas e se banhará com água. Então poderá entrar no acampamento, mas estará impuro até o cair da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4297, 'nvi', 'Números', 'nm', 19, 8, 'Aquele que queimar a novilha também lavará as suas roupas e se banhará com água, e também estará impuro até o cair da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4298, 'nvi', 'Números', 'nm', 19, 9, '"Um homem cerimonialmente puro recolherá as cinzas da novilha e as colocará num local puro, fora do acampamento. Serão guardadas pela comunidade de Israel para uso na água da purificação, para a purificação de pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4299, 'nvi', 'Números', 'nm', 19, 10, 'Aquele que recolher as cinzas da novilha também lavará as suas roupas, e ficará impuro até o cair da tarde. Este é um decreto perpétuo, tanto para os israelitas como para os estrangeiros residentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4300, 'nvi', 'Números', 'nm', 19, 11, '"Quem tocar num cadáver humano ficará impuro durante sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4301, 'nvi', 'Números', 'nm', 19, 12, 'Deverá purificar-se com essa água no terceiro e no sétimo dia; então estará puro. Mas, se não se purificar no terceiro e no sétimo dia, não estará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4302, 'nvi', 'Números', 'nm', 19, 13, 'Quem tocar num cadáver humano e não se purificar, contamina o tabernáculo do Senhor e será eliminado de Israel. Ficará impuro porque a água da purificação não foi derramada sobre ele; sua impureza permanece sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4303, 'nvi', 'Números', 'nm', 19, 14, '"Esta é a lei que se aplica quando alguém morre numa tenda: quem entrar na tenda e quem nela estiver ficará impuro sete dias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4304, 'nvi', 'Números', 'nm', 19, 15, 'e qualquer recipiente que não estiver bem fechado ficará impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4305, 'nvi', 'Números', 'nm', 19, 16, '"Quem estiver no campo e tocar em alguém que tenha sido morto à espada, ou em que tenha sofrido morte natural, ou num osso humano, ou num túmulo, ficará impuro durante sete dias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4306, 'nvi', 'Números', 'nm', 19, 17, '"Pela pessoa impura, colocarão um pouco das cinzas do holocausto de purificação num jarro e derramarão água da fonte por cima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4307, 'nvi', 'Números', 'nm', 19, 18, 'Então um homem cerimonialmente puro pegará hissopo, molhará na água e a aspergirá sobre a tenda, sobre todos os utensílios e sobre todas as pessoas que estavam ali. Também a aspergirá sobre todo aquele que tiver tocado num osso humano, ou num túmulo, ou em alguém que tenha sido morto ou que tenha sofrido morte natural.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4308, 'nvi', 'Números', 'nm', 19, 19, 'Aquele que estiver puro a aspergirá sobre a pessoa impura no terceiro e no sétimo dia, e no sétimo dia deverá purificá-la. Aquele que estiver sendo purificado lavará as suas roupas e se banhará com água, e naquela tarde estará puro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4309, 'nvi', 'Números', 'nm', 19, 20, 'Mas, se aquele que estiver impuro não se purificar, será eliminado da assembléia, pois contaminou o santuário do Senhor. A água da purificação não foi aspergida sobre ele, e ele está impuro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4310, 'nvi', 'Números', 'nm', 19, 21, 'Este é um decreto perpétuo para eles. "O homem que aspergir a água da purificação também lavará as suas roupas, e todo aquele que tocar na água da purificação ficará impuro até o cair da tarde.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4311, 'nvi', 'Números', 'nm', 19, 22, 'Qualquer coisa na qual alguém que estiver impuro tocar se tornará impura, e qualquer pessoa que nela tocar ficará impura até o cair da tarde".');
