INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4312, 'nvi', 'Números', 'nm', 20, 1, 'No primeiro mês toda a comunidade de Israel chegou ao deserto de Zim e ficou em Cades. Ali Miriã morreu e foi sepultada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4313, 'nvi', 'Números', 'nm', 20, 2, 'Não havia água para a comunidade, e o povo se juntou contra Moisés e contra Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4314, 'nvi', 'Números', 'nm', 20, 3, 'Discutiram com Moisés e disseram: "Quem dera tivéssemos morrido quando os nossos irmãos caíram mortos perante o Senhor!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4315, 'nvi', 'Números', 'nm', 20, 4, 'Por que vocês trouxeram a assembléia do Senhor a este deserto, para que nós e os nossos rebanhos morrêssemos aqui?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4316, 'nvi', 'Números', 'nm', 20, 5, 'Por que vocês nos tiraram do Egito e nos trouxeram para este lugar terrível? Aqui não há cereal, nem figos, nem uvas, nem romãs, nem água para beber! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4317, 'nvi', 'Números', 'nm', 20, 6, 'Moisés e Arão saíram de diante da assembléia para a entrada da Tenda do Encontro e se prostraram, rosto em terra, e a glória do Senhor lhes apareceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4318, 'nvi', 'Números', 'nm', 20, 7, 'E o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4319, 'nvi', 'Números', 'nm', 20, 8, '"Pegue a vara, e com o seu irmão Arão reúna a comunidade e diante desta fale àquela rocha, e ela verterá água. Vocês tirarão água da rocha para a comunidade e os rebanhos beberem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4320, 'nvi', 'Números', 'nm', 20, 9, 'Então Moisés pegou a vara que estava diante do Senhor, como este lhe havia ordenado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4321, 'nvi', 'Números', 'nm', 20, 10, 'Moisés e Arão reuniram a assembléia em frente da rocha, e Moisés disse: "Escutem, rebeldes, será que teremos que tirar água desta rocha para lhes dar? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4322, 'nvi', 'Números', 'nm', 20, 11, 'Então Moisés ergueu o braço e bateu na rocha duas vezes com a vara. Jorrou água, e a comunidade e os rebanhos beberam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4323, 'nvi', 'Números', 'nm', 20, 12, 'O Senhor, porém, disse a Moisés e a Arão: "Como vocês não confiaram em mim para honrar minha santidade à vista dos israelitas, vocês não conduzirão esta comunidade para a terra que lhes dou".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4324, 'nvi', 'Números', 'nm', 20, 13, 'Essas foram as águas de Meribá, onde os israelitas discutiram com o Senhor e onde ele manifestou sua santidade entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4325, 'nvi', 'Números', 'nm', 20, 14, 'De Cades, Moisés enviou mensageiros ao rei de Edom, dizendo: "Assim diz o teu irmão Israel: Tu sabes de todas as dificuldades que vieram sobre nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4326, 'nvi', 'Números', 'nm', 20, 15, 'Os nossos antepassados desceram para o Egito, e ali vivemos durante muitos anos. Os egípcios, porém, nos maltrataram, como também a eles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4327, 'nvi', 'Números', 'nm', 20, 16, 'mas quando clamamos ao Senhor, ele ouviu o nosso clamor, enviou um anjo e nos tirou do Egito. "Agora estamos em Cades, cidade na fronteira do teu território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4328, 'nvi', 'Números', 'nm', 20, 17, 'Deixa-nos atravessar a tua terra. Não passaremos por nenhuma plantação ou vinha, nem beberemos água de poço algum. Passaremos pela estrada do rei e não nos desviaremos nem para a direita nem para a esquerda, até que tenhamos atravessado o teu território".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4329, 'nvi', 'Números', 'nm', 20, 18, 'Mas Edom respondeu: "Vocês não poderão passar por aqui; se tentarem, nós os atacaremos com a espada".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4330, 'nvi', 'Números', 'nm', 20, 19, 'E os israelitas disseram: "Iremos pela estrada principal; se nós e os nossos rebanhos bebermos de tua água, pagaremos por ela. Queremos apenas atravessar a pé, e nada mais".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4331, 'nvi', 'Números', 'nm', 20, 20, 'Mas Edom insistiu: "Vocês não poderão atravessar". Então Edom os atacou com um exército grande e poderoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4332, 'nvi', 'Números', 'nm', 20, 21, 'Visto que Edom se recusou a deixá-los atravessar o seu território, Israel desviou-se dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4333, 'nvi', 'Números', 'nm', 20, 22, 'Toda a comunidade israelita partiu de Cades e chegou ao monte Hor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4334, 'nvi', 'Números', 'nm', 20, 23, 'Naquele monte, perto da fronteira de Edom, o Senhor disse a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4335, 'nvi', 'Números', 'nm', 20, 24, '"Arão será reunido aos seus antepassados. Não entrará na terra que dou aos israelitas, porque vocês dois se rebelaram contra a minha ordem junto às águas de Meribá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4336, 'nvi', 'Números', 'nm', 20, 25, 'Leve Arão e seu filho Eleazar para o alto do monte Hor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4337, 'nvi', 'Números', 'nm', 20, 26, 'Tire as vestes de Arão e coloque-as em seu filho Eleazar, pois Arão será reunido aos seus antepassados; ele morrerá ali".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4338, 'nvi', 'Números', 'nm', 20, 27, 'Moisés fez conforme o Senhor ordenou; subiram o monte Hor à vista de toda a comunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4339, 'nvi', 'Números', 'nm', 20, 28, 'Moisés tirou as vestes de Arão e as colocou em seu filho Eleazar. E Arão morreu no alto do monte. Depois disso, Moisés e Eleazar desceram do monte,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4340, 'nvi', 'Números', 'nm', 20, 29, 'e, quando toda a comunidade soube que Arão tinha morrido, toda a nação de Israel pranteou por ele durante trinta dias.');
