INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4341, 'nvi', 'Números', 'nm', 21, 1, 'Quando o rei cananeu de Arade, que vivia no Neguebe, soube que Israel vinha pela estrada de Atarim, atacou os israelitas e capturou alguns deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4342, 'nvi', 'Números', 'nm', 21, 2, 'Então Israel fez este voto ao Senhor: "Se entregares este povo em nossas mãos, destruiremos totalmente as suas cidades".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4343, 'nvi', 'Números', 'nm', 21, 3, 'O Senhor ouviu o pedido de Israel e lhes entregou os cananeus. Israel os destruiu completamente, a eles e às suas cidades; de modo que o lugar foi chamado Hormá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4344, 'nvi', 'Números', 'nm', 21, 4, 'Partiram eles do monte Hor pelo caminho do mar Vermelho, para contornarem a terra de Edom. Mas o povo ficou impaciente no caminho');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4345, 'nvi', 'Números', 'nm', 21, 5, 'e falou contra Deus e contra Moisés, dizendo: "Por que vocês nos tiraram do Egito para morrermos no deserto? Não há pão! Não há água! E nós detestamos esta comida miserável! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4346, 'nvi', 'Números', 'nm', 21, 6, 'Então o Senhor enviou serpentes venenosas que morderam o povo, e muitos morreram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4347, 'nvi', 'Números', 'nm', 21, 7, 'O povo foi a Moisés e disse: "Pecamos quando falamos contra o Senhor e contra você. Ore pedindo ao Senhor que tire as serpentes do meio de nós". E Moisés orou pelo povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4348, 'nvi', 'Números', 'nm', 21, 8, 'O Senhor disse a Moisés: "Faça uma serpente e coloque-a no alto de um poste; quem for mordido e olhar para ela viverá".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4349, 'nvi', 'Números', 'nm', 21, 9, 'Moisés fez então uma serpente de bronze e a colocou num poste. Quando alguém era mordido por uma serpente e olhava para a serpente de bronze, permanecia vivo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4350, 'nvi', 'Números', 'nm', 21, 10, 'Os israelitas partiram e acamparam em Obote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4351, 'nvi', 'Números', 'nm', 21, 11, 'Depois partiram de Obote e acamparam em Ijé-Abarim, no deserto defronte de Moabe, ao leste.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4352, 'nvi', 'Números', 'nm', 21, 12, 'Dali partiram e acamparam no vale de Zerede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4353, 'nvi', 'Números', 'nm', 21, 13, 'Partiram dali e acamparam do outro lado do Arnom, que fica no deserto que se estende até o território amorreu. O Arnom é a fronteira de Moabe, entre Moabe e os amorreus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4354, 'nvi', 'Números', 'nm', 21, 14, 'É por isso que se diz no Livro das Guerras do Senhor: "... Vaebe, em Sufá, e os vales, o Arnom');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4355, 'nvi', 'Números', 'nm', 21, 15, 'e as ravinas dos vales que se estendem até a cidade de Ar e chegam até a fronteira de Moabe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4356, 'nvi', 'Números', 'nm', 21, 16, 'De lá prosseguiram até Beer, ao poço do qual o Senhor disse a Moisés: "Reúna o povo, e eu lhe darei água".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4357, 'nvi', 'Números', 'nm', 21, 17, 'Então Israel cantou esta canção: "Brote água, ó poço! Cantem a seu respeito,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4358, 'nvi', 'Números', 'nm', 21, 18, 'a respeito do poço que os líderes cavaram, que os nobres abriram com cetros e cajados". Então saíram do deserto para Mataná,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4359, 'nvi', 'Números', 'nm', 21, 19, 'de Mataná para Naaliel, de Naaliel para Bamote,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4360, 'nvi', 'Números', 'nm', 21, 20, 'e de Bamote para o vale de Moabe, onde o topo do Pisga defronta com o deserto de Jesimom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4361, 'nvi', 'Números', 'nm', 21, 21, 'Israel enviou mensageiros para dizer a Seom, rei dos amorreus:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4362, 'nvi', 'Números', 'nm', 21, 22, '"Deixa-nos atravessar a tua terra. Não entraremos em nenhuma plantação, em nenhuma vinha, nem beberemos água de poço algum. Passaremos pela estrada do rei até que tenhamos atravessado o teu território".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4363, 'nvi', 'Números', 'nm', 21, 23, 'Seom, porém, não deixou Israel atravessar o seu território. Convocou todo o seu exército e atacou Israel no deserto. Quando chegou a Jaza, lutou contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4364, 'nvi', 'Números', 'nm', 21, 24, 'Porém Israel o destruiu com a espada e tomou-lhe as terras desde o Arnom até o Jaboque, até o território dos amonitas, pois Jazar estava na fronteira dos amonitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4365, 'nvi', 'Números', 'nm', 21, 25, 'Israel capturou todas as cidades dos amorreus e as ocupou, inclusive Hesbom e todos os seus povoados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4366, 'nvi', 'Números', 'nm', 21, 26, 'Hesbom era a cidade de Seom, rei dos amorreus, que havia lutado contra o antigo rei de Moabe, tendo lhe tomado todas as suas terras até o Arnom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4367, 'nvi', 'Números', 'nm', 21, 27, 'É por isso que os poetas dizem: "Venham a Hesbom! Seja ela reconstruída; seja restaurada a cidade de Seom!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4368, 'nvi', 'Números', 'nm', 21, 28, '"Fogo saiu de Hesbom, uma chama da cidade de Seom; consumiu Ar, de Moabe, os senhores do alto Arnom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4369, 'nvi', 'Números', 'nm', 21, 29, 'Ai de você, Moabe! Você está destruído, ó povo de Camos! Ele fez de seus filhos, fugitivos e de suas filhas, prisioneiras de Seom, rei dos amorreus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4370, 'nvi', 'Números', 'nm', 21, 30, '"Mas nós os derrotamos; Hesbom está destruída por todo o caminho até Dibom. Nós os arrasamos até Nofá, e até Medeba".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4371, 'nvi', 'Números', 'nm', 21, 31, 'Assim Israel habitou na terra dos amorreus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4372, 'nvi', 'Números', 'nm', 21, 32, 'Moisés enviou espiões a Jazar, e os israelitas tomaram os povoados ao redor e expulsaram os amorreus que ali estavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4373, 'nvi', 'Números', 'nm', 21, 33, 'Depois voltaram e subiram pelo caminho de Basã, e Ogue, rei de Basã, com todo o seu exército, marchou para enfrentá-los em Edrei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4374, 'nvi', 'Números', 'nm', 21, 34, 'Mas o Senhor disse a Moisés: "Não tenha medo dele, pois eu o entreguei a você, juntamente com todo o seu exército e com a sua terra. Você fará com ele o que fez com Seom, rei dos amorreus, que habitava em Hesbom".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4375, 'nvi', 'Números', 'nm', 21, 35, 'Então eles o derrotaram, bem como os seus filhos e todo o seu exército, não lhes deixando sobrevivente algum. E tomaram posse da terra dele.');
