INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4376, 'nvi', 'Números', 'nm', 22, 1, 'Os israelitas partiram e acamparam nas campinas de Moabe, para além do Jordão, perto de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4377, 'nvi', 'Números', 'nm', 22, 2, 'Balaque, filho de Zipor, viu tudo o que Israel tinha feito aos amorreus,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4378, 'nvi', 'Números', 'nm', 22, 3, 'e Moabe teve muito medo do povo, porque era muita gente. Moabe teve pavor dos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4379, 'nvi', 'Números', 'nm', 22, 4, 'Então os moabitas disseram aos líderes de Midiã: "Essa multidão devorará tudo o que há ao nosso redor, como o boi devora o capim do pasto". Balaque, filho de Zipor, rei de Moabe naquela época,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4380, 'nvi', 'Números', 'nm', 22, 5, 'enviou mensageiros para chamar Balaão, filho de Beor, que estava em Petor, perto do Rio, em sua terra natal. A mensagem de Balaque dizia: "Um povo que saiu do Egito cobre a face da terra e se estabeleceu perto de mim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4381, 'nvi', 'Números', 'nm', 22, 6, 'Venha agora lançar uma maldição contra ele, pois é forte demais para mim. Talvez então eu tenha condições de derrotá-lo e de expulsá-lo da terra. Pois sei que quem você abençoa é abençoado, e quem você amaldiçoa é amaldiçoado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4382, 'nvi', 'Números', 'nm', 22, 7, 'Os líderes de Moabe e os de Midiã partiram, levando consigo o preço para os encantamentos mágicos. Quando chegaram, comunicaram a Balaão o que Balaque tinha dito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4383, 'nvi', 'Números', 'nm', 22, 8, 'Disse-lhes Balaão: "Passem a noite aqui, e eu lhes trarei a resposta que o Senhor me der". E os líderes moabitas ficaram com ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4384, 'nvi', 'Números', 'nm', 22, 9, 'Deus veio a Balaão e lhe perguntou: "Quem são esses homens que estão com você? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4385, 'nvi', 'Números', 'nm', 22, 10, 'Balaão respondeu a Deus: "Balaque, filho de Zipor, rei de Moabe, enviou-me esta mensagem:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4386, 'nvi', 'Números', 'nm', 22, 11, '‘Um povo que saiu do Egito cobre a face da terra. Venha agora lançar uma maldição contra ele. Talvez então eu tenha condições de derrotá-lo e de expulsá-lo’ ".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4387, 'nvi', 'Números', 'nm', 22, 12, 'Mas Deus disse a Balaão: "Não vá com eles. Você não poderá amaldiçoar este povo, porque é povo abençoado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4388, 'nvi', 'Números', 'nm', 22, 13, 'Na manhã seguinte Balaão se levantou e disse aos líderes de Balaque: "Voltem para a sua terra, pois o Senhor não permitiu que eu os acompanhe".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4389, 'nvi', 'Números', 'nm', 22, 14, 'Os líderes moabitas voltaram a Balaque e lhe disseram: "Balaão recusou-se a acompanhar-nos".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4390, 'nvi', 'Números', 'nm', 22, 15, 'Balaque enviou outros líderes, em maior número e mais importantes do que os primeiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4391, 'nvi', 'Números', 'nm', 22, 16, 'Eles foram a Balaão e lhe disseram: "Assim diz Balaque, filho de Zipor: Que nada o impeça de vir a mim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4392, 'nvi', 'Números', 'nm', 22, 17, 'porque o recompensarei generosamente e farei tudo o que você me disser. Venha, por favor, e lance para mim uma maldição contra este povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4393, 'nvi', 'Números', 'nm', 22, 18, 'Balaão, porém, respondeu aos conselheiros de Balaque: "Mesmo que Balaque me desse o seu palácio cheio de prata e de ouro, eu não poderia fazer coisa alguma, grande ou pequena, que vá além da ordem do Senhor meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4394, 'nvi', 'Números', 'nm', 22, 19, 'Agora, fiquem também vocês aqui esta noite, e eu descobrirei o que mais o Senhor tem para dizer-me".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4395, 'nvi', 'Números', 'nm', 22, 20, 'Naquela noite Deus veio a Balaão e lhe disse: "Visto que esses homens vieram chamá-lo, vá com eles, mas faça apenas o que eu lhe disser".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4396, 'nvi', 'Números', 'nm', 22, 21, 'Balaão levantou-se pela manhã, pôs a sela sobre a sua jumenta e foi com os líderes de Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4397, 'nvi', 'Números', 'nm', 22, 22, 'Mas acendeu-se a ira de Deus quando ele foi, e o anjo do Senhor pôs-se no caminho para impedi-lo de prosseguir. Balaão ia montado em sua jumenta, e seus dois servos o acompanhavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4398, 'nvi', 'Números', 'nm', 22, 23, 'Quando a jumenta viu o anjo do Senhor parado no caminho, empunhando uma espada, saiu do caminho e foi-se pelo campo. Balaão bateu nela para fazê-la voltar ao caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4399, 'nvi', 'Números', 'nm', 22, 24, 'Então o anjo do Senhor se pôs num caminho estreito entre duas vinhas, com muros dos dois lados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4400, 'nvi', 'Números', 'nm', 22, 25, 'Quando a jumenta viu o anjo do Senhor, encostou-se no muro, apertando o pé de Balaão contra ele. Por isso ele bateu nela de novo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4401, 'nvi', 'Números', 'nm', 22, 26, 'O anjo do Senhor foi adiante e se colocou num lugar estreito, e não havia espaço para desviar, nem para a direita nem para a esquerda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4402, 'nvi', 'Números', 'nm', 22, 27, 'Quando a jumenta viu o anjo do Senhor, deitou-se debaixo de Balaão. Acendeu-se a ira de Balaão, que bateu nela com a sua vara.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4403, 'nvi', 'Números', 'nm', 22, 28, 'Então o Senhor abriu a boca da jumenta, e ela disse a Balaão: "Que foi que eu lhe fiz, para você bater em mim três vezes? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4404, 'nvi', 'Números', 'nm', 22, 29, 'Balaão respondeu à jumenta: "Você me fez de tolo! Quem dera eu tivesse uma espada na mão; eu a mataria agora mesmo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4405, 'nvi', 'Números', 'nm', 22, 30, 'Mas a jumenta disse a Balaão: "Não sou sua jumenta, que você sempre montou até o dia de hoje? Tenho eu o costume de fazer isso com você? " "Não", disse ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4406, 'nvi', 'Números', 'nm', 22, 31, 'Então o Senhor abriu os olhos de Balaão, e ele viu o anjo do Senhor parado no caminho, empunhando a sua espada. Então Balaão inclinou-se e prostrou-se, rosto em terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4407, 'nvi', 'Números', 'nm', 22, 32, 'E o anjo do Senhor lhe perguntou: "Por que você bateu três vezes em sua jumenta? Eu vim aqui para impedi-lo de prosseguir porque o seu caminho me desagrada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4408, 'nvi', 'Números', 'nm', 22, 33, 'A jumenta me viu e se afastou de mim por três vezes. Se ela não se afastasse a esta altura eu certamente o teria matado; mas a ela eu teria poupado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4409, 'nvi', 'Números', 'nm', 22, 34, 'Balaão disse ao anjo do Senhor: "Pequei. Não percebi que estavas parado no caminho para me impedires de prosseguir. Agora, se o que estou fazendo te desagrada, eu voltarei".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4410, 'nvi', 'Números', 'nm', 22, 35, 'Então o anjo do Senhor disse a Balaão: "Vá com os homens, mas fale apenas o que eu lhe disser". Assim Balaão foi com os príncipes de Balaque.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4411, 'nvi', 'Números', 'nm', 22, 36, 'Quando Balaque soube que Balaão estava chegando, foi ao seu encontro na cidade moabita da fronteira do Arnom, no limite do seu território.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4412, 'nvi', 'Números', 'nm', 22, 37, 'E Balaque disse a Balaão: "Não mandei chamá-lo urgentemente? Por que não veio? Acaso não tenho condições de recompensá-lo? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4413, 'nvi', 'Números', 'nm', 22, 38, '"Aqui estou! ", respondeu Balaão. "Mas, seria eu capaz de dizer alguma coisa? Direi somente o que Deus puser em minha boca".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4414, 'nvi', 'Números', 'nm', 22, 39, 'Então Balaão foi com Balaque até Quiriate-Huzote.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4415, 'nvi', 'Números', 'nm', 22, 40, 'Balaque sacrificou bois e ovelhas, e deu parte da carne a Balaão e aos líderes que com ele estavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4416, 'nvi', 'Números', 'nm', 22, 41, 'Na manhã seguinte Balaque levou Balaão até o alto de Bamote-Baal, de onde viu uma parte do povo.');
