INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4417, 'nvi', 'Números', 'nm', 23, 1, 'Balaão disse a Balaque: "Construa para mim aqui sete altares e prepare-me sete novilhos e sete carneiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4418, 'nvi', 'Números', 'nm', 23, 2, 'Balaque fez o que Balaão pediu, e os dois ofereceram um novilho e um carneiro em cada altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4419, 'nvi', 'Números', 'nm', 23, 3, 'E Balaão disse a Balaque: "Fique aqui junto ao seu holocausto, enquanto eu me retiro. Talvez o Senhor venha ao meu encontro. O que ele me revelar eu lhe contarei". E foi para um monte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4420, 'nvi', 'Números', 'nm', 23, 4, 'Deus o encontrou, e Balaão disse: "Preparei sete altares, e em cada altar ofereci um novilho e um carneiro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4421, 'nvi', 'Números', 'nm', 23, 5, 'O Senhor pôs uma mensagem na boca de Balaão e disse: "Volte a Balaque e dê-lhe essa mensagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4422, 'nvi', 'Números', 'nm', 23, 6, 'Ele voltou a Balaque e o encontrou ao lado de seu holocausto, e com ele todos os líderes de Moabe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4423, 'nvi', 'Números', 'nm', 23, 7, 'Então Balaão pronunciou este oráculo: "Balaque trouxe-me de Arã, o rei de Moabe, buscou-me nas montanhas do Oriente. ‘Venha, amaldiçoe a Jacó para mim’, disse ele, ‘venha, pronuncie ameaças contra Israel! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4424, 'nvi', 'Números', 'nm', 23, 8, 'Como posso amaldiçoar a quem Deus não amaldiçoou? Como posso pronunciar ameaças quem o Senhor não quis ameaçar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4425, 'nvi', 'Números', 'nm', 23, 9, 'Dos cumes rochosos eu os vejo, dos montes eu os avisto. Vejo um povo que vive separado e não se considera como qualquer nação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4426, 'nvi', 'Números', 'nm', 23, 10, 'Quem pode contar o pó de Jacó ou o número da quarta parte de Israel? Morra eu a morte dos justos, e seja o meu fim como o deles! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4427, 'nvi', 'Números', 'nm', 23, 11, 'Então Balaque disse a Balaão: "Que foi que você me fez? Eu o chamei para amaldiçoar meus inimigos, mas você nada fez senão abençoá-los! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4428, 'nvi', 'Números', 'nm', 23, 12, 'E ele respondeu: "Será que não devo dizer o que o Senhor põe em minha boca? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4429, 'nvi', 'Números', 'nm', 23, 13, 'Balaque lhe disse: "Venha comigo a outro lugar de onde você poderá vê-los; você verá só uma parte, mas não todos eles. E dali amaldiçoe este povo para mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4430, 'nvi', 'Números', 'nm', 23, 14, 'Então ele o levou para o campo de Zofim, no topo do Pisga, e ali construiu sete altares e ofereceu um novilho e um carneiro em cada altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4431, 'nvi', 'Números', 'nm', 23, 15, 'Balaão disse a Balaque: "Fique aqui ao lado de seu holocausto enquanto vou me encontrar com ele ali adiante".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4432, 'nvi', 'Números', 'nm', 23, 16, 'Encontrando-se o Senhor com Balaão, pôs uma mensagem em sua boca e disse: "Volte a Balaque e dê-lhe essa mensagem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4433, 'nvi', 'Números', 'nm', 23, 17, 'Ele voltou e o encontrou ao lado de seu holocausto, e com ele os líderes de Moabe. Balaque perguntou-lhe: "O que o Senhor disse? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4434, 'nvi', 'Números', 'nm', 23, 18, 'Então ele pronunciou este oráculo: "Levante-se, Balaque, e ouça-me; escute-me, filho de Zipor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4435, 'nvi', 'Números', 'nm', 23, 19, 'Deus não é homem para que minta, nem filho de homem para que se arrependa. Acaso ele fala, e deixa de agir? Acaso promete, e deixa de cumprir?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4436, 'nvi', 'Números', 'nm', 23, 20, 'Recebi uma ordem para abençoar; ele abençoou, e não o posso mudar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4437, 'nvi', 'Números', 'nm', 23, 21, 'Nenhuma desgraça se vê em Jacó, nenhum sofrimento em Israel. O Senhor, o seu Deus, está com eles; o brado de aclamação do Rei está no meio deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4438, 'nvi', 'Números', 'nm', 23, 22, 'Deus os está trazendo do Egito; eles têm a força do boi selvagem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4439, 'nvi', 'Números', 'nm', 23, 23, 'Não há magia que possa contra Jacó, nem encantamento contra Israel. Agora se dirá de Jacó e de Israel: ‘Vejam o que Deus tem feito! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4440, 'nvi', 'Números', 'nm', 23, 24, 'O povo se levanta como leoa; levanta-se como o leão, que não se deita até que devore a sua presa e beba o sangue das suas vítimas".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4441, 'nvi', 'Números', 'nm', 23, 25, 'Balaque disse então a Balaão: "Não os amaldiçoe nem os abençoe! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4442, 'nvi', 'Números', 'nm', 23, 26, 'Balaão respondeu: "Não lhe disse que devo fazer tudo o que o Senhor disser? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4443, 'nvi', 'Números', 'nm', 23, 27, 'Balaque disse a Balaão: "Venha, deixe-me levá-lo a outro lugar. Talvez Deus se agrade que dali você os amaldiçoe para mim".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4444, 'nvi', 'Números', 'nm', 23, 28, 'E Balaque levou Balaão para o topo do Peor, de onde se vê o deserto de Jesimom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4445, 'nvi', 'Números', 'nm', 23, 29, 'Balaão disse a Balaque: "Edifique-me aqui sete altares, e prepare-me sete novilhos e sete carneiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4446, 'nvi', 'Números', 'nm', 23, 30, 'Balaque fez o que Balaão disse, e ofereceu um novilho e um carneiro em cada altar.');
