INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4447, 'nvi', 'Números', 'nm', 24, 1, 'Quando Balaão viu que agradava ao Senhor abençoar Israel, não recorreu à magia como nas outras vezes, mas voltou o rosto para o deserto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4448, 'nvi', 'Números', 'nm', 24, 2, 'Então viu Israel acampado, tribo por tribo; e o Espírito de Deus veio sobre ele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4449, 'nvi', 'Números', 'nm', 24, 3, 'e ele pronunciou este oráculo: "Palavra de Balaão, filho de Beor, palavra daquele cujos olhos vêem claramente,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4450, 'nvi', 'Números', 'nm', 24, 4, 'palavra daquele que ouve as palavras de Deus, daquele que vê a visão que vem do Todo-poderoso, daquele que cai prostrado e vê com clareza:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4451, 'nvi', 'Números', 'nm', 24, 5, '"Quão belas são as suas tendas, ó Jacó, as suas habitações, ó Israel!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4452, 'nvi', 'Números', 'nm', 24, 6, 'Como vales estendem-se, como jardins que margeiam rios, como aloés plantados pelo Senhor, como cedros junto às águas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4453, 'nvi', 'Números', 'nm', 24, 7, 'Seus reservatórios de água transbordarão; suas lavouras serão bem irrigadas. "O seu rei será maior do que Agague; o seu reino será exaltado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4454, 'nvi', 'Números', 'nm', 24, 8, 'Deus os está trazendo do Egito; eles têm a força do boi selvagem. Devoram nações inimigas e despedaçam seus ossos; com suas flechas os atravessam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4455, 'nvi', 'Números', 'nm', 24, 9, 'Como o leão e a leoa eles se abaixam e se deitam, quem ousará despertá-los? Sejam abençoados os que os abençoarem, e amaldiçoados os que os amaldiçoarem! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4456, 'nvi', 'Números', 'nm', 24, 10, 'Então acendeu-se a ira de Balaque contra Balaão, e, batendo as palmas das mãos, disse: "Eu o chamei para amaldiçoar meus inimigos, mas você já os abençoou três vezes!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4457, 'nvi', 'Números', 'nm', 24, 11, 'Agora, fuja para a sua casa! Eu disse que lhe daria generosa recompensa, mas o Senhor o impediu de recebê-la".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4458, 'nvi', 'Números', 'nm', 24, 12, 'Mas Balaão respondeu a Balaque: "Eu não disse aos mensageiros que você me enviou:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4459, 'nvi', 'Números', 'nm', 24, 13, 'Mesmo que Balaque me desse o seu palácio cheio de prata e de ouro, eu não poderia fazer coisa alguma de minha própria vontade, boa ou má, que vá além da ordem do Senhor, e que devo dizer somente o que o Senhor disser?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4460, 'nvi', 'Números', 'nm', 24, 14, 'Agora estou voltando para o meu povo, mas venha, deixe-me adverti-lo do que este povo fará ao seu povo nos dias futuros". O Quarto Oráculo de Balaão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4461, 'nvi', 'Números', 'nm', 24, 15, 'Então pronunciou este seu oráculo: "Palavra de Balaão, filho de Beor, palavra daquele cujos olhos vêem claramente,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4462, 'nvi', 'Números', 'nm', 24, 16, 'daquele que ouve as palavras de Deus, que possui o conhecimento do Altíssimo, daquele que vê a visão que vem do Todo-poderoso, daquele que cai prostrado, e vê com clareza:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4463, 'nvi', 'Números', 'nm', 24, 17, 'Eu o vejo, mas não agora; eu o avisto, mas não de perto. Uma estrela surgirá de Jacó; um cetro se levantará de Israel. Ele esmagará as frontes de Moabe e o crânio de todos os descendentes de Sete.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4464, 'nvi', 'Números', 'nm', 24, 18, 'Edom será dominado; Seir, seu inimigo, também será dominado, mas Israel se fortalecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4465, 'nvi', 'Números', 'nm', 24, 19, 'De Jacó sairá o governo; ele destruirá os sobreviventes das cidades".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4466, 'nvi', 'Números', 'nm', 24, 20, 'Balaão viu Amaleque e pronunciou este oráculo: "Amaleque foi o primeiro entre as nações, mas o seu fim será destruição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4467, 'nvi', 'Números', 'nm', 24, 21, 'Depois viu os queneus e pronunciou este oráculo: "Sua habitação é segura, seu ninho está firmado na rocha;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4468, 'nvi', 'Números', 'nm', 24, 22, 'todavia, vocês, queneus, serão destruídos quando Assur os levar prisioneiros".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4469, 'nvi', 'Números', 'nm', 24, 23, 'Finalmente pronunciou este oráculo: "Ah, quem poderá viver quando Deus fizer isto?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4470, 'nvi', 'Números', 'nm', 24, 24, 'Navios virão da costa de Quitim e subjugarão Assur e Héber, mas o seu fim também será destruição".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4471, 'nvi', 'Números', 'nm', 24, 25, 'Então Balaão se levantou e voltou para casa, e Balaque seguiu o seu caminho.');
