INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4490, 'nvi', 'Números', 'nm', 26, 1, 'Depois da praga, o Senhor disse a Moisés e a Eleazar, filho do sacerdote Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4491, 'nvi', 'Números', 'nm', 26, 2, '"Façam um recenseamento de toda a comunidade de Israel, segundo as suas famílias; contem todos os de vinte anos para cima que possam servir no exército de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4492, 'nvi', 'Números', 'nm', 26, 3, 'Nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó, Moisés e o sacerdote Eleazar falaram com eles e disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4493, 'nvi', 'Números', 'nm', 26, 4, '"Façam um recenseamento dos homens de vinte anos para cima", conforme o Senhor tinha ordenado a Moisés. Estes foram os israelitas que saíram do Egito:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4494, 'nvi', 'Números', 'nm', 26, 5, 'Os descendentes de Rúben, filho mais velho de Israel, foram: de Enoque, o clã enoquita; de Palu, o clã paluíta;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4495, 'nvi', 'Números', 'nm', 26, 6, 'de Hezrom, o clã hezronita; de Carmi, o clã carmita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4496, 'nvi', 'Números', 'nm', 26, 7, 'Esses foram os clãs de Rúben; foram contados 43. 730 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4497, 'nvi', 'Números', 'nm', 26, 8, 'O filho de Palu foi Eliabe,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4498, 'nvi', 'Números', 'nm', 26, 9, 'e os filhos de Eliabe foram Nemuel, Datã e Abirão. Estes, Datã e Abirão, foram os líderes da comunidade que se rebelaram contra Moisés e contra Arão, estando entre os seguidores de Corá quando se rebelaram contra o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4499, 'nvi', 'Números', 'nm', 26, 10, 'A terra abriu a boca e os engoliu juntamente com Corá, cujos seguidores morreram quando o fogo devorou duzentos e cinqüenta homens, que serviram como sinal de advertência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4500, 'nvi', 'Números', 'nm', 26, 11, 'A descendência de Corá, contudo, não desapareceu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4501, 'nvi', 'Números', 'nm', 26, 12, 'Os descendentes de Simeão segundo os seus clãs foram: de Nemuel, o clã nemuelita; de Jamim, o clã jaminita; de Jaquim, o clã jaquinita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4502, 'nvi', 'Números', 'nm', 26, 13, 'de Zerá, o clã zeraíta; de Saul, o clã saulita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4503, 'nvi', 'Números', 'nm', 26, 14, 'Esses foram os clãs de Simeão; havia 22. 200 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4504, 'nvi', 'Números', 'nm', 26, 15, 'Os descendentes de Gade segundo os seus clãs foram: de Zefom, o clã zefonita; de Hagi, o clã hagita; de Suni, o clã sunita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4505, 'nvi', 'Números', 'nm', 26, 16, 'de Ozni, o clã oznita; de Eri, o clã erita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4506, 'nvi', 'Números', 'nm', 26, 17, 'de Arodi, o clã arodita; de Areli, o clã arelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4507, 'nvi', 'Números', 'nm', 26, 18, 'Esses foram os clãs de Gade; foram contados 40. 500 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4508, 'nvi', 'Números', 'nm', 26, 19, 'Er e Onã eram filhos de Judá, mas morreram em Canaã.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4509, 'nvi', 'Números', 'nm', 26, 20, 'Os descendentes de Judá segundo os seus clãs foram: de Selá, o clã selanita; de Perez, o clã perezita; de Zerá, o clã zeraíta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4510, 'nvi', 'Números', 'nm', 26, 21, 'Os descendentes de Perez foram: de Hezrom, o clã hezronita; de Hamul, o clã hamulita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4511, 'nvi', 'Números', 'nm', 26, 22, 'Esses foram os clãs de Judá; foram contados 76. 500 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4512, 'nvi', 'Números', 'nm', 26, 23, 'Os descendentes de Issacar segundo os seus clãs foram: de Tolá, o clã tolaíta; de Puá, o clã puvita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4513, 'nvi', 'Números', 'nm', 26, 24, 'de Jasube, o clã jasubita; de Sinrom, o clã sinronita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4514, 'nvi', 'Números', 'nm', 26, 25, 'Esses foram os clãs de Issacar; foram contados 64. 300 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4515, 'nvi', 'Números', 'nm', 26, 26, 'Os descendentes de Zebulom segundo os seus clãs foram: de Serede, o clã seredita; de Elom, o clã elonita; de Jaleel, o clã jaleelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4516, 'nvi', 'Números', 'nm', 26, 27, 'Esses foram os clãs de Zebulom; foram contados 60. 500 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4517, 'nvi', 'Números', 'nm', 26, 28, 'Os descendentes de José segundo os seus clãs, por meio de Manassés e Efraim, foram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4518, 'nvi', 'Números', 'nm', 26, 29, 'Os descendentes de Manassés: de Maquir, o clã maquirita ( Maquir foi o pai de Gileade ); de Gileade, o clã gileadita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4519, 'nvi', 'Números', 'nm', 26, 30, 'Estes foram os descendentes de Gileade: de Jezer, o clã jezerita; de Heleque, o clã helequita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4520, 'nvi', 'Números', 'nm', 26, 31, 'de Asriel, o clã asrielita; de Siquém, o clã siquemita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4521, 'nvi', 'Números', 'nm', 26, 32, 'de Semida, o clã semidaíta; de Héfer, o clã heferita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4522, 'nvi', 'Números', 'nm', 26, 33, '( Zelofeade, filho de Héfer, não teve filhos; teve somente filhas, cujos nomes eram Maalá, Noa, Hogla, Milca e Tirza. )');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4523, 'nvi', 'Números', 'nm', 26, 34, 'Esses foram os clãs de Manassés; foram contados 52. 700 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4524, 'nvi', 'Números', 'nm', 26, 35, 'Os descendentes de Efraim segundo os seus clãs foram: de Sutela, o clã sutelaíta; de Bequer, o clã bequerita; de Taã, o clã taanita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4525, 'nvi', 'Números', 'nm', 26, 36, 'Estes foram os descendentes de Sutela: de Erã, o clã eranita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4526, 'nvi', 'Números', 'nm', 26, 37, 'Esses foram os clãs de Efraim; foram contados 32. 500 homens. Esses foram os descendentes de José segundo os seus clãs.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4527, 'nvi', 'Números', 'nm', 26, 38, 'Os descendentes de Benjamim segundo os seus clãs foram: de Belá, o clã belaíta; de Asbel, o clã asbelita; de Airã, o clã airamita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4528, 'nvi', 'Números', 'nm', 26, 39, 'de Sufã; o clã sufamita; de Hufã, o clã hufamita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4529, 'nvi', 'Números', 'nm', 26, 40, 'Os descendentes de Belá por meio de Arde e Naamã foram: de Arde, o clã ardita; de Naamã, o clã naamanita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4530, 'nvi', 'Números', 'nm', 26, 41, 'Esses foram os clãs de Benjamim; foram contados 45. 600 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4531, 'nvi', 'Números', 'nm', 26, 42, 'Os descendentes de Dã segundo os seus clãs foram: de Suã, o clã suamita. Esses foram os clãs de Dã:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4532, 'nvi', 'Números', 'nm', 26, 43, 'Todos eles eram clãs suamitas; foram contados 64. 400 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4533, 'nvi', 'Números', 'nm', 26, 44, 'Os descendentes de Aser segundo os seus clãs foram: de Imna, o clã imnaíta; de Isvi, o clã isvita; de Berias, o clã beriaíta;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4534, 'nvi', 'Números', 'nm', 26, 45, 'e dos descendentes de Berias: de Héber, o clã heberita; de Malquiel, o clã malquielita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4535, 'nvi', 'Números', 'nm', 26, 46, 'Aser teve uma filha chamada Sera.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4536, 'nvi', 'Números', 'nm', 26, 47, 'Esses foram os clãs de Aser; foram contados 53. 400 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4537, 'nvi', 'Números', 'nm', 26, 48, 'Os descendentes de Naftali segundo os seus clãs foram: de Jazeel, o clã jazeelita; de Guni, o clã gunita;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4538, 'nvi', 'Números', 'nm', 26, 49, 'de Jezer, o clã jezerita; de Silém, o clã silemita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4539, 'nvi', 'Números', 'nm', 26, 50, 'Esses foram os clãs de Naftali; foram contados 45. 400 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4540, 'nvi', 'Números', 'nm', 26, 51, 'O número total dos homens de Israel foi 601. 730.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4541, 'nvi', 'Números', 'nm', 26, 52, 'Disse ainda o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4542, 'nvi', 'Números', 'nm', 26, 53, '"A terra será repartida entre eles como herança, de acordo com o número dos nomes alistados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4543, 'nvi', 'Números', 'nm', 26, 54, 'A um clã maior dê uma herança maior, e a um clã menor, uma herança menor; cada um receberá a sua herança de acordo com o seu número de recenseados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4544, 'nvi', 'Números', 'nm', 26, 55, 'A terra, porém, será distribuída por sorteio. Cada um herdará sua parte de acordo com o nome da tribo de seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4545, 'nvi', 'Números', 'nm', 26, 56, 'Cada herança será distribuída por sorteio entre os clãs maiores e entre os clãs menores". O Segundo Recenseamento dos Levitas');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4546, 'nvi', 'Números', 'nm', 26, 57, 'Estes foram os levitas contados segundo os seus clãs: de Gérson, o clã gersonita; de Coate, o clã coatita; de Merari, o clã merarita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4547, 'nvi', 'Números', 'nm', 26, 58, 'Estes também eram clãs levitas: o clã libnita; o clã hebronita; o clã malita; o clã musita; o clã coreíta. Coate foi o pai de Anrão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4548, 'nvi', 'Números', 'nm', 26, 59, 'o nome da mulher de Anrão era Joquebede, descendente de Levi, que nasceu no Egito. Ela lhe deu à luz Arão, Moisés e Miriã, irmã deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4549, 'nvi', 'Números', 'nm', 26, 60, 'Arão foi o pai de Nadabe, Abiú, Eleazar e Itamar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4550, 'nvi', 'Números', 'nm', 26, 61, 'Mas Nadabe e Abiú morreram quando apresentaram uma oferta com fogo profano perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4551, 'nvi', 'Números', 'nm', 26, 62, 'O total de levitas do sexo masculino de um mês de idade para cima que foram contados foi 23. 000. Não foram contados junto com os outros israelitas porque não receberam herança entre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4552, 'nvi', 'Números', 'nm', 26, 63, 'São esses os que foram recenseados por Moisés e pelo sacerdote Eleazar quando contaram os israelitas nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4553, 'nvi', 'Números', 'nm', 26, 64, 'Nenhum deles estava entre os que foram contados por Moisés e pelo sacerdote Arão quando contaram os israelitas no deserto do Sinai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4554, 'nvi', 'Números', 'nm', 26, 65, 'Pois o Senhor tinha dito àqueles israelitas que iriam morrer no deserto, e nenhum deles sobreviveu, exceto Calebe, filho de Jefoné, e Josué, filho de Num.');
