INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4555, 'nvi', 'Números', 'nm', 27, 1, 'Aproximaram-se as filhas de Zelofeade, filho de Héfer, neto de Gileade, bisneto de Maquir, trineto de Manassés; pertencia aos clãs de Manassés, filho de José. Os nomes das suas filhas eram Maalá, Noa, Hogla, Milca e Tirza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4556, 'nvi', 'Números', 'nm', 27, 2, 'Elas se prostraram à entrada da Tenda do Encontro diante de Moisés, do sacerdote Eleazar, dos líderes de toda a comunidade, e disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4557, 'nvi', 'Números', 'nm', 27, 3, '"Nosso pai morreu no deserto. Ele não estava entre os seguidores de Corá, que se ajuntaram contra o Senhor, mas morreu por causa do seu próprio pecado e não deixou filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4558, 'nvi', 'Números', 'nm', 27, 4, 'Por que o nome de nosso pai deveria desaparecer de seu clã por não ter tido um filho? Dê-nos propriedade entre os parentes de nosso pai".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4559, 'nvi', 'Números', 'nm', 27, 5, 'Moisés levou o caso perante o Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4560, 'nvi', 'Números', 'nm', 27, 6, 'e o Senhor lhe disse:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4561, 'nvi', 'Números', 'nm', 27, 7, '"As filhas de Zelofeade têm razão. Você lhes dará propriedade como herança entre os parentes do pai delas, e lhes passará a herança do pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4562, 'nvi', 'Números', 'nm', 27, 8, '"Diga aos israelitas: Se um homem morrer e não deixar filho, transfiram a sua herança para a sua filha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4563, 'nvi', 'Números', 'nm', 27, 9, 'Se ele não tiver filha, dêem a sua herança aos irmãos dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4564, 'nvi', 'Números', 'nm', 27, 10, 'Se não tiver irmãos, dêem-na aos irmãos de seu pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4565, 'nvi', 'Números', 'nm', 27, 11, 'Se ainda seu pai não tiver irmãos, dêem a herança ao parente mais próximo em seu clã". Esta será uma exigência legal para os israelitas, como o Senhor ordenou a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4566, 'nvi', 'Números', 'nm', 27, 12, 'Então o Senhor disse a Moisés: "Suba este monte da serra de Abarim e veja a terra que dei aos israelitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4567, 'nvi', 'Números', 'nm', 27, 13, 'Depois de vê-la, você também será reunido ao seu povo, como seu irmão Arão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4568, 'nvi', 'Números', 'nm', 27, 14, 'pois, quando a comunidade se rebelou nas águas do deserto de Zim, vocês dois desobedeceram à minha ordem de honrar minha santidade perante eles". Isso aconteceu nas águas de Meribá, em Cades, no deserto de Zim.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4569, 'nvi', 'Números', 'nm', 27, 15, 'Moisés disse ao Senhor:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4570, 'nvi', 'Números', 'nm', 27, 16, '"Que o Senhor, o Deus que a todos dá vida, designe um homem como líder desta comunidade');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4571, 'nvi', 'Números', 'nm', 27, 17, 'para conduzi-los em suas batalhas, para que a comunidade do Senhor não seja como ovelhas sem pastor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4572, 'nvi', 'Números', 'nm', 27, 18, 'Então o Senhor disse a Moisés: "Chame Josué, filho de Num, homem em quem está o Espírito, e imponha as mãos sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4573, 'nvi', 'Números', 'nm', 27, 19, 'Faça-o apresentar-se ao sacerdote Eleazar e a toda a comunidade e o comissione na presença deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4574, 'nvi', 'Números', 'nm', 27, 20, 'Dê-lhe parte da sua autoridade para que toda a comunidade de Israel lhe obedeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4575, 'nvi', 'Números', 'nm', 27, 21, 'Ele deverá apresentar-se ao sacerdote Eleazar, que lhe dará diretrizes ao consultar o Urim perante o Senhor. Josué e toda a comunidade dos israelitas seguirão suas instruções quando saírem para a batalha".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4576, 'nvi', 'Números', 'nm', 27, 22, 'Moisés fez como o Senhor lhe ordenou. Chamou Josué e o apresentou ao sacerdote Eleazar e a toda a comunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4577, 'nvi', 'Números', 'nm', 27, 23, 'Impôs as mãos sobre ele e o comissionou. Tudo conforme o Senhor tinha dito por meio de Moisés.');
