INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4609, 'nvi', 'Números', 'nm', 29, 1, '"No dia primeiro do sétimo mês convoquem uma santa assembléia e não façam trabalho algum. Nesse dia vocês tocarão as trombetas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4610, 'nvi', 'Números', 'nm', 29, 2, 'Como aroma agradável ao Senhor, ofereçam um holocausto de um novilho, um carneiro e sete cordeiros de um ano, todos sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4611, 'nvi', 'Números', 'nm', 29, 3, 'Com o novilho preparem uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4612, 'nvi', 'Números', 'nm', 29, 4, 'e com cada um dos sete cordeiros, um jarro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4613, 'nvi', 'Números', 'nm', 29, 5, 'Ofereçam também um bode como sacrifício pelo pecado, para fazer propiciação por vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4614, 'nvi', 'Números', 'nm', 29, 6, 'além dos holocaustos mensais e diários com as ofertas de cereal e com as ofertas derramadas, conforme prescritas. São ofertas preparadas no fogo, de aroma agradável ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4615, 'nvi', 'Números', 'nm', 29, 7, '"No dia dez desse sétimo mês convoquem uma santa assembléia. Vocês se humilharão a si mesmos e não farão trabalho algum.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4616, 'nvi', 'Números', 'nm', 29, 8, 'Apresentem como aroma agradável ao Senhor um holocausto de um novilho, de um carneiro e de sete cordeiros de um ano de idade, todos sem defeito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4617, 'nvi', 'Números', 'nm', 29, 9, 'Com o novilho preparem uma oferta de cereais de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4618, 'nvi', 'Números', 'nm', 29, 10, 'e com cada um dos sete cordeiros, um jarro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4619, 'nvi', 'Números', 'nm', 29, 11, 'Ofereçam também um bode como sacrifício pelo pecado, além do sacrifício pelo pecado para fazer propiciação e o holocausto diário com a oferta de cereal e com as ofertas derramadas.');
