INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4665, 'nvi', 'Números', 'nm', 31, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4666, 'nvi', 'Números', 'nm', 31, 2, '"Vingue-se dos midianitas pelo que fizeram aos israelitas. Depois disso você será reunido aos seus antepassados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4667, 'nvi', 'Números', 'nm', 31, 3, 'Então Moisés disse ao povo: "Armem alguns dos homens para irem à guerra contra os midianitas e executarem a vingança do Senhor contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4668, 'nvi', 'Números', 'nm', 31, 4, 'Enviem à batalha mil homens de cada tribo de Israel".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4669, 'nvi', 'Números', 'nm', 31, 5, 'Doze mil homens armados para a guerra, mil de cada tribo, foram mandados pelos clãs de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4670, 'nvi', 'Números', 'nm', 31, 6, 'Moisés os enviou à guerra, mil de cada tribo, juntamente com Finéias, filho do sacerdote Eleazar, que levou consigo objetos do santuário e as cornetas para o toque de guerra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4671, 'nvi', 'Números', 'nm', 31, 7, 'Lutaram então contra Midiã, conforme o Senhor tinha ordenado a Moisés, e mataram todos os homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4672, 'nvi', 'Números', 'nm', 31, 8, 'Entre os mortos estavam os cinco reis de Midiã: Evi, Requém, Zur, Hur e Reba. Também mataram à espada Balaão, filho de Beor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4673, 'nvi', 'Números', 'nm', 31, 9, 'Os israelitas capturaram as mulheres e as crianças midianitas e tomaram como despojo todos os rebanhos e bens dos midianitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4674, 'nvi', 'Números', 'nm', 31, 10, 'Queimaram todas as cidades em que os midianitas haviam se estabelecido, bem como todos os seus acampamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4675, 'nvi', 'Números', 'nm', 31, 11, 'Tomaram todos os despojos, incluindo pessoas e animais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4676, 'nvi', 'Números', 'nm', 31, 12, 'e levaram os prisioneiros, homens e mulheres, e os despojos a Moisés, ao sacerdote Eleazar e à comunidade de Israel em seu acampamento, nas campinas de Moabe, do outro lado de Jericó.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4677, 'nvi', 'Números', 'nm', 31, 13, 'Moisés, o sacerdote Eleazar e todos os líderes da comunidade saíram para recebê-los fora do acampamento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4678, 'nvi', 'Números', 'nm', 31, 14, 'Mas Moisés indignou-se contra os oficiais do exército que voltaram da guerra, os líderes de milhares e os líderes de centenas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4679, 'nvi', 'Números', 'nm', 31, 15, '"Vocês deixaram todas as mulheres vivas? ", perguntou-lhes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4680, 'nvi', 'Números', 'nm', 31, 16, '"Foram elas que seguiram o conselho de Balaão e levaram Israel a ser infiel ao Senhor no caso de Peor, de modo que uma praga feriu a comunidade do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4681, 'nvi', 'Números', 'nm', 31, 17, 'Agora matem todos os meninos. E matem também todas as mulheres que se deitaram com homem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4682, 'nvi', 'Números', 'nm', 31, 18, 'mas poupem todas as meninas virgens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4683, 'nvi', 'Números', 'nm', 31, 19, '"Todos vocês que mataram alguém ou que tocaram em algum morto ficarão sete dias fora do acampamento. No terceiro e no sétimo dia vocês deverão purificar-se a si mesmos e aos seus prisioneiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4684, 'nvi', 'Números', 'nm', 31, 20, 'Purifiquem toda roupa e também tudo o que é feito de couro, de pêlo de bode ou de madeira. "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4685, 'nvi', 'Números', 'nm', 31, 21, 'Depois o sacerdote Eleazar disse aos soldados que tinham ido à guerra: "Esta é a exigência da lei que o Senhor ordenou a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4686, 'nvi', 'Números', 'nm', 31, 22, 'Ouro, prata, bronze, ferro, estanho, chumbo');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4687, 'nvi', 'Números', 'nm', 31, 23, 'e tudo o que resista ao fogo, vocês terão que passar pelo fogo para purificá-las, mas também deverão purificá-las com a água da purificação. E tudo o que não resistir ao fogo terá que passar pela água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4688, 'nvi', 'Números', 'nm', 31, 24, 'No sétimo dia lavem as suas roupas, e vocês ficarão puros. Depois poderão entrar no acampamento".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4689, 'nvi', 'Números', 'nm', 31, 25, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4690, 'nvi', 'Números', 'nm', 31, 26, '"Você, o sacerdote Eleazar e os chefes das famílias da comunidade deverão contar todo o povo e os animais capturados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4691, 'nvi', 'Números', 'nm', 31, 27, 'Dividam os despojos entre os guerreiros que participaram da batalha e o restante da comunidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4692, 'nvi', 'Números', 'nm', 31, 28, 'Daquilo que os guerreiros trouxeram da guerra, separem como tributo ao Senhor um de cada quinhentos, sejam pessoas, bois, jumentos, ovelhas ou bodes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4693, 'nvi', 'Números', 'nm', 31, 29, 'Tomem esse tributo da metade que foi dada como porção a eles e entreguem-no ao sacerdote Eleazar como a porção do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4694, 'nvi', 'Números', 'nm', 31, 30, 'Da metade dada aos israelitas, escolham um de cada cinqüenta, sejam pessoas, bois, jumentos, ovelhas ou bodes. Entreguem-nos aos levitas, encarregados de cuidar do tabernáculo do Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4695, 'nvi', 'Números', 'nm', 31, 31, 'Moisés e o sacerdote Eleazar fizeram como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4696, 'nvi', 'Números', 'nm', 31, 32, 'Os despojos que restaram das presas tomadas pelos soldados foram 675. 000 ovelhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4697, 'nvi', 'Números', 'nm', 31, 33, '72. 000 cabeças de gado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4698, 'nvi', 'Números', 'nm', 31, 34, '61. 000 jumentos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4699, 'nvi', 'Números', 'nm', 31, 35, 'e 32. 000 mulheres virgens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4700, 'nvi', 'Números', 'nm', 31, 36, 'A metade dada aos que lutaram na guerra foi esta: 337. 500 ovelhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4701, 'nvi', 'Números', 'nm', 31, 37, 'das quais o tributo para o Senhor foram 675;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4702, 'nvi', 'Números', 'nm', 31, 38, '36. 000 cabeças de gado, das quais o tributo para o Senhor foram 72;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4703, 'nvi', 'Números', 'nm', 31, 39, '30. 500 jumentos, dos quais o tributo para o Senhor foram 61;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4704, 'nvi', 'Números', 'nm', 31, 40, '16. 000 pessoas, das quais o tributo para o Senhor foram 32.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4705, 'nvi', 'Números', 'nm', 31, 41, 'Moisés deu o tributo ao sacerdote Eleazar como contribuição ao Senhor, conforme o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4706, 'nvi', 'Números', 'nm', 31, 42, 'A outra metade, pertencente aos israelitas, Moisés separou da dos combatentes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4707, 'nvi', 'Números', 'nm', 31, 43, 'essa era a metade pertencente à comunidade, com 337. 500 ovelhas,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4708, 'nvi', 'Números', 'nm', 31, 44, '36. 000 cabeças de gado,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4709, 'nvi', 'Números', 'nm', 31, 45, '30. 500 jumentos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4710, 'nvi', 'Números', 'nm', 31, 46, 'e 16. 000 pessoas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4711, 'nvi', 'Números', 'nm', 31, 47, 'Da metade pertencente aos israelitas, Moisés escolheu um de cada cinqüenta, tanto de pessoas como de animais, conforme o Senhor lhe tinha ordenado, e os entregou aos levitas, encarregados de cuidar do tabernáculo do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4712, 'nvi', 'Números', 'nm', 31, 48, 'Então os oficiais que estavam sobre as unidades do exército, os líderes de milhares e os líderes de centenas foram a Moisés');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4713, 'nvi', 'Números', 'nm', 31, 49, 'e lhe disseram: "Seus servos contaram os soldados sob o nosso comando, e não está faltando ninguém.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4714, 'nvi', 'Números', 'nm', 31, 50, 'Por isso trouxemos como oferta ao Senhor os artigos de ouro dos quais cada um de nós se apossou: braceletes, pulseiras, anéis de sinete, brincos e colares; para fazer propiciação por nós perante o Senhor".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4715, 'nvi', 'Números', 'nm', 31, 51, 'Moisés e o sacerdote Eleazar receberam deles. Todas as jóias de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4716, 'nvi', 'Números', 'nm', 31, 52, 'Todo o ouro dado pelos líderes de milhares e pelos líderes de centenas que Moisés e Eleazar apresentaram como contribuição ao Senhor pesou duzentos quilos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4717, 'nvi', 'Números', 'nm', 31, 53, 'Cada soldado tinha tomado despojos para si mesmo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4718, 'nvi', 'Números', 'nm', 31, 54, 'Moisés e o sacerdote Eleazar receberam o ouro dado pelos líderes de milhares e pelos líderes de centenas e o levaram para a Tenda do Encontro como memorial para que o Senhor se lembrasse dos israelitas.');
