INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4719, 'nvi', 'Números', 'nm', 32, 1, 'As tribos de Rúben e de Gade, donas de numerosos rebanhos, viram que as terras de Jazar e de Gileade eram próprias para a criação de gado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4720, 'nvi', 'Números', 'nm', 32, 2, 'Por isso foram a Moisés, ao sacerdote Eleazar e aos líderes da comunidade, e disseram:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4721, 'nvi', 'Números', 'nm', 32, 3, '"Atarote, Dibom, Jazar, Ninra, Hesbom, Eleale, Sebã, Nebo e Beom,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4722, 'nvi', 'Números', 'nm', 32, 4, 'terras que o Senhor subjugou perante a comunidade de Israel, são próprias para a criação de gado, e os seus servos possuem gado".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4723, 'nvi', 'Números', 'nm', 32, 5, 'E acrescentaram: "Se podemos contar com o favor de vocês, deixem que essa terra seja dada a estes seus servos como nossa herança. Não nos façam atravessar o Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4724, 'nvi', 'Números', 'nm', 32, 6, 'Moisés respondeu aos homens de Gade e de Rúben: "E os seus compatriotas irão à guerra enquanto vocês ficam aqui?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4725, 'nvi', 'Números', 'nm', 32, 7, 'Por que vocês desencorajam os israelitas de entrar na terra que o Senhor lhes deu?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4726, 'nvi', 'Números', 'nm', 32, 8, 'Foi isso que os pais de vocês fizeram quando os enviei de Cades-Barnéia para verem a terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4727, 'nvi', 'Números', 'nm', 32, 9, 'Depois de subirem ao vale de Escol e examinarem a terra, desencorajaram os israelitas de entrar na terra que o Senhor lhes tinha dado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4728, 'nvi', 'Números', 'nm', 32, 10, 'A ira do Senhor se acendeu naquele dia, e ele fez este juramento:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4729, 'nvi', 'Números', 'nm', 32, 11, '‘Como não me seguiram de coração íntegro, nenhum dos homens de vinte anos para cima que saíram do Egito verá a terra que prometi sob juramento a Abraão, a Isaque e a Jacó,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4730, 'nvi', 'Números', 'nm', 32, 12, 'com exceção de Calebe, filho de Jefoné, o quenezeu, e Josué, filho de Num, seguiram ao Senhor com integridade de coração’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4731, 'nvi', 'Números', 'nm', 32, 13, 'A ira do Senhor acendeu-se contra Israel, e ele os fez andar errantes no deserto durante quarenta anos, até que passou toda a geração daqueles que lhe tinham desagradado com seu mau procedimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4732, 'nvi', 'Números', 'nm', 32, 14, '"E aí estão vocês, raça de pecadores, pondo-se no lugar dos seus antepassados e acendendo ainda mais a ira do Senhor contra Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4733, 'nvi', 'Números', 'nm', 32, 15, 'Se deixarem de segui-lo, de novo ele os abandonará no deserto, e vocês serão o motivo da destruição de todo este povo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4734, 'nvi', 'Números', 'nm', 32, 16, 'Então se aproximaram dele e disseram: "Gostaríamos de construir aqui currais para o nosso gado e cidades para as nossas mulheres e filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4735, 'nvi', 'Números', 'nm', 32, 17, 'Mas nós nos armaremos e estaremos prontos para ir à frente dos israelitas até que os tenhamos levado ao seu lugar. Enquanto isso, nossas mulheres e nossos filhos morarão em cidades fortificadas para se proteger dos habitantes da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4736, 'nvi', 'Números', 'nm', 32, 18, 'Não retornaremos aos nossos lares enquanto todos os israelitas não receberem a sua herança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4737, 'nvi', 'Números', 'nm', 32, 19, 'Não receberemos herança alguma com eles do outro lado do Jordão, uma vez que a nossa herança nos seja dada no lado leste do Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4738, 'nvi', 'Números', 'nm', 32, 20, 'Disse-lhes Moisés: "Se fizerem isso, se perante o Senhor vocês se armarem para a guerra,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4739, 'nvi', 'Números', 'nm', 32, 21, 'e se, armados, todos vocês atravessarem o Jordão perante o Senhor até que ele tenha expulsado os seus inimigos da frente dele,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4740, 'nvi', 'Números', 'nm', 32, 22, 'então, quando a terra estiver subjugada perante o Senhor, vocês poderão voltar e estarão livres da sua obrigação para com o Senhor e para com Israel. E esta terra será propriedade de vocês perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4741, 'nvi', 'Números', 'nm', 32, 23, '"Mas, se vocês não fizerem isso, estarão pecando contra o Senhor; e estejam certos de que vocês não escaparão do pecado que cometeram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4742, 'nvi', 'Números', 'nm', 32, 24, 'Construam cidades para as suas mulheres e crianças, e currais para os seus rebanhos, mas façam o que vocês prometeram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4743, 'nvi', 'Números', 'nm', 32, 25, 'Então os homens de Gade e de Rúben disseram a Moisés: "Nós, seus servos, faremos como o meu senhor ordena.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4744, 'nvi', 'Números', 'nm', 32, 26, 'Nossos filhos e mulheres, todos os nossos rebanhos ficarão aqui nas cidades de Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4745, 'nvi', 'Números', 'nm', 32, 27, 'Mas os seus servos, todos os homens armados para a batalha, atravessarão para lutar perante o Senhor, assim como o meu senhor está dizendo".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4746, 'nvi', 'Números', 'nm', 32, 28, 'Moisés deu as seguintes instruções acerca deles ao sacerdote Eleazar, a Josué, filho de Num, e aos chefes de família das tribos israelitas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4747, 'nvi', 'Números', 'nm', 32, 29, '"Se os homens de Gade e de Rúben, todos eles armados para a batalha, atravessarem o Jordão com vocês perante o Senhor, então, quando a terra for subjugada perante vocês, entreguem-lhes como propriedade a terra de Gileade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4748, 'nvi', 'Números', 'nm', 32, 30, 'Mas, se não atravessarem armados com vocês, terão que aceitar a propriedade deles com vocês em Canaã".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4749, 'nvi', 'Números', 'nm', 32, 31, 'Os homens de Gade e de Rúben responderam: "Os seus servos farão o que o Senhor disse.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4750, 'nvi', 'Números', 'nm', 32, 32, 'Atravessaremos o Jordão perante o Senhor e entraremos armados em Canaã, mas a propriedade que vamos herdar estará deste lado do Jordão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4751, 'nvi', 'Números', 'nm', 32, 33, 'Então Moisés deu às tribos de Gade e de Rúben e à metade da tribo de Manassés, filho de José, o reino de Seom, rei dos amorreus, e o reino de Ogue, rei de Basã, toda a terra com as suas cidades e o território ao redor delas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4752, 'nvi', 'Números', 'nm', 32, 34, 'A tribo de Gade construiu Dibom, Atarote, Aroer,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4753, 'nvi', 'Números', 'nm', 32, 35, 'Atarote-Sofã, Jazar, Jogbeá,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4754, 'nvi', 'Números', 'nm', 32, 36, 'Bete-Ninra e Bete-Harã como cidades fortificadas, e fez currais para os seus rebanhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4755, 'nvi', 'Números', 'nm', 32, 37, 'E a tribo de Rúben reconstruiu Hesbom, Eleale e Quiriataim,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4756, 'nvi', 'Números', 'nm', 32, 38, 'bem como Nebo e Baal-Meom ( esses nomes foram mudados ) e Sibma. E deu outros nomes a essas cidades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4757, 'nvi', 'Números', 'nm', 32, 39, 'Os descendentes de Maquir, filho de Manassés, foram a Gileade, tomaram posse dela e expulsaram os amorreus que lá estavam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4758, 'nvi', 'Números', 'nm', 32, 40, 'Então Moisés deu Gileade aos maquiritas, descendentes de Manassés, e eles passaram a habitar ali.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4759, 'nvi', 'Números', 'nm', 32, 41, 'Jair, descendente de Manassés, conquistou os povoados deles e os chamou Havote-Jair.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (4760, 'nvi', 'Números', 'nm', 32, 42, 'E Noba conquistou Quenate e os seus povoados e a chamou Noba, de acordo com o seu nome.');
