INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3744, 'nvi', 'Números', 'nm', 4, 1, 'Disse ainda o Senhor a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3745, 'nvi', 'Números', 'nm', 4, 2, '"Façam um recenseamento dos coatitas na tribo de Levi, pelos seus clãs e famílias;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3746, 'nvi', 'Números', 'nm', 4, 3, 'contem todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3747, 'nvi', 'Números', 'nm', 4, 4, '"O serviço dos coatitas na Tenda do Encontro será o cuidado das coisas santíssimas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3748, 'nvi', 'Números', 'nm', 4, 5, 'Quando o acampamento tiver que mudar, Arão e os seus filhos entrarão e descerão o véu protetor e com ele cobrirão a arca da aliança.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3749, 'nvi', 'Números', 'nm', 4, 6, 'Depois a cobrirão com couro, estenderão um pano inteiramente azul sobre ela e colocarão as varas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3750, 'nvi', 'Números', 'nm', 4, 7, '"Sobre a mesa da Presença eles estenderão um pano azul e colocarão os pratos, os recipientes para incenso, as tigelas e as bacias para as ofertas derramadas, e os pães da Presença, que devem estar sempre sobre ela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3751, 'nvi', 'Números', 'nm', 4, 8, 'Sobre tudo isso estenderão um pano vermelho e o cobrirão com couro. Depois colocarão as varas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3752, 'nvi', 'Números', 'nm', 4, 9, '"Pegarão também um pano azul e cobrirão o candelabro usado para iluminação, as suas candeias, as suas tesouras de aparo, os seus apagadores e todos os seus jarros necessários para alimentá-la de óleo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3753, 'nvi', 'Números', 'nm', 4, 10, 'Então o embrulharão com todos os seus utensílios numa cobertura de couro e o colocarão num suporte para carregar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3754, 'nvi', 'Números', 'nm', 4, 11, '"Sobre o altar de ouro estenderão um pano azul e o cobrirão com couro. E colocarão as suas varas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3755, 'nvi', 'Números', 'nm', 4, 12, '"Apanharão todos os utensílios usados na ministração no santuário, depois os embrulharão num pano azul e os cobrirão com couro; a seguir os colocarão num suporte para carregar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3756, 'nvi', 'Números', 'nm', 4, 13, '"Tirarão a cinza do altar de bronze e estenderão sobre ele um pano roxo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3757, 'nvi', 'Números', 'nm', 4, 14, 'Colocarão sobre ele todos os utensílios usados na ministração no altar: os braseiros, os garfos de carne, as pás e as bacias da aspersão. Sobre ele estenderão uma cobertura de couro e colocarão as varas no lugar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3758, 'nvi', 'Números', 'nm', 4, 15, '"Quando Arão e os seus filhos terminarem de cobrir os utensílios sagrados e todos os artigos sagrados, e o acampamento estiver pronto para partir, os coatitas virão carregá-los. Mas não tocarão nas coisas sagradas; se o fizerem, morrerão. São esses os utensílios da Tenda do Encontro que os coatitas carregarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3759, 'nvi', 'Números', 'nm', 4, 16, '"Eleazar, filho do sacerdote Arão, ficará encarregado do azeite para a iluminação, do incenso aromático, da oferta costumeira de cereal e do óleo da unção. Ficará encarregado de todo o tabernáculo e de tudo o que nele há, seus utensílios e seus artigos sagrados".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3760, 'nvi', 'Números', 'nm', 4, 17, 'O Senhor disse a Moisés e a Arão:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3761, 'nvi', 'Números', 'nm', 4, 18, '"Não permitam que o ramo dos clãs coatitas seja eliminado dentre os levitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3762, 'nvi', 'Números', 'nm', 4, 19, 'Mas, para que continuem vivos e não morram quando se aproximarem das coisas santíssimas, Arão e os seus filhos entrarão no santuário e designarão a cada homem a sua tarefa e o que deverá carregar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3763, 'nvi', 'Números', 'nm', 4, 20, 'Os coatitas não entrarão para ver as coisas sagradas, nem por um breve momento, para que não morram".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3764, 'nvi', 'Números', 'nm', 4, 21, 'E o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3765, 'nvi', 'Números', 'nm', 4, 22, '"Faça também um recenseamento dos gersonitas, pelas suas famílias e clãs;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3766, 'nvi', 'Números', 'nm', 4, 23, 'conte todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3767, 'nvi', 'Números', 'nm', 4, 24, '"Este é o serviço dos clãs gersonitas, o que devem fazer e carregar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3768, 'nvi', 'Números', 'nm', 4, 25, 'Eles levarão as cortinas internas do tabernáculo, a Tenda do Encontro, a sua cobertura, a cobertura exterior de couro, as cortinas da entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3769, 'nvi', 'Números', 'nm', 4, 26, 'Farão tudo o que for necessário com aquelas coisas e com as cortinas externas do pátio que rodeia o tabernáculo e o altar, com a cortina da entrada, com as cordas e com todos os utensílios usados em seu serviço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3770, 'nvi', 'Números', 'nm', 4, 27, 'Todo o serviço deles, tudo o que devem fazer e carregar estará sob a direção de Arão e de seus filhos. Designe como responsabilidade deles tudo o que tiverem que carregar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3771, 'nvi', 'Números', 'nm', 4, 28, 'Esse é o serviço dos clãs gersonitas na Tenda do Encontro. Suas atividades estarão sob a supervisão de Itamar, filho do sacerdote Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3772, 'nvi', 'Números', 'nm', 4, 29, '"Conte os meraritas conforme os seus clãs e famílias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3773, 'nvi', 'Números', 'nm', 4, 30, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3774, 'nvi', 'Números', 'nm', 4, 31, 'Esta é a responsabilidade deles no serviço que realizam na Tenda do Encontro: carregar as armações do tabernáculo, seus travessões, suas colunas e suas bases,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3775, 'nvi', 'Números', 'nm', 4, 32, 'bem como as colunas do pátio, que rodeia a tenda, com suas bases, suas estacas e suas cordas; todos os seus utensílios e tudo o que está relacionado com o seu uso. Designe a cada um aquilo que deverá levar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3776, 'nvi', 'Números', 'nm', 4, 33, 'Este é o serviço dos clãs meraritas. Todo o serviço deles na Tenda do Encontro estará sob a supervisão de Itamar, filho do sacerdote Arão".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3777, 'nvi', 'Números', 'nm', 4, 34, 'Moisés, Arão e os líderes da comunidade contaram os coatitas, conforme seus clãs e famílias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3778, 'nvi', 'Números', 'nm', 4, 35, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para que fizessem o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3779, 'nvi', 'Números', 'nm', 4, 36, 'Foram contados, conforme os seus clãs, 2. 750 homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3780, 'nvi', 'Números', 'nm', 4, 37, 'Esse foi o total de recenseados dos clãs coatitas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3781, 'nvi', 'Números', 'nm', 4, 38, 'Os gersonitas foram contados conforme os seus clãs e famílias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3782, 'nvi', 'Números', 'nm', 4, 39, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3783, 'nvi', 'Números', 'nm', 4, 40, 'Foram contados conforme os seus clãs e famílias 2. 630.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3784, 'nvi', 'Números', 'nm', 4, 41, 'Esse foi o total de recenseados dos clãs gersonitas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3785, 'nvi', 'Números', 'nm', 4, 42, 'Os meraritas foram contados conforme os seus clãs e famílias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3786, 'nvi', 'Números', 'nm', 4, 43, 'todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3787, 'nvi', 'Números', 'nm', 4, 44, 'Foram contados conforme os seus clãs 3. 200.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3788, 'nvi', 'Números', 'nm', 4, 45, 'Esse foi o total de recenseados dos clãs meraritas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3789, 'nvi', 'Números', 'nm', 4, 46, 'Assim Moisés, Arão e os líderes de Israel contaram todos os levitas conforme os seus clãs e famílias,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3790, 'nvi', 'Números', 'nm', 4, 47, 'todos os homens entre trinta e cinqüenta anos de idade que vieram para servir e carregar a Tenda do Encontro');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3791, 'nvi', 'Números', 'nm', 4, 48, 'somavam 8. 580.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3792, 'nvi', 'Números', 'nm', 4, 49, 'Conforme a ordem do Senhor anunciada por Moisés, a cada um foi designado o seu trabalho e foi dito o que deveria carregar. Assim foram todos contados, conforme o Senhor tinha ordenado a Moisés.');
