INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3793, 'nvi', 'Números', 'nm', 5, 1, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3794, 'nvi', 'Números', 'nm', 5, 2, '"Ordene aos israelitas que mandem para fora do acampamento todo aquele que tiver lepra, ou que tiver um fluxo, ou que se tornar impuro por tocar um cadáver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3795, 'nvi', 'Números', 'nm', 5, 3, 'Mande-os para fora do acampamento tanto homens como mulheres, para que não contaminem o seu próprio acampamento, onde habito entre eles".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3796, 'nvi', 'Números', 'nm', 5, 4, 'Os israelitas assim fizeram e os mandaram para fora do acampamento, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3797, 'nvi', 'Números', 'nm', 5, 5, 'E o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3798, 'nvi', 'Números', 'nm', 5, 6, '"Diga aos israelitas: Quando um homem ou uma mulher prejudicar outra pessoa e, portanto, ofender ao Senhor, será culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3799, 'nvi', 'Números', 'nm', 5, 7, 'Confessará o pecado que cometeu, fará restituição total, acrescentará um quinto a esse valor e entregará tudo isso a quem ele prejudicou.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3800, 'nvi', 'Números', 'nm', 5, 8, 'Mas, se o prejudicado não tiver nenhum parente próximo para receber a restituição, esta pertencerá ao Senhor e será entregue ao sacerdote, juntamente com o carneiro com o qual se faz propiciação pelo culpado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3801, 'nvi', 'Números', 'nm', 5, 9, 'Todas as contribuições, ou seja, todas as dádivas sagradas que os israelitas trouxerem ao sacerdote, pertencerão a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3802, 'nvi', 'Números', 'nm', 5, 10, 'As dádivas sagradas de cada pessoa pertencem a ela, mas o que ela der ao sacerdote pertencerá ao sacerdote".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3803, 'nvi', 'Números', 'nm', 5, 11, 'Então o Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3804, 'nvi', 'Números', 'nm', 5, 12, '"Diga o seguinte aos israelitas: Se a mulher de alguém se desviar e lhe for infiel,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3805, 'nvi', 'Números', 'nm', 5, 13, 'e outro homem deitar-se com ela, e isso estiver oculto de seu marido, e a impureza dela não for descoberta, por não haver testemunha contra ela nem tiver ela sido pega no ato;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3806, 'nvi', 'Números', 'nm', 5, 14, 'e se o marido dela tiver ciúmes e suspeitar de sua mulher, esteja ela pura ou impura,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3807, 'nvi', 'Números', 'nm', 5, 15, 'ele a levará ao sacerdote, com uma oferta de um jarro de farinha de cevada em favor dela. Não derramará azeite nem porá incenso sobre a farinha, porque é uma oferta de cereal pelo ciúme, para que se revele a verdade sobre o pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3808, 'nvi', 'Números', 'nm', 5, 16, '"O sacerdote trará a mulher e a colocará perante o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3809, 'nvi', 'Números', 'nm', 5, 17, 'Então apanhará um pouco de água sagrada num jarro de barro e colocará na água um pouco do pó do chão do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3810, 'nvi', 'Números', 'nm', 5, 18, 'Depois de colocar a mulher perante o Senhor, o sacerdote soltará o cabelo dela e porá nas mãos dela a oferta memorial, a oferta pelo ciúme, enquanto ele mesmo terá em sua mão a água amarga que traz maldição.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3811, 'nvi', 'Números', 'nm', 5, 19, 'Então o sacerdote fará a mulher jurar e lhe dirá: Se nenhum outro homem se deitou com você e se você não foi infiel nem se tornou impura enquanto casada, que esta água amarga que traz maldição não lhe faça mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3812, 'nvi', 'Números', 'nm', 5, 20, 'Mas, se você foi infiel enquanto casada e se contaminou por ter se deitado com um homem que não é seu marido —');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3813, 'nvi', 'Números', 'nm', 5, 21, 'então o sacerdote fará a mulher pronunciar este juramento com maldição — que o Senhor faça de você objeto de maldição e de desprezo no meio do povo fazendo que a sua barriga inche e que você jamais tenha filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3814, 'nvi', 'Números', 'nm', 5, 22, 'Que esta água que traz maldição entre em seu corpo, inche a sua barriga e a impeça de ter filhos. "Então a mulher dirá: ‘Amém. Assim seja’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3815, 'nvi', 'Números', 'nm', 5, 23, '"O sacerdote escreverá essas maldições num documento e depois as lavará na água amarga.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3816, 'nvi', 'Números', 'nm', 5, 24, 'Ele a fará beber a água amarga que traz maldição, e essa água entrará nela, causando-lhe amargo sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3817, 'nvi', 'Números', 'nm', 5, 25, 'O sacerdote apanhará das mãos dela a oferta de cereal pelo ciúme, a moverá ritualmente perante o Senhor e a trará ao altar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3818, 'nvi', 'Números', 'nm', 5, 26, 'Então apanhará um punhado da oferta de cereal como oferta memorial e a queimará sobre o altar; depois disso fará a mulher beber a água.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3819, 'nvi', 'Números', 'nm', 5, 27, 'Se ela houver se contaminado, sendo infiel ao seu marido, quando o sacerdote fizer que ela beba a água que traz maldição, essa água entrará nela e causará um amargo sofrimento; sua barriga inchará e ela, incapaz de ter filhos, se tornará objeto de maldição entre o seu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3820, 'nvi', 'Números', 'nm', 5, 28, 'Se, porém, a mulher não houver se contaminado, mas estiver pura, não sofrerá punição e será capaz de ter filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3821, 'nvi', 'Números', 'nm', 5, 29, '"Esse é, pois, o ritual quanto ao ciúme, quando uma mulher for infiel e se contaminar enquanto casada,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3822, 'nvi', 'Números', 'nm', 5, 30, 'ou quando o ciúme se apoderar de um homem porque suspeita de sua mulher. O sacerdote a colocará perante o Senhor e a fará passar por todo esse ritual.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3823, 'nvi', 'Números', 'nm', 5, 31, 'Se a suspeita não se confirmar, o marido estará inocente, do contrário a mulher sofrerá as conseqüências da sua iniqüidade".');
