INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3824, 'nvi', 'Números', 'nm', 6, 1, 'O Senhor disse ainda a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3825, 'nvi', 'Números', 'nm', 6, 2, '"Diga o seguinte aos israelitas: Se um homem ou uma mulher fizer um voto especial, um voto de separação para o Senhor como nazireu,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3826, 'nvi', 'Números', 'nm', 6, 3, 'terá que se abster de vinho e de outras bebidas fermentadas e não poderá beber vinagre feito de vinho ou de outra bebida fermentada. Não poderá beber suco de uva nem comer uvas nem passas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3827, 'nvi', 'Números', 'nm', 6, 4, 'Enquanto for nazireu, não poderá comer nada que venha da videira, nem mesmo as sementes ou as cascas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3828, 'nvi', 'Números', 'nm', 6, 5, '"Durante todo o período de seu voto de separação, nenhuma lâmina será usada em sua cabeça. Até que termine o período de sua separação para o Senhor ele estará consagrado e deixará crescer o cabelo de sua cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3829, 'nvi', 'Números', 'nm', 6, 6, 'Durante todo o período de sua separação para o Senhor, não poderá aproximar-se de um cadáver.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3830, 'nvi', 'Números', 'nm', 6, 7, 'Mesmo que o seu próprio pai ou mãe ou irmã ou irmão morra, ele não poderá tornar-se impuro por causa deles, pois traz sobre a cabeça o símbolo de sua separação para Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3831, 'nvi', 'Números', 'nm', 6, 8, 'Durante todo o período de sua separação, estará consagrado ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3832, 'nvi', 'Números', 'nm', 6, 9, '"Se alguém morrer repentinamente perto dele, contaminando assim o cabelo que consagrou, ele terá que rapar a cabeça sete dias depois, dia da sua purificação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3833, 'nvi', 'Números', 'nm', 6, 10, 'No oitavo dia, trará duas rolinhas ou dois pombinhos ao sacerdote à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3834, 'nvi', 'Números', 'nm', 6, 11, 'O sacerdote oferecerá um como oferta pelo pecado e o outro como holocausto, para fazer propiciação por ele, pois pecou ao se aproximar de um cadáver. Naquele mesmo dia o nazireu reconsagrará a sua cabeça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3835, 'nvi', 'Números', 'nm', 6, 12, 'Ele se dedicará ao Senhor pelo período de sua separação e trará um cordeiro de um ano de idade como oferta de reparação. Não se contarão os dias anteriores porque ficou contaminado durante a sua separação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3836, 'nvi', 'Números', 'nm', 6, 13, '"Este é o ritual do nazireu quando terminar o período de sua separação: ele será trazido à entrada da Tenda do Encontro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3837, 'nvi', 'Números', 'nm', 6, 14, 'Ali apresentará a sua oferta ao Senhor: um cordeiro de um ano e sem defeito como holocausto, uma cordeira de um ano e sem defeito como oferta pelo pecado, um carneiro sem defeito como oferta de comunhão');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3838, 'nvi', 'Números', 'nm', 6, 15, 'juntamente com a sua oferta de cereal, com a oferta derramada e com um cesto de pães sem fermento com bolos feitos da melhor farinha amassada com azeite e pães finos untados com azeite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3839, 'nvi', 'Números', 'nm', 6, 16, '"O sacerdote os apresentará ao Senhor e oferecerá o sacrifício pelo pecado e o holocausto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3840, 'nvi', 'Números', 'nm', 6, 17, 'Apresentará o cesto de pães sem fermento e oferecerá o cordeiro como sacrifício de comunhão ao Senhor, juntamente com a oferta de cereal e a oferta derramada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3841, 'nvi', 'Números', 'nm', 6, 18, '"Em seguida, à entrada da Tenda do Encontro, o nazireu rapará o cabelo que consagrou e o jogará no fogo que está embaixo do sacrifício da oferta de comunhão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3842, 'nvi', 'Números', 'nm', 6, 19, '"Depois que o nazireu rapar o cabelo da sua consagração, o sacerdote lhe colocará nas mãos um ombro cozido do carneiro, um bolo e um pão fino tirados do cesto, ambos sem fermento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3843, 'nvi', 'Números', 'nm', 6, 20, 'O sacerdote os moverá perante o Senhor como gesto ritual de apresentação; são santos e pertencem ao sacerdote, bem como o peito que foi movido e a coxa. Depois disso o nazireu poderá beber vinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3844, 'nvi', 'Números', 'nm', 6, 21, '"Esse é o ritual do voto de nazireu e da oferta dedicada ao Senhor de acordo com a sua separação, sem contar qualquer outra coisa que ele possa dedicar. Cumprirá o voto que tiver feito de acordo com o ritual do nazireu".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3845, 'nvi', 'Números', 'nm', 6, 22, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3846, 'nvi', 'Números', 'nm', 6, 23, '"Diga a Arão e aos seus filhos: Assim vocês abençoarão os israelitas:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3847, 'nvi', 'Números', 'nm', 6, 24, '"O Senhor te abençoe e te guarde;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3848, 'nvi', 'Números', 'nm', 6, 25, 'o Senhor faça resplandecer o seu rosto sobre ti e te conceda graça;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3849, 'nvi', 'Números', 'nm', 6, 26, 'o Senhor volte para ti o seu rosto e te dê paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3850, 'nvi', 'Números', 'nm', 6, 27, '"Assim eles invocarão o meu nome sobre os israelitas, e eu os abençoarei".');
