INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3851, 'nvi', 'Números', 'nm', 7, 1, 'Quando Moisés acabou de armar o tabernáculo, ele o ungiu e o consagrou, juntamente com todos os seus utensílios. Também ungiu e consagrou o altar com todos os seus utensílios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3852, 'nvi', 'Números', 'nm', 7, 2, 'Então os líderes de Israel, os chefes das famílias que eram os líderes das tribos encarregados do recenseamento, apresentaram ofertas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3853, 'nvi', 'Números', 'nm', 7, 3, 'Trouxeram as suas dádivas ao Senhor: seis carroças cobertas e doze bois, um boi de cada líder e uma carroça de cada dois líderes; e as apresentaram diante do tabernáculo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3854, 'nvi', 'Números', 'nm', 7, 4, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3855, 'nvi', 'Números', 'nm', 7, 5, '"Aceite as ofertas deles para que sejam usadas no trabalho da Tenda do Encontro. Entregue-as aos levitas, conforme exigir o trabalho de cada homem".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3856, 'nvi', 'Números', 'nm', 7, 6, 'Então Moisés recebeu as carroças e os bois e os entregou aos levitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3857, 'nvi', 'Números', 'nm', 7, 7, 'Deu duas carroças e quatro bois aos gersonitas, conforme exigia o trabalho deles,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3858, 'nvi', 'Números', 'nm', 7, 8, 'e quatro carroças e oito bois aos meraritas, conforme exigia o trabalho deles. Estavam todos sob a supervisão de Itamar, filho do sacerdote Arão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3859, 'nvi', 'Números', 'nm', 7, 9, 'Mas aos coatitas Moisés não deu nada, pois eles deveriam carregar nos ombros os objetos sagrados pelos quais eram responsáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3860, 'nvi', 'Números', 'nm', 7, 10, 'Quando o altar foi ungido, os líderes trouxeram as suas ofertas para a dedicação do altar, e as apresentaram diante dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3861, 'nvi', 'Números', 'nm', 7, 11, 'Pois o Senhor tinha dito a Moisés: "Cada dia um líder deverá trazer a sua oferta para a dedicação do altar".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3862, 'nvi', 'Números', 'nm', 7, 12, 'No primeiro dia, Naassom, filho de Aminadabe, da tribo de Judá, trouxe a sua oferta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3863, 'nvi', 'Números', 'nm', 7, 13, 'A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo como oferta de cereal;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3864, 'nvi', 'Números', 'nm', 7, 14, 'uma vasilha de ouro de cento e vinte gramas, cheia de incenso;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3865, 'nvi', 'Números', 'nm', 7, 15, 'um novilho, um carneiro e um cordeiro de um ano como holocausto;');
