INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3867, 'nvi', 'Números', 'nm', 7, 17, 'e dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para serem oferecidos como sacrifício de comunhão. Essa foi a oferta de Naassom, filho de Aminadabe.');
