INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3934, 'nvi', 'Números', 'nm', 7, 84, 'Essas foram as ofertas dos líderes israelitas para a dedicação do altar quando este foi ungido: doze pratos de prata, doze bacias de prata para as aspersões e doze vasilhas de ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3935, 'nvi', 'Números', 'nm', 7, 85, 'Cada prato de prata pesava um quilo e quinhentos e sessenta gramas, e cada bacia para as aspersões pesava oitocentos e quarenta gramas. O total de peças de prata pesava vinte e oito quilos e oitocentos gramas, com base no peso padrão do santuário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3936, 'nvi', 'Números', 'nm', 7, 86, 'As doze vasilhas de ouro cheias de incenso pesavam cada uma cento e vinte gramas, com base no peso padrão do santuário. O total de vasilhas de ouro pesava um quilo e quatrocentos e quarenta gramas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3937, 'nvi', 'Números', 'nm', 7, 87, 'O total de animais oferecidos em holocausto foi doze novilhos, doze carneiros e doze cordeiros de um ano, juntamente com as ofertas de cereal. Doze bodes foram trazidos para a oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3938, 'nvi', 'Números', 'nm', 7, 88, 'O total de animais oferecidos em sacrifício de comunhão foi vinte e quatro bois, sessenta carneiros, sessenta bodes e sessenta cordeiros de um ano. Foram essas as ofertas trazidas para a dedicação do altar depois que este foi ungido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3939, 'nvi', 'Números', 'nm', 7, 89, 'Quando entrava na Tenda do Encontro para falar com o Senhor, Moisés ouvia a voz que lhe falava do meio dos dois querubins, de cima da tampa da arca da aliança. Era assim que o Senhor falava com ele.');
