INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3940, 'nvi', 'Números', 'nm', 8, 1, 'Disse também o Senhor a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3941, 'nvi', 'Números', 'nm', 8, 2, '"Diga o seguinte a Arão: Quando você preparar as sete lâmpadas, estas deverão iluminar a área da frente do candelabro".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3942, 'nvi', 'Números', 'nm', 8, 3, 'Arão assim fez; dispôs as lâmpadas de modo que estivessem voltadas para a frente do candelabro, como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3943, 'nvi', 'Números', 'nm', 8, 4, 'O candelabro foi feito da seguinte maneira: de ouro batido do pedestal às flores, conforme o modelo que o Senhor tinha mostrado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3944, 'nvi', 'Números', 'nm', 8, 5, 'O Senhor disse a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3945, 'nvi', 'Números', 'nm', 8, 6, '"Separe os levitas do meio dos israelitas e os purifique.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3946, 'nvi', 'Números', 'nm', 8, 7, 'A purificação deles será assim: você aspergirá a água da purificação sobre eles; fará com que rapem o corpo todo e lavem as roupas, para que se purifiquem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3947, 'nvi', 'Números', 'nm', 8, 8, 'Depois eles trarão um novilho com a oferta de cereal da melhor farinha amassada com óleo; e você trará um segundo novilho como oferta pelo pecado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3948, 'nvi', 'Números', 'nm', 8, 9, 'Você levará os levitas para a frente da Tenda do Encontro e reunirá toda a comunidade de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3949, 'nvi', 'Números', 'nm', 8, 10, 'Levará os levitas à presença do Senhor, e os israelitas imporão as mãos sobre eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3950, 'nvi', 'Números', 'nm', 8, 11, 'Arão apresentará os levitas ao Senhor como oferta ritualmente movida da parte dos israelitas: eles serão dedicados ao trabalho do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3951, 'nvi', 'Números', 'nm', 8, 12, '"Depois que os levitas impuserem as mãos sobre a cabeça dos novilhos, você oferecerá um novilho como oferta pelo pecado e o outro como holocausto ao Senhor, para fazer propiciação pelos levitas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3952, 'nvi', 'Números', 'nm', 8, 13, 'Disponha os levitas em frente de Arão e dos filhos dele e apresente-os como oferta movida ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3953, 'nvi', 'Números', 'nm', 8, 14, 'Dessa maneira você separará os levitas do meio dos israelitas, e os levitas serão meus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3954, 'nvi', 'Números', 'nm', 8, 15, '"Depois que você purificar os levitas e os apresentar como oferta movida, eles entrarão na Tenda do Encontro para ministrar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3955, 'nvi', 'Números', 'nm', 8, 16, 'Eles são os israelitas que deverão ser inteiramente dedicados a mim. Eu os separei para serem meus em lugar dos primogênitos, do primeiro filho homem de cada mulher israelita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3956, 'nvi', 'Números', 'nm', 8, 17, 'Todo primogênito em Israel, entre os homens ou entre os rebanhos, é meu. Eu os separei para mim quando feri todos os primogênitos no Egito');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3957, 'nvi', 'Números', 'nm', 8, 18, 'e escolhi os levitas em lugar de todos os primogênitos em Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3958, 'nvi', 'Números', 'nm', 8, 19, 'Dentre todos os israelitas, dediquei os levitas como dádivas a Arão e aos seus filhos; eles ministrarão na Tenda do Encontro em nome dos israelitas e farão propiciação por eles, para que nenhuma praga atinja os israelitas quando se aproximarem do santuário".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3959, 'nvi', 'Números', 'nm', 8, 20, 'Moisés, Arão e toda a comunidade de Israel fizeram com os levitas como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3960, 'nvi', 'Números', 'nm', 8, 21, 'Os levitas se purificaram e lavaram suas roupas; e Arão os apresentou como oferta ritualmente movida perante o Senhor e fez propiciação por eles para purificá-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3961, 'nvi', 'Números', 'nm', 8, 22, 'Depois disso os levitas passaram a ministrar na Tenda do Encontro sob a supervisão de Arão e dos seus filhos. Fizeram com os levitas como o Senhor tinha ordenado a Moisés.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3962, 'nvi', 'Números', 'nm', 8, 23, 'O Senhor disse ainda a Moisés:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3963, 'nvi', 'Números', 'nm', 8, 24, '"Isto diz respeito aos levitas: os homens de vinte e cinco anos para cima, aptos para servir, tomarão parte no trabalho que se faz na Tenda do Encontro,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3964, 'nvi', 'Números', 'nm', 8, 25, 'mas aos cinqüenta anos deverão afastar-se do serviço regular e nele não mais trabalharão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (3965, 'nvi', 'Números', 'nm', 8, 26, 'Poderão ajudar seus companheiros de ofício na responsabilidade de cuidar da Tenda do Encontro, mas eles mesmos não deverão fazer o trabalho. Assim você designará as responsabilidades dos levitas".');
