INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22284, 'nvi', 'Oséias', 'os', 14, 1, 'Volte, ó Israel, para o Senhor, para o seu Deus. Seus pecados causaram sua queda!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22285, 'nvi', 'Oséias', 'os', 14, 2, 'Preparem o que vão dizer e voltem para o Senhor. Peçam-lhe: "Perdoa todos os nossos pecados e, por misericórdia, recebe-nos, para que te ofereçamos o fruto dos nossos lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22286, 'nvi', 'Oséias', 'os', 14, 3, 'A Assíria não nos pode salvar; não montaremos cavalos de guerra. Nunca mais diremos: ‘Nossos deuses’ àquilo que as nossas próprias mãos fizeram, porque tu amas o órfão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22287, 'nvi', 'Oséias', 'os', 14, 4, '"Eu curarei a infidelidade deles e os amarei de todo o meu coração, pois a minha ira desviou-se deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22288, 'nvi', 'Oséias', 'os', 14, 5, 'Serei como orvalho para Israel; ele florescerá como o lírio. Como o cedro do Líbano aprofundará suas raízes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22289, 'nvi', 'Oséias', 'os', 14, 6, 'seus brotos crescerão. Seu esplendor será como o da oliveira, sua fragrância como a do cedro do Líbano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22290, 'nvi', 'Oséias', 'os', 14, 7, 'Os que habitavam à sua sombra voltarão. Reviverão como o trigo. Florescerão como a videira, e a fama de Israel será como o do vinho do Líbano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22291, 'nvi', 'Oséias', 'os', 14, 8, 'O que Efraim ainda tem com ídolos? Sou eu que lhe respondo e dele cuidarei. Sou como um pinheiro verde; o fruto que você produz de mim procede".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22292, 'nvi', 'Oséias', 'os', 14, 9, 'Quem é sábio? Aquele que considerar essas coisas. Quem tem discernimento? Aquele que as compreender. Os caminhos do Senhor são justos; os justos andam neles, mas os rebeldes neles tropeçam.');
