INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22107, 'nvi', 'Oséias', 'os', 2, 1, '"Chamem a seus irmãos ‘meu povo’, e a suas irmãs ‘minhas amadas’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22108, 'nvi', 'Oséias', 'os', 2, 2, '"Repreendam sua mãe, repreendam-na, pois ela não é minha mulher, e eu não sou seu marido. Retire ela do rosto a aparência adúltera e do meio dos seios a infidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22109, 'nvi', 'Oséias', 'os', 2, 3, 'Do contrário, eu a deixarei nua, como no dia em que nasceu; eu farei dela um deserto, eu a transformarei em terra ressequida, e a matarei de sede.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22110, 'nvi', 'Oséias', 'os', 2, 4, 'Não tratarei com amor os seus filhos, porque são filhos de adultério.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22111, 'nvi', 'Oséias', 'os', 2, 5, 'A mãe deles foi infiel, engravidou deles e está coberta de vergonha. Pois ela disse: ‘Irei atrás dos meus amantes, que me dão comida, água, lã, linho, azeite e bebida’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22112, 'nvi', 'Oséias', 'os', 2, 6, 'Por isso bloquearei o caminho dela com espinheiros; eu a cercarei de tal modo que ela não poderá encontrar o seu caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22113, 'nvi', 'Oséias', 'os', 2, 7, 'Ela correrá atrás dos seus amantes, mas não os alcançará; procurará por eles, mas não os encontrará. Então ela dirá: ‘Voltarei para o meu marido como no início, pois eu estava bem melhor do que agora’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22114, 'nvi', 'Oséias', 'os', 2, 8, 'Ela não reconheceu que fui eu quem lhe deu o trigo, o vinho e o azeite, quem a cobriu de ouro e de prata, que eles usaram para Baal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22115, 'nvi', 'Oséias', 'os', 2, 9, '"Por isso levarei o meu trigo quando ele ficar maduro, e o meu vinho quando ficar pronto. Arrancarei dela minha lã e meu linho, que serviam para cobrir a sua nudez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22116, 'nvi', 'Oséias', 'os', 2, 10, 'Agora, pois, vou expor a sua lascívia diante dos olhos dos seus amantes; ninguém a livrará das minhas mãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22117, 'nvi', 'Oséias', 'os', 2, 11, 'Acabarei com a sua alegria: suas festas anuais, suas luas novas, seus dias de sábado e todas as suas festas fixas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22118, 'nvi', 'Oséias', 'os', 2, 12, 'Arruinarei suas videiras e suas figueiras, que, segundo ela, foi pagamento recebido de seus amantes; farei delas um matagal, e os animais selvagens as devorarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22119, 'nvi', 'Oséias', 'os', 2, 13, 'Eu a castigarei pelos dias em que ela queimou incenso aos baalins; ela se enfeitou com anéis e jóias, e foi atrás dos seus amantes, mas de mim, ela se esqueceu", declara o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22120, 'nvi', 'Oséias', 'os', 2, 14, '"Portanto, agora vou atraí-la; vou levá-la para o deserto e vou falar-lhe com carinho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22121, 'nvi', 'Oséias', 'os', 2, 15, 'Ali devolverei a ela as suas vinhas, e farei do vale de Açor uma porta de esperança. Ali ela me responderá como nos dias de sua infância, como no dia em que saiu do Egito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22122, 'nvi', 'Oséias', 'os', 2, 16, '"Naquele dia", declara o Senhor, "você me chamará ‘meu marido’; não me chamará mais ‘meu senhor’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22123, 'nvi', 'Oséias', 'os', 2, 17, 'Tirarei dos seus lábios os nomes dos baalins; seus nomes não serão mais invocados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22124, 'nvi', 'Oséias', 'os', 2, 18, 'Naquele dia farei em favor deles um acordo com os animais do campo, com as aves do céu e com os animais que rastejam pelo chão. Arco, espada e guerra, eu os abolirei da terra, para que todos possam viver em paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22125, 'nvi', 'Oséias', 'os', 2, 19, 'Eu me casarei com você para sempre; eu me casarei com você com justiça e retidão, com amor e compaixão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22126, 'nvi', 'Oséias', 'os', 2, 20, 'Eu me casarei com você com fidelidade, e você reconhecerá o Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22127, 'nvi', 'Oséias', 'os', 2, 21, '"Naquele dia eu responderei", declara o Senhor. "Responderei aos céus, e eles responderão à terra;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22128, 'nvi', 'Oséias', 'os', 2, 22, 'e a terra responderá ao cereal, ao vinho e ao azeite, e eles responderão a Jezreel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22129, 'nvi', 'Oséias', 'os', 2, 23, 'Eu a plantarei para mim mesmo na terra; tratarei com amor aquela que chamei Não amada. Direi àquele chamado ‘Não-meu-povo’: Você é meu povo; e ele dirá: ‘Tu és o meu Deus’. "');
