INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22135, 'nvi', 'Oséias', 'os', 4, 1, 'Israelitas, ouçam a palavra do Senhor, porque o Senhor tem uma acusação contra vocês que vivem nesta terra: "A fidelidade e o amor desapareceram desta terra, como também o conhecimento de Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22136, 'nvi', 'Oséias', 'os', 4, 2, 'Só se vêem maldição, mentira e assassinatos, roubo e mais roubo, adultério mais adultério; ultrapassam todos os limites! E o derramamento de sangue é constante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22137, 'nvi', 'Oséias', 'os', 4, 3, 'Por causa disso a terra pranteia, e todos os seus habitantes desfalecem; os animais do campo, as aves do céu e os peixes do mar estão morrendo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22138, 'nvi', 'Oséias', 'os', 4, 4, '"Mas, que ninguém discuta, que ninguém faça acusação, pois sou eu quem acusa os sacerdotes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22139, 'nvi', 'Oséias', 'os', 4, 5, 'Vocês tropeçam dia e noite, e os profetas tropeçam com vocês. Por isso destruirei sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22140, 'nvi', 'Oséias', 'os', 4, 6, 'Meu povo foi destruído por falta de conhecimento. "Uma vez que vocês rejeitaram o conhecimento, eu também os rejeito como meus sacerdotes; uma vez que vocês ignoraram a lei do seu Deus, eu também ignorarei seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22141, 'nvi', 'Oséias', 'os', 4, 7, 'Quanto mais aumentaram os sacerdotes, mais eles pecaram contra mim; trocaram a Glória deles por algo vergonhoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22142, 'nvi', 'Oséias', 'os', 4, 8, 'Eles se alimentam dos pecados do meu povo e têm prazer em sua iniqüidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22143, 'nvi', 'Oséias', 'os', 4, 9, 'Portanto, castigarei tanto o povo quanto os sacerdotes por causa dos seus caminhos, e lhes retribuirei seus atos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22144, 'nvi', 'Oséias', 'os', 4, 10, '"Eles comerão, mas não terão o suficiente; eles se prostituirão, mas não aumentarão a prole, porque abandonaram o Senhor para se entregarem');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22145, 'nvi', 'Oséias', 'os', 4, 11, 'à prostituição, ao vinho velho e ao novo, o que prejudica o discernimento do meu povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22146, 'nvi', 'Oséias', 'os', 4, 12, 'Eles pedem conselhos a um ídolo de madeira, e de um pedaço de pau recebem resposta. Um espírito de prostituição os leva a desviar-se; eles são infiéis ao seu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22147, 'nvi', 'Oséias', 'os', 4, 13, 'Sacrificam no alto dos montes e queimam incenso nas colinas, debaixo de um carvalho, de um estoraque ou de um terebinto, onde a sombra é agradável. Por isso as suas filhas se prostituem e as suas noras adulteram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22148, 'nvi', 'Oséias', 'os', 4, 14, '"Não castigarei suas filhas por se prostituírem, nem suas noras por adulterarem, porque os próprios homens se associam a meretrizes e participam dos sacrifícios oferecidos pelas prostitutas cultuais — um povo sem entendimento precipita-se à ruína!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22149, 'nvi', 'Oséias', 'os', 4, 15, '"Embora você adultere, ó Israel, que Judá não se torne culpada! "Deixem de ir a Gilgal; não subam a Bete-Áven. E não digam: ‘Juro pelo nome do Senhor! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22150, 'nvi', 'Oséias', 'os', 4, 16, 'Os israelitas são rebeldes como bezerra indomável. Como pode o Senhor apascentá-los como cordeiros na campina?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22151, 'nvi', 'Oséias', 'os', 4, 17, 'Efraim está ligado a ídolos; deixem-no só!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22152, 'nvi', 'Oséias', 'os', 4, 18, 'Mesmo quando acaba a bebida, eles continuam em sua prostituição; seus governantes amam profundamente os caminhos vergonhosos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (22153, 'nvi', 'Oséias', 'os', 4, 19, 'Um redemoinho os varrerá para longe, e os seus altares lhes trarão vergonha.');
