INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16402, 'nvi', 'Provérbios', 'pv', 1, 1, 'Estes são os provérbios de Salomão, filho de Davi, rei de Israel.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16403, 'nvi', 'Provérbios', 'pv', 1, 2, 'Eles ajudarão a experimentar a sabedoria e a disciplina; a compreender as palavras que dão entendimento;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16404, 'nvi', 'Provérbios', 'pv', 1, 3, 'a viver com disciplina e sensatez, fazendo o que é justo, direito e correto;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16405, 'nvi', 'Provérbios', 'pv', 1, 4, 'ajudarão a dar prudência aos inexperientes e conhecimento e bom senso aos jovens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16406, 'nvi', 'Provérbios', 'pv', 1, 5, 'Se o sábio der ouvidos, aumentará seu conhecimento, e quem tem discernimento obterá orientação');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16407, 'nvi', 'Provérbios', 'pv', 1, 6, 'para compreender provérbios e parábolas, ditados e enigmas dos sábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16408, 'nvi', 'Provérbios', 'pv', 1, 7, 'O temor do Senhor é o princípio do conhecimento, mas os insensatos desprezam a sabedoria e a disciplina.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16409, 'nvi', 'Provérbios', 'pv', 1, 8, 'Ouça, meu filho, a instrução de seu pai e não despreze o ensino de sua mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16410, 'nvi', 'Provérbios', 'pv', 1, 9, 'Eles serão um enfeite para a sua cabeça, um adorno para o seu pescoço.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16411, 'nvi', 'Provérbios', 'pv', 1, 10, 'Meu filho, se os maus tentarem seduzi-lo, não ceda!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16412, 'nvi', 'Provérbios', 'pv', 1, 11, 'Se disserem: "Venha conosco; fiquemos de tocaia para matar alguém, vamos divertir-nos armando emboscada contra quem de nada suspeita!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16413, 'nvi', 'Provérbios', 'pv', 1, 12, 'Vamos engoli-los vivos, como a sepultura engole os mortos; vamos destruí-los inteiros, como são destruídos os que descem à cova;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16414, 'nvi', 'Provérbios', 'pv', 1, 13, 'acharemos todo tipo de objetos valiosos e encheremos as nossas casas com o que roubarmos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16415, 'nvi', 'Provérbios', 'pv', 1, 14, 'junte-se ao nosso bando; dividiremos em partes iguais tudo o que conseguirmos! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16416, 'nvi', 'Provérbios', 'pv', 1, 15, 'Meu filho, não vá pela vereda dessa gente! Afaste os pés do caminho que eles seguem,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16417, 'nvi', 'Provérbios', 'pv', 1, 16, 'pois os pés deles correm para fazer o mal, estão sempre prontos para derramar sangue.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16418, 'nvi', 'Provérbios', 'pv', 1, 17, 'Assim como é inútil estender a rede se as aves o observam,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16419, 'nvi', 'Provérbios', 'pv', 1, 18, 'também esses homens não percebem que fazem tocaia contra a própria vida; armam emboscadas contra eles mesmos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16420, 'nvi', 'Provérbios', 'pv', 1, 19, 'Tal é o caminho de todos os gananciosos; quem assim procede se destrói.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16421, 'nvi', 'Provérbios', 'pv', 1, 20, 'A sabedoria clama em voz alta nas ruas, ergue a voz nas praças públicas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16422, 'nvi', 'Provérbios', 'pv', 1, 21, 'nas esquinas das ruas barulhentas ela clama, nas portas da cidade faz o seu discurso:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16423, 'nvi', 'Provérbios', 'pv', 1, 22, '"Até quando vocês, inexperientes, irão contentar-se com a sua inexperiência? Vocês, zombadores, até quando terão prazer na zombaria? E vocês, tolos, até quando desprezarão o conhecimento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16424, 'nvi', 'Provérbios', 'pv', 1, 23, 'Se acatarem a minha repreensão, eu lhes darei um espírito de sabedoria e lhes revelarei os meus pensamentos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16425, 'nvi', 'Provérbios', 'pv', 1, 24, 'Vocês, porém, rejeitaram o meu convite; ninguém se importou quando estendi minha mão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16426, 'nvi', 'Provérbios', 'pv', 1, 25, 'Visto que desprezaram totalmente o meu conselho e não quiseram aceitar a minha repreensão,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16427, 'nvi', 'Provérbios', 'pv', 1, 26, 'eu, de minha parte, vou rir-me da sua desgraça; zombarei quando o que temem se abater sobre vocês,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16428, 'nvi', 'Provérbios', 'pv', 1, 27, 'quando aquilo que temem abater-se sobre vocês como uma tempestade, quando a desgraça os atingir como um vendaval, quando a angústia e a dor os dominarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16429, 'nvi', 'Provérbios', 'pv', 1, 28, '"Então vocês me chamarão, mas não responderei; procurarão por mim, mas não me encontrarão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16430, 'nvi', 'Provérbios', 'pv', 1, 29, 'Visto que desprezaram o conhecimento e recusaram o temor do Senhor,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16431, 'nvi', 'Provérbios', 'pv', 1, 30, 'não quiseram aceitar o meu conselho e fizeram pouco caso da minha advertência,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16432, 'nvi', 'Provérbios', 'pv', 1, 31, 'comerão do fruto da sua conduta e se fartarão de suas próprias maquinações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16433, 'nvi', 'Provérbios', 'pv', 1, 32, 'Pois a inconstância dos inexperientes os matará, e a falsa segurança dos tolos os destruirá;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16434, 'nvi', 'Provérbios', 'pv', 1, 33, 'mas quem me ouvir viverá em segurança e estará tranqüilo, sem temer nenhum mal".');
