INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16658, 'nvi', 'Provérbios', 'pv', 10, 1, 'Provérbios de Salomão: O filho sábio dá alegria ao pai; o filho tolo dá tristeza à mãe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16659, 'nvi', 'Provérbios', 'pv', 10, 2, 'Os tesouros de origem desonesta não servem para nada, mas a retidão livra da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16660, 'nvi', 'Provérbios', 'pv', 10, 3, 'O Senhor não deixa o justo passar fome, mas frustra a ambição dos ímpios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16661, 'nvi', 'Provérbios', 'pv', 10, 4, 'As mãos preguiçosas empobrecem o homem, porém as mãos diligentes lhe trazem riqueza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16662, 'nvi', 'Provérbios', 'pv', 10, 5, 'Aquele que faz a colheita no verão é filho sensato, mas aquele que dorme durante a ceifa é filho que causa vergonha.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16663, 'nvi', 'Provérbios', 'pv', 10, 6, 'As bênçãos coroam a cabeça dos justos, mas a boca dos ímpios abriga a violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16664, 'nvi', 'Provérbios', 'pv', 10, 7, 'A memória deixada pelos justos será uma bênção, mas o nome dos ímpios apodrecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16665, 'nvi', 'Provérbios', 'pv', 10, 8, 'Os sábios de coração aceitam mandamentos, mas a boca do insensato o leva à ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16666, 'nvi', 'Provérbios', 'pv', 10, 9, 'Quem anda com integridade anda com segurança, mas quem segue veredas tortuosas será descoberto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16667, 'nvi', 'Provérbios', 'pv', 10, 10, 'Aquele que pisca maliciosamente causa tristeza, e a boca do insensato o leva à ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16668, 'nvi', 'Provérbios', 'pv', 10, 11, 'A boca do justo é fonte de vida, mas a boca dos ímpios abriga a violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16669, 'nvi', 'Provérbios', 'pv', 10, 12, 'O ódio provoca dissensão, mas o amor cobre todos os pecados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16670, 'nvi', 'Provérbios', 'pv', 10, 13, 'A sabedoria está nos lábios dos que têm discernimento, mas a vara é para as costas daquele que não tem juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16671, 'nvi', 'Provérbios', 'pv', 10, 14, 'Os sábios acumulam conhecimento, mas a boca do insensato é um convite à ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16672, 'nvi', 'Provérbios', 'pv', 10, 15, 'A riqueza dos ricos é a sua cidade fortificada, mas a pobreza é a ruína dos pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16673, 'nvi', 'Provérbios', 'pv', 10, 16, 'O salário do justo lhe traz vida, mas a renda do ímpio lhe traz castigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16674, 'nvi', 'Provérbios', 'pv', 10, 17, 'Quem acolhe a disciplina mostra o caminho da vida, mas quem ignora a repreensão desencaminha outros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16675, 'nvi', 'Provérbios', 'pv', 10, 18, 'Quem esconde o ódio tem lábios mentirosos, e quem espalha calúnia é tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16676, 'nvi', 'Provérbios', 'pv', 10, 19, 'Quando são muitas as palavras o pecado está presente, mas quem controla a língua é sensato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16677, 'nvi', 'Provérbios', 'pv', 10, 20, 'A língua dos justos é prata escolhida, mas o coração dos ímpios quase não tem valor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16678, 'nvi', 'Provérbios', 'pv', 10, 21, 'As palavras dos justos dão sustento a muitos, mas os insensatos morrem por falta de juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16679, 'nvi', 'Provérbios', 'pv', 10, 22, 'A bênção do Senhor traz riqueza, e não inclui dor alguma.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16680, 'nvi', 'Provérbios', 'pv', 10, 23, 'O tolo encontra prazer na má conduta, mas o homem cheio de entendimento deleita-se na sabedoria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16681, 'nvi', 'Provérbios', 'pv', 10, 24, 'O que o ímpio teme lhe acontecerá; o que os justos desejam lhes será concedido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16682, 'nvi', 'Provérbios', 'pv', 10, 25, 'Passada a tempestade, o ímpio já não existe, mas o justo permanece firme para sempre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16683, 'nvi', 'Provérbios', 'pv', 10, 26, 'Como o vinagre para os dentes e a fumaça para os olhos, assim é o preguiçoso para aqueles que o enviam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16684, 'nvi', 'Provérbios', 'pv', 10, 27, 'O temor do Senhor prolonga a vida, mas a vida do ímpio é abreviada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16685, 'nvi', 'Provérbios', 'pv', 10, 28, 'O que o justo almeja redunda em alegria, mas as esperanças dos ímpios dão em nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16686, 'nvi', 'Provérbios', 'pv', 10, 29, 'O caminho do Senhor é o refúgio dos íntegros, mas é a ruína dos que praticam o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16687, 'nvi', 'Provérbios', 'pv', 10, 30, 'Os justos jamais serão desarraigados, mas os ímpios pouco duram na terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16688, 'nvi', 'Provérbios', 'pv', 10, 31, 'A boca do justo produz sabedoria, mas a língua perversa será extirpada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16689, 'nvi', 'Provérbios', 'pv', 10, 32, 'Os lábios do justo sabem o que é próprio, mas a boca dos ímpios só conhece a perversidade.');
