INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16690, 'nvi', 'Provérbios', 'pv', 11, 1, 'O Senhor repudia balanças desonestas, mas os pesos exatos lhe dão prazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16691, 'nvi', 'Provérbios', 'pv', 11, 2, 'Quando vem o orgulho, chega a desgraça, mas a sabedoria está com os humildes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16692, 'nvi', 'Provérbios', 'pv', 11, 3, 'A integridade dos justos os guia, mas a falsidade dos infiéis os destrói.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16693, 'nvi', 'Provérbios', 'pv', 11, 4, 'De nada vale a riqueza no dia da ira divina, mas a retidão livra da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16694, 'nvi', 'Provérbios', 'pv', 11, 5, 'A retidão dos irrepreensíveis lhes abre um caminho reto, mas os ímpios são abatidos por sua própria impiedade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16695, 'nvi', 'Provérbios', 'pv', 11, 6, 'A justiça dos justos os livra, mas o desejo dos infiéis os aprisiona.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16696, 'nvi', 'Provérbios', 'pv', 11, 7, 'Quando morre o ímpio, sua esperança perece; tudo o que ele esperava do seu poder dá em nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16697, 'nvi', 'Provérbios', 'pv', 11, 8, 'O justo é salvo das tribulações, e estas são transferidas para o ímpio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16698, 'nvi', 'Provérbios', 'pv', 11, 9, 'Com a boca o ímpio pretende destruir o próximo, mas pelo seu conhecimento o justo se livra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16699, 'nvi', 'Provérbios', 'pv', 11, 10, 'Quando os justos prosperam, a cidade exulta; quando os ímpios perecem, há cantos de alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16700, 'nvi', 'Provérbios', 'pv', 11, 11, 'Pela bênção dos justos a cidade é exaltada, mas pela boca dos ímpios é destruída.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16701, 'nvi', 'Provérbios', 'pv', 11, 12, 'O homem que não tem juízo ridiculariza o seu próximo, mas o que tem entendimento refreia a língua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16702, 'nvi', 'Provérbios', 'pv', 11, 13, 'Quem muito fala trai a confidência, mas quem merece confiança guarda o segredo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16703, 'nvi', 'Provérbios', 'pv', 11, 14, 'Sem diretrizes a nação cai; o que a salva é ter muitos conselheiros.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16704, 'nvi', 'Provérbios', 'pv', 11, 15, 'Quem serve de fiador certamente sofrerá, mas quem se nega a fazê-lo está seguro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16705, 'nvi', 'Provérbios', 'pv', 11, 16, 'A mulher bondosa conquista o respeito, mas os homens cruéis só conquistam riquezas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16706, 'nvi', 'Provérbios', 'pv', 11, 17, 'Quem faz o bem aos outros, a si mesmo o faz; o homem cruel causa o seu próprio mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16707, 'nvi', 'Provérbios', 'pv', 11, 18, 'O ímpio recebe salários enganosos, mas quem semeia a retidão colhe segura recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16708, 'nvi', 'Provérbios', 'pv', 11, 19, 'Quem permanece na justiça viverá, mas quem sai em busca do mal corre para a morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16709, 'nvi', 'Provérbios', 'pv', 11, 20, 'O Senhor detesta os perversos de coração, mas os de conduta irrepreensível dão-lhe prazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16710, 'nvi', 'Provérbios', 'pv', 11, 21, 'Esteja certo de que os ímpios não ficarão sem castigo, mas os justos serão poupados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16711, 'nvi', 'Provérbios', 'pv', 11, 22, 'Como anel de ouro em focinho de porco, assim é a mulher bonita, mas indiscreta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16712, 'nvi', 'Provérbios', 'pv', 11, 23, 'O desejo dos justos resulta em bem; a esperança dos ímpios, em ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16713, 'nvi', 'Provérbios', 'pv', 11, 24, 'Há quem dê generosamente, e vê aumentar suas riquezas; outros retêm o que deveriam dar, e caem na pobreza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16714, 'nvi', 'Provérbios', 'pv', 11, 25, 'O generoso prosperará; quem dá alívio aos outros, alívio receberá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16715, 'nvi', 'Provérbios', 'pv', 11, 26, 'O povo amaldiçoa aquele que esconde o trigo, mas a bênção coroa aquele que logo se dispõe a vendê-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16716, 'nvi', 'Provérbios', 'pv', 11, 27, 'Quem procura o bem será respeitado; já o mal vai de encontro a quem o busca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16717, 'nvi', 'Provérbios', 'pv', 11, 28, 'Quem confia em suas riquezas certamente cairá, mas os justos florescerão como a folhagem verdejante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16718, 'nvi', 'Provérbios', 'pv', 11, 29, 'Quem causa problemas à sua família herdará somente vento; o insensato será servo do sábio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16719, 'nvi', 'Provérbios', 'pv', 11, 30, 'O fruto da retidão é árvore de vida, e aquele que conquista almas é sábio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16720, 'nvi', 'Provérbios', 'pv', 11, 31, 'Se os justos recebem a punição que merecem na terra, quanto mais o ímpio e o pecador!');
