INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16721, 'nvi', 'Provérbios', 'pv', 12, 1, 'Todo o que ama a disciplina ama o conhecimento, mas aquele que odeia a repreensão é tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16722, 'nvi', 'Provérbios', 'pv', 12, 2, 'O homem bom obtém o favor do Senhor, mas o homem que planeja maldades o Senhor condena.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16723, 'nvi', 'Provérbios', 'pv', 12, 3, 'Ninguém consegue se firmar mediante a impiedade, e não se pode desarraigar o justo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16724, 'nvi', 'Provérbios', 'pv', 12, 4, 'A mulher exemplar é a coroa do seu marido, mas a de comportamento vergonhoso é como câncer em seus ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16725, 'nvi', 'Provérbios', 'pv', 12, 5, 'Os planos dos justos são retos, mas o conselho dos ímpios é enganoso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16726, 'nvi', 'Provérbios', 'pv', 12, 6, 'As palavras dos ímpios são emboscadas mortais, mas quando os justos falam há livramento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16727, 'nvi', 'Provérbios', 'pv', 12, 7, 'Os ímpios são derrubados e desaparecem, mas a casa dos justos permanece firme.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16728, 'nvi', 'Provérbios', 'pv', 12, 8, 'O homem é louvado segundo a sua sabedoria, mas o que tem o coração perverso é desprezado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16729, 'nvi', 'Provérbios', 'pv', 12, 9, 'Melhor é não ser ninguém e, ainda assim, ter quem o sirva, do que fingir ser alguém e não ter comida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16730, 'nvi', 'Provérbios', 'pv', 12, 10, 'O justo cuida bem dos seus rebanhos, mas até os atos mais bondosos dos ímpios são cruéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16731, 'nvi', 'Provérbios', 'pv', 12, 11, 'Quem trabalha a sua terra terá fartura de alimento, mas quem vai atrás de fantasias não tem juízo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16732, 'nvi', 'Provérbios', 'pv', 12, 12, 'Os ímpios cobiçam o despojo tomado pelos maus, mas a raiz do justo floresce.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16733, 'nvi', 'Provérbios', 'pv', 12, 13, 'O mau se enreda no pecado do falar, mas o justo não cai nessas dificuldades.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16734, 'nvi', 'Provérbios', 'pv', 12, 14, 'Do fruto de sua boca o homem se beneficia, e o trabalho de suas mãos será recompensado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16735, 'nvi', 'Provérbios', 'pv', 12, 15, 'O caminho do insensato parece-lhe justo, mas o sábio ouve os conselhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16736, 'nvi', 'Provérbios', 'pv', 12, 16, 'O insensato revela de imediato o seu aborrecimento, mas o homem prudente ignora o insulto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16737, 'nvi', 'Provérbios', 'pv', 12, 17, 'A testemunha fiel dá testemunho honesto, mas a testemunha falsa conta mentiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16738, 'nvi', 'Provérbios', 'pv', 12, 18, 'Há palavras que ferem como espada, mas a língua dos sábios traz a cura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16739, 'nvi', 'Provérbios', 'pv', 12, 19, 'Os lábios que dizem a verdade permanecem para sempre, mas a língua mentirosa dura apenas um instante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16740, 'nvi', 'Provérbios', 'pv', 12, 20, 'O engano está no coração dos que maquinam o mal, mas a alegria está entre os que promovem a paz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16741, 'nvi', 'Provérbios', 'pv', 12, 21, 'Nenhum mal atingirá o justo, mas os ímpios estão cobertos de problemas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16742, 'nvi', 'Provérbios', 'pv', 12, 22, 'O Senhor odeia os lábios mentirosos, mas se deleita com os que falam a verdade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16743, 'nvi', 'Provérbios', 'pv', 12, 23, 'O homem prudente não alardeia o seu conhecimento, mas o coração dos tolos derrama insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16744, 'nvi', 'Provérbios', 'pv', 12, 24, 'As mãos diligentes governarão, mas os preguiçosos acabarão escravos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16745, 'nvi', 'Provérbios', 'pv', 12, 25, 'O coração ansioso deprime o homem, mas uma palavra bondosa o anima.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16746, 'nvi', 'Provérbios', 'pv', 12, 26, 'O homem honesto é cauteloso em suas amizades, mas o caminho dos ímpios os leva a perder-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16747, 'nvi', 'Provérbios', 'pv', 12, 27, 'O preguiçoso não aproveita a sua caça, mas o diligente dá valor a seus bens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16748, 'nvi', 'Provérbios', 'pv', 12, 28, 'No caminho da justiça está a vida; essa é a vereda que preserva da morte.');
