INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16774, 'nvi', 'Provérbios', 'pv', 14, 1, 'A mulher sábia edifica a sua casa, mas com as próprias mãos a insensata derruba a sua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16775, 'nvi', 'Provérbios', 'pv', 14, 2, 'Quem anda direito teme o Senhor, mas quem segue caminhos enganosos o despreza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16776, 'nvi', 'Provérbios', 'pv', 14, 3, 'A conversa do insensato traz a vara para as suas costas, mas os lábios dos sábios os protegem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16777, 'nvi', 'Provérbios', 'pv', 14, 4, 'Onde não há bois o celeiro fica vazio, mas da força do boi vem a grande colheita.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16778, 'nvi', 'Provérbios', 'pv', 14, 5, 'A testemunha sincera não engana, mas a falsa transborda em mentiras.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16779, 'nvi', 'Provérbios', 'pv', 14, 6, 'O zombador busca sabedoria e nada encontra, mas o conhecimento vem facilmente ao que tem discernimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16780, 'nvi', 'Provérbios', 'pv', 14, 7, 'Mantenha-se longe do tolo, pois você não achará conhecimento no que ele falar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16781, 'nvi', 'Provérbios', 'pv', 14, 8, 'A sabedoria do homem prudente é discernir o seu caminho, mas a insensatez dos tolos é enganosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16782, 'nvi', 'Provérbios', 'pv', 14, 9, 'Os insensatos zombam da idéia de reparar o pecado cometido, mas a boa vontade está entre os justos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16783, 'nvi', 'Provérbios', 'pv', 14, 10, 'Cada coração conhece a sua própria amargura, e não há quem possa partilhar sua alegria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16784, 'nvi', 'Provérbios', 'pv', 14, 11, 'A casa dos ímpios será destruída, mas a tenda dos justos florescerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16785, 'nvi', 'Provérbios', 'pv', 14, 12, 'Há caminho que parece certo ao homem, mas no final conduz à morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16786, 'nvi', 'Provérbios', 'pv', 14, 13, 'Mesmo no riso o coração pode sofrer, e a alegria pode terminar em tristeza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16787, 'nvi', 'Provérbios', 'pv', 14, 14, 'Os infiéis receberão a retribuição de sua conduta, mas o homem bom será recompensado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16788, 'nvi', 'Provérbios', 'pv', 14, 15, 'O inexperiente acredita em qualquer coisa, mas o homem prudente vê bem onde pisa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16789, 'nvi', 'Provérbios', 'pv', 14, 16, 'O sábio é cauteloso e evita o mal, mas o tolo é impetuoso e irresponsável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16790, 'nvi', 'Provérbios', 'pv', 14, 17, 'Quem é irritadiço faz tolices, e o homem cheio de astúcias é odiado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16791, 'nvi', 'Provérbios', 'pv', 14, 18, 'Os inexperientes herdam a insensatez, mas o conhecimento é a coroa dos prudentes.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16792, 'nvi', 'Provérbios', 'pv', 14, 19, 'Os maus se inclinarão diante dos homens de bem, e os ímpios, às portas da justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16793, 'nvi', 'Provérbios', 'pv', 14, 20, 'Os pobres são evitados até por seus vizinhos, mas os amigos dos ricos são muitos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16794, 'nvi', 'Provérbios', 'pv', 14, 21, 'Quem despreza o próximo comete pecado, mas como é feliz quem trata com bondade os necessitados!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16795, 'nvi', 'Provérbios', 'pv', 14, 22, 'Não é certo que se perdem os que só pensam no mal? Mas os que planejam o bem encontram amor e fidelidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16796, 'nvi', 'Provérbios', 'pv', 14, 23, 'Todo trabalho árduo traz proveito, mas o só falar leva à pobreza.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16797, 'nvi', 'Provérbios', 'pv', 14, 24, 'A riqueza dos sábios é a sua coroa, mas a insensatez dos tolos produz apenas insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16798, 'nvi', 'Provérbios', 'pv', 14, 25, 'A testemunha que fala a verdade salva vidas, mas a testemunha falsa é enganosa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16799, 'nvi', 'Provérbios', 'pv', 14, 26, 'Aquele que teme ao Senhor possui uma fortaleza segura, refúgio para os seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16800, 'nvi', 'Provérbios', 'pv', 14, 27, 'O temor do Senhor é fonte de vida, e afasta das armadilhas da morte.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16801, 'nvi', 'Provérbios', 'pv', 14, 28, 'Uma grande população é a glória do rei, mas, sem súditos, o príncipe está arruinado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16802, 'nvi', 'Provérbios', 'pv', 14, 29, 'O homem paciente dá prova de grande entendimento, mas o precipitado revela insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16803, 'nvi', 'Provérbios', 'pv', 14, 30, 'O coração em paz dá vida ao corpo, mas a inveja apodrece os ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16804, 'nvi', 'Provérbios', 'pv', 14, 31, 'Aquele que oprime o pobre com isso despreza o seu Criador, mas quem ao necessitado trata com bondade honra a Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16805, 'nvi', 'Provérbios', 'pv', 14, 32, 'Quando chega a calamidade, os ímpios são derrubados; os justos, porém, até em face da morte encontram refúgio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16806, 'nvi', 'Provérbios', 'pv', 14, 33, 'A sabedoria repousa no coração dos que têm discernimento e mesmo entre os tolos ela se deixa conhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16807, 'nvi', 'Provérbios', 'pv', 14, 34, 'A justiça engrandece a nação, mas o pecado é uma vergonha para qualquer povo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16808, 'nvi', 'Provérbios', 'pv', 14, 35, 'O servo sábio agrada o rei, mas o que procede vergonhosamente incorre em sua ira.');
