INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16875, 'nvi', 'Provérbios', 'pv', 17, 1, 'Melhor é um pedaço de pão seco com paz e tranqüilidade do que uma casa onde há banquetes, e muitas brigas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16876, 'nvi', 'Provérbios', 'pv', 17, 2, 'O servo sábio dominará sobre o filho de conduta vergonhosa, e participará da herança como um dos irmãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16877, 'nvi', 'Provérbios', 'pv', 17, 3, 'O crisol é para a prata e o forno é para o ouro, mas o Senhor prova o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16878, 'nvi', 'Provérbios', 'pv', 17, 4, 'O ímpio dá atenção aos lábios maus; o mentiroso dá ouvidos à língua destruidora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16879, 'nvi', 'Provérbios', 'pv', 17, 5, 'Quem zomba dos pobres mostra desprezo pelo Criador deles; quem se alegra com a desgraça não ficará sem castigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16880, 'nvi', 'Provérbios', 'pv', 17, 6, 'Os filhos dos filhos são uma coroa para os idosos, e os pais são o orgulho dos seus filhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16881, 'nvi', 'Provérbios', 'pv', 17, 7, 'Os lábios arrogantes não ficam bem ao insensato; muito menos os lábios mentirosos ao governante!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16882, 'nvi', 'Provérbios', 'pv', 17, 8, 'O suborno é um recurso fascinante para aquele que o oferece; aonde quer que vá, ele tem sucesso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16883, 'nvi', 'Provérbios', 'pv', 17, 9, 'Aquele que cobre uma ofensa promove amor, mas quem a lança em rosto separa bons amigos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16884, 'nvi', 'Provérbios', 'pv', 17, 10, 'A repreensão faz marca mais profunda no homem de entendimento do que cem açoites no tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16885, 'nvi', 'Provérbios', 'pv', 17, 11, 'O homem mau só pende para a rebeldia; por isso um oficial impiedoso será enviado contra ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16886, 'nvi', 'Provérbios', 'pv', 17, 12, 'Melhor é encontrar uma ursa da qual roubaram os filhotes do que um tolo em sua insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16887, 'nvi', 'Provérbios', 'pv', 17, 13, 'Quem retribui o bem com o mal, jamais deixará de ter mal no seu lar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16888, 'nvi', 'Provérbios', 'pv', 17, 14, 'Começar uma discussão é como abrir brecha num dique; por isso resolva a questão antes que surja a contenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16889, 'nvi', 'Provérbios', 'pv', 17, 15, 'Absolver o ímpio e condenar o justo, são coisas que o Senhor odeia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16890, 'nvi', 'Provérbios', 'pv', 17, 16, 'De que serve o dinheiro na mão do tolo, já que ele não quer obter sabedoria?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16891, 'nvi', 'Provérbios', 'pv', 17, 17, 'O amigo ama em todos os momentos; é um irmão na adversidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16892, 'nvi', 'Provérbios', 'pv', 17, 18, 'O homem sem juízo, com um aperto de mãos se compromete e se torna fiador do seu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16893, 'nvi', 'Provérbios', 'pv', 17, 19, 'Quem ama a discussão ama o pecado; quem constrói portas altas está procurando a sua ruína.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16894, 'nvi', 'Provérbios', 'pv', 17, 20, 'O homem de coração perverso não prospera, e o de língua enganosa cai na desgraça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16895, 'nvi', 'Provérbios', 'pv', 17, 21, 'O filho tolo só dá tristeza, e nenhuma alegria tem o pai do insensato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16896, 'nvi', 'Provérbios', 'pv', 17, 22, 'O coração bem disposto é remédio eficiente, mas o espírito oprimido resseca os ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16897, 'nvi', 'Provérbios', 'pv', 17, 23, 'O ímpio aceita às escondidas o suborno para desviar o curso da justiça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16898, 'nvi', 'Provérbios', 'pv', 17, 24, 'O homem de discernimento mantém a sabedoria em vista, mas os olhos do tolo perambulam até os confins da terra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16899, 'nvi', 'Provérbios', 'pv', 17, 25, 'O filho tolo é a tristeza do seu pai e a amargura daquela que o deu à luz.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16900, 'nvi', 'Provérbios', 'pv', 17, 26, 'Não é bom castigar o inocente, nem açoitar quem merece ser honrado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16901, 'nvi', 'Provérbios', 'pv', 17, 27, 'Quem tem conhecimento é comedido no falar, e quem tem entendimento é de espírito sereno.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16902, 'nvi', 'Provérbios', 'pv', 17, 28, 'Até o insensato passará por sábio, se ficar quieto, e, se contiver a língua, parecerá que tem discernimento.');
