INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16956, 'nvi', 'Provérbios', 'pv', 20, 1, 'O vinho é zombadore a bebida fermentada provoca brigas; não é sábio deixar-se dominar por eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16957, 'nvi', 'Provérbios', 'pv', 20, 2, 'O medo que o rei provoca é como o do rugido de um leão; quem o irrita põe em risco a própria vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16958, 'nvi', 'Provérbios', 'pv', 20, 3, 'É uma honra dar fim a contendas, mas todos insensatos envolvem-se nelas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16959, 'nvi', 'Provérbios', 'pv', 20, 4, 'O preguiçoso não ara a terra na estação própria; mas na época da colheita procura, e não acha nada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16960, 'nvi', 'Provérbios', 'pv', 20, 5, 'Os propósitos do coração do homem são águas profundas, mas quem tem discernimento os traz à tona.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16961, 'nvi', 'Provérbios', 'pv', 20, 6, 'Muitos se dizem amigos leais, mas um homem fiel, quem poderá achar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16962, 'nvi', 'Provérbios', 'pv', 20, 7, 'O homem justo leva uma vida íntegra; como são felizes os seus filhos!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16963, 'nvi', 'Provérbios', 'pv', 20, 8, 'Quando o rei se assenta no trono para julgar, com o olhar esmiúça todo o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16964, 'nvi', 'Provérbios', 'pv', 20, 9, 'Quem poderá dizer: "Purifiquei o coração; estou livre do meu pecado? "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16965, 'nvi', 'Provérbios', 'pv', 20, 10, 'Pesos adulterados e medidas falsificadas, são coisas que o Senhor detesta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16966, 'nvi', 'Provérbios', 'pv', 20, 11, 'Até a criança mostra o que é por suas ações; o seu procedimento revelará se ela é pura e justa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16967, 'nvi', 'Provérbios', 'pv', 20, 12, 'Os ouvidos que ouvem e os olhos que vêem foram feitos pelo Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16968, 'nvi', 'Provérbios', 'pv', 20, 13, 'Não ame o sono, ou você acabará ficando pobre; fique desperto, e terá alimento de sobra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16969, 'nvi', 'Provérbios', 'pv', 20, 14, '"Não vale isso! Não vale isso! ", diz o comprador, mas, quando se vai, gaba-se do bom negócio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16970, 'nvi', 'Provérbios', 'pv', 20, 15, 'Mesmo onde há ouro e rubis em grande quantidade, os lábios que transmitem conhecimento são uma rara preciosidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16971, 'nvi', 'Provérbios', 'pv', 20, 16, 'Tome-se a veste de quem serve de fiador ao estranho; sirva ela de penhor de quem dá garantia a uma mulher leviana.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16972, 'nvi', 'Provérbios', 'pv', 20, 17, 'Saborosa é a comida que se obtém com mentiras, mas depois dá areia na boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16973, 'nvi', 'Provérbios', 'pv', 20, 18, 'Os conselhos são importantes para quem quiser fazer planos, e quem sai à guerra precisa de orientação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16974, 'nvi', 'Provérbios', 'pv', 20, 19, 'Quem vive contando casos não guarda segredo; por isso, evite quem fala demais.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16975, 'nvi', 'Provérbios', 'pv', 20, 20, 'Se alguém amaldiçoar seu pai ou sua mãe, a luz de sua vida se extinguirá na mais profunda escuridão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16976, 'nvi', 'Provérbios', 'pv', 20, 21, 'A herança que se obtém com ganância no princípio, no final não será abençoada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16977, 'nvi', 'Provérbios', 'pv', 20, 22, 'Não diga: "Eu o farei pagar pelo mal que me fez! " Espere pelo Senhor, e ele dará a vitória a você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16978, 'nvi', 'Provérbios', 'pv', 20, 23, 'O Senhor detesta pesos adulterados, e balanças falsificadas não o agradam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16979, 'nvi', 'Provérbios', 'pv', 20, 24, 'Os passos do homem são dirigidos pelo Senhor. Como poderia alguém discernir o seu próprio caminho?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16980, 'nvi', 'Provérbios', 'pv', 20, 25, 'É uma armadilha consagrar algo precipitadamente, e só pensar nas conseqüências depois que se fez o voto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16981, 'nvi', 'Provérbios', 'pv', 20, 26, 'O rei sábio abana os ímpios, e passa sobre eles a roda de debulhar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16982, 'nvi', 'Provérbios', 'pv', 20, 27, 'O espírito do homem é a lâmpada do Senhor, e vasculha cada parte do seu ser.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16983, 'nvi', 'Provérbios', 'pv', 20, 28, 'A bondade e a fidelidade preservam o rei; por sua bondade ele dá firmeza ao seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16984, 'nvi', 'Provérbios', 'pv', 20, 29, 'A beleza dos jovens está na sua força; a glória dos idosos, nos seus cabelos brancos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16985, 'nvi', 'Provérbios', 'pv', 20, 30, 'Os golpes e os ferimentos eliminam o mal; os açoites limpam as profundezas do ser.');
