INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16986, 'nvi', 'Provérbios', 'pv', 21, 1, 'O coração do rei é como um rio controlado pelo Senhor; ele o dirige para onde quer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16987, 'nvi', 'Provérbios', 'pv', 21, 2, 'Todos os caminhos do homem lhe parecem justos, mas o Senhor pesa o coração.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16988, 'nvi', 'Provérbios', 'pv', 21, 3, 'Fazer o que é justo e certo é mais aceitável ao Senhor do que oferecer sacrifícios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16989, 'nvi', 'Provérbios', 'pv', 21, 4, 'A vida de pecado dos ímpios se vê no olhar orgulhoso e no coração arrogante,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16990, 'nvi', 'Provérbios', 'pv', 21, 5, 'Os planos bem elaborados levam à fartura; mas o apressado sempre acaba na miséria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16991, 'nvi', 'Provérbios', 'pv', 21, 6, 'A fortuna obtida com língua mentirosa é ilusão fugidia e armadilha mortal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16992, 'nvi', 'Provérbios', 'pv', 21, 7, 'A violência dos ímpios os arrastará, pois recusam-se a agir corretamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16993, 'nvi', 'Provérbios', 'pv', 21, 8, 'O caminho do culpado é tortuoso, mas a conduta do inocente é reta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16994, 'nvi', 'Provérbios', 'pv', 21, 9, 'Melhor é viver num canto sob o telhado do que repartir a casa com uma mulher briguenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16995, 'nvi', 'Provérbios', 'pv', 21, 10, 'O desejo do perverso é fazer o mal; ele não tem dó do próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16996, 'nvi', 'Provérbios', 'pv', 21, 11, 'Quando o zombador é castigado, o inexperiente obtém sabedoria; quando o sábio recebe instrução, obtém conhecimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16997, 'nvi', 'Provérbios', 'pv', 21, 12, 'O justo observa a casa dos ímpios e os faz cair na desgraça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16998, 'nvi', 'Provérbios', 'pv', 21, 13, 'Quem fecha os ouvidos ao clamor dos pobres também clamará e não terá resposta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (16999, 'nvi', 'Provérbios', 'pv', 21, 14, 'O presente que se faz em segredo acalma a ira, e o suborno oferecido às ocultas apazigua a maior fúria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17000, 'nvi', 'Provérbios', 'pv', 21, 15, 'Quando se faz justiça, o justo se alegra, mas os malfeitores se apavoram.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17001, 'nvi', 'Provérbios', 'pv', 21, 16, 'Quem se afasta do caminho da sensatez repousará na companhia dos mortos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17002, 'nvi', 'Provérbios', 'pv', 21, 17, 'Quem se entrega aos prazeres passará necessidade; quem se apega ao vinho e ao azeite jamais será rico.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17003, 'nvi', 'Provérbios', 'pv', 21, 18, 'O ímpio serve de resgate para o justo, e o infiel, para o homem íntegro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17004, 'nvi', 'Provérbios', 'pv', 21, 19, 'Melhor é viver no deserto do que com uma mulher briguenta e amargurada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17005, 'nvi', 'Provérbios', 'pv', 21, 20, 'Na casa do sábio há comida e azeite armazenados, mas o tolo devora tudo o que pode.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17006, 'nvi', 'Provérbios', 'pv', 21, 21, 'Quem segue a justiça e a lealdade encontra vida, justiça e honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17007, 'nvi', 'Provérbios', 'pv', 21, 22, 'O sábio conquista a cidade dos valentes e derruba a fortaleza em que eles confiam.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17008, 'nvi', 'Provérbios', 'pv', 21, 23, 'Quem é cuidadoso no que fala evita muito sofrimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17009, 'nvi', 'Provérbios', 'pv', 21, 24, 'O vaidoso e arrogante, chama-se zombador; ele age com extremo orgulho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17010, 'nvi', 'Provérbios', 'pv', 21, 25, 'O preguiçoso morre de tanto desejar e de nunca pôr as mãos no trabalho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17011, 'nvi', 'Provérbios', 'pv', 21, 26, 'O dia inteiro ele deseja mais e mais, enquanto o justo reparte sem cessar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17012, 'nvi', 'Provérbios', 'pv', 21, 27, 'O sacrifício dos ímpios já por si é detestável; quanto mais quando oferecido com más intenções.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17013, 'nvi', 'Provérbios', 'pv', 21, 28, 'A testemunha falsa perecerá, mas o testemunho do homem bem informado permanecerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17014, 'nvi', 'Provérbios', 'pv', 21, 29, 'O ímpio mostra no rosto a sua arrogância, mas o justo mantém em ordem o seu caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17015, 'nvi', 'Provérbios', 'pv', 21, 30, 'Não há sabedoria alguma, nem discernimento algum, nem plano algum que possa opor-se ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17016, 'nvi', 'Provérbios', 'pv', 21, 31, 'Prepara-se o cavalo para o dia da batalha, mas o Senhor é que dá a vitória.');
