INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17017, 'nvi', 'Provérbios', 'pv', 22, 1, 'A boa reputação vale mais que grandes riquezas; desfrutar de boa estima vale mais que prata e ouro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17018, 'nvi', 'Provérbios', 'pv', 22, 2, 'O rico e o pobre têm isto em comum: O Senhor é o Criador de ambos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17019, 'nvi', 'Provérbios', 'pv', 22, 3, 'O prudente percebe o perigo e busca refúgio; o inexperiente segue adiante e sofre as conseqüências.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17020, 'nvi', 'Provérbios', 'pv', 22, 4, 'A recompensa da humildade e do temor do Senhor são a riqueza, a honra e a vida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17021, 'nvi', 'Provérbios', 'pv', 22, 5, 'No caminho do perverso há espinhos e armadilhas; quem quer proteger a própria vida mantém-se longe dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17022, 'nvi', 'Provérbios', 'pv', 22, 6, 'Instrua a criança segundo os objetivos que você tem para ela, e mesmo com o passar dos anos não se desviará deles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17023, 'nvi', 'Provérbios', 'pv', 22, 7, 'O rico domina sobre o pobre; quem toma emprestado é escravo de quem empresta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17024, 'nvi', 'Provérbios', 'pv', 22, 8, 'Quem semeia a injustiça colhe a maldade; o castigo da sua arrogância será completo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17025, 'nvi', 'Provérbios', 'pv', 22, 9, 'Quem é generoso será abençoado, pois reparte o seu pão com o pobre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17026, 'nvi', 'Provérbios', 'pv', 22, 10, 'Quando se manda embora o zombador, a briga acaba; cessam as contendas e os insultos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17027, 'nvi', 'Provérbios', 'pv', 22, 11, 'Quem ama a sinceridade de coração e se expressa com elegância será amigo do rei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17028, 'nvi', 'Provérbios', 'pv', 22, 12, 'Os olhos do Senhor protegem o conhecimento, mas ele frustra as palavras dos infiéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17029, 'nvi', 'Provérbios', 'pv', 22, 13, 'O preguiçoso diz: "Há um leão lá fora! " "Serei morto na rua! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17030, 'nvi', 'Provérbios', 'pv', 22, 14, 'A conversa da mulher imoral é uma cova profunda; nela cairá quem estiver sob a ira do Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17031, 'nvi', 'Provérbios', 'pv', 22, 15, 'A insensatez está ligada ao coração da criança, mas a vara da disciplina a livrará dela.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17032, 'nvi', 'Provérbios', 'pv', 22, 16, 'Tanto quem oprime o pobre para enriquecer-se como quem faz cortesia ao rico, com certeza passarão necessidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17033, 'nvi', 'Provérbios', 'pv', 22, 17, 'Preste atenção e ouça os ditados dos sábios; aplique o coração ao meu ensino.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17034, 'nvi', 'Provérbios', 'pv', 22, 18, 'Será uma satisfação guardá-los no íntimo e tê-los todos na ponta da língua.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17035, 'nvi', 'Provérbios', 'pv', 22, 19, 'Para que você confie no Senhor, a você hoje ensinarei.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17036, 'nvi', 'Provérbios', 'pv', 22, 20, 'Já não lhe escrevi conselhos e instruções,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17037, 'nvi', 'Provérbios', 'pv', 22, 21, 'ensinando-lhe palavras dignas de confiança, para que você responda com a verdade a quem o enviou?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17038, 'nvi', 'Provérbios', 'pv', 22, 22, 'Não explore os pobres por serem pobres, nem oprima os necessitados no tribunal,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17039, 'nvi', 'Provérbios', 'pv', 22, 23, 'pois o Senhor será o advogado deles, e despojará da vida os que os despojarem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17040, 'nvi', 'Provérbios', 'pv', 22, 24, 'Não se associe com quem vive de mau humor, nem ande em companhia de quem facilmente se ira;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17041, 'nvi', 'Provérbios', 'pv', 22, 25, 'do contrário você acabará imitando essa conduta e cairá em armadilha mortal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17042, 'nvi', 'Provérbios', 'pv', 22, 26, 'Não seja como aqueles que, com um aperto de mãos, empenham-se com outros e se tornam fiadores de dívidas;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17043, 'nvi', 'Provérbios', 'pv', 22, 27, 'se você não tem como pagá-las, por que correr o risco de perder até a cama em que dorme?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17044, 'nvi', 'Provérbios', 'pv', 22, 28, 'Não mude de lugar os antigos marcos que limitam as propriedades e que foram colocados por seus antepassados.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17045, 'nvi', 'Provérbios', 'pv', 22, 29, 'Você já observou um homem habilidoso em seu trabalho? Será promovido ao serviço real; não trabalhará para gente obscura.');
