INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17046, 'nvi', 'Provérbios', 'pv', 23, 1, 'Quando você se assentar para uma refeição com alguma autoridade, observe com atenção quem está diante de você,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17047, 'nvi', 'Provérbios', 'pv', 23, 2, 'e encoste a faca à sua própria garganta, se estiver com grande apetite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17048, 'nvi', 'Provérbios', 'pv', 23, 3, 'Não deseje as iguarias que lhe oferece, pois podem ser enganosas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17049, 'nvi', 'Provérbios', 'pv', 23, 4, 'Não esgote suas forças tentando ficar rico; tenha bom senso!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17050, 'nvi', 'Provérbios', 'pv', 23, 5, 'As riquezas desaparecem assim que você as contempla; elas criam asas e voam como águias pelo céu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17051, 'nvi', 'Provérbios', 'pv', 23, 6, 'Não aceite a refeição de um hospedeiro invejoso, nem deseje as iguarias que lhe oferece;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17052, 'nvi', 'Provérbios', 'pv', 23, 7, 'pois ele só pensa nos gastos. Ele lhe diz: "Coma e beba! ", mas não fala com sinceridade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17053, 'nvi', 'Provérbios', 'pv', 23, 8, 'Você vomitará o pouco que comeu, e desperdiçará a sua cordialidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17054, 'nvi', 'Provérbios', 'pv', 23, 9, 'Não vale a pena conversar com o tolo, pois ele despreza a sabedoria do que você fala.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17055, 'nvi', 'Provérbios', 'pv', 23, 10, 'Não mude de lugar os antigos marcos de propriedade, nem invada as terras dos órfãos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17056, 'nvi', 'Provérbios', 'pv', 23, 11, 'pois Aquele que defende os direitos deles é forte. Ele lutará contra você para defendê-los.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17057, 'nvi', 'Provérbios', 'pv', 23, 12, 'Dedique à disciplina o seu coração, e os seus ouvidos às palavras que dão conhecimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17058, 'nvi', 'Provérbios', 'pv', 23, 13, 'Não evite disciplinar a criança; se você a castigar com a vara, ela não morrerá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17059, 'nvi', 'Provérbios', 'pv', 23, 14, 'Castigue-a, você mesmo, com a vara, e assim a livrará da sepultura.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17060, 'nvi', 'Provérbios', 'pv', 23, 15, 'Meu filho, se o seu coração for sábio, o meu coração se alegrará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17061, 'nvi', 'Provérbios', 'pv', 23, 16, 'Sentirei grande alegria quando os seus lábios falarem com retidão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17062, 'nvi', 'Provérbios', 'pv', 23, 17, 'Não inveje os pecadores em seu coração; melhor será que tema sempre ao Senhor.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17063, 'nvi', 'Provérbios', 'pv', 23, 18, 'Se agir assim, certamente haverá bom futuro para você, e a sua esperança não falhará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17064, 'nvi', 'Provérbios', 'pv', 23, 19, 'Ouça, meu filho, e seja sábio; guie o seu coração pelo bom caminho.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17065, 'nvi', 'Provérbios', 'pv', 23, 20, 'Não ande com os que se encharcam de vinho, nem com os que se empanturram de carne.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17066, 'nvi', 'Provérbios', 'pv', 23, 21, 'Pois os bêbados e os glutões se empobrecerão, e a sonolência os vestirá de trapos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17067, 'nvi', 'Provérbios', 'pv', 23, 22, 'Ouça o seu pai, que o gerou; não despreze sua mãe quando ela envelhecer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17068, 'nvi', 'Provérbios', 'pv', 23, 23, 'Compre a verdade e não abra mão dela, nem tampouco da sabedoria, da disciplina e do discernimento.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17069, 'nvi', 'Provérbios', 'pv', 23, 24, 'O pai do justo exultará de júbilo; quem tem filho sábio nele se alegra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17070, 'nvi', 'Provérbios', 'pv', 23, 25, 'Bom será que se alegrem seu pai e sua mãe e que exulte a mulher que o deu à luz!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17071, 'nvi', 'Provérbios', 'pv', 23, 26, 'Meu filho, dê-me o seu coração; mantenha os seus olhos em meus caminhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17072, 'nvi', 'Provérbios', 'pv', 23, 27, 'pois a prostituta é uma cova profunda, e a mulher pervertida é um poço estreito.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17073, 'nvi', 'Provérbios', 'pv', 23, 28, 'Como o assaltante, ela fica de tocaia, e multiplica entre os homens os infiéis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17074, 'nvi', 'Provérbios', 'pv', 23, 29, 'De quem são os ais? De quem as tristezas? E as brigas, de quem são? E os ferimentos desnecessários? De quem são os olhos vermelhos?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17075, 'nvi', 'Provérbios', 'pv', 23, 30, 'Dos que se demoram bebendo vinho, dos que andam à procura de bebida misturada.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17076, 'nvi', 'Provérbios', 'pv', 23, 31, 'Não se deixe atrair pelo vinho quando está vermelho, quando cintila no copo e escorre suavemente!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17077, 'nvi', 'Provérbios', 'pv', 23, 32, 'No fim, ele morde como serpente e envenena como víbora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17078, 'nvi', 'Provérbios', 'pv', 23, 33, 'Seus olhos verão coisas estranhas, e sua mente imaginará coisas distorcidas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17079, 'nvi', 'Provérbios', 'pv', 23, 34, 'Você será como quem dorme no meio do mar, como quem se deita no alto das cordas do mastro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17080, 'nvi', 'Provérbios', 'pv', 23, 35, 'E dirá: "Espancaram-me, mas eu nada senti! Bateram em mim, mas nem percebi! Quando acordarei para que possa beber mais uma vez? "');
