INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17081, 'nvi', 'Provérbios', 'pv', 24, 1, 'Não tenha inveja dos ímpios, nem deseje a companhia deles;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17082, 'nvi', 'Provérbios', 'pv', 24, 2, 'pois destruição é o que planejam no coração, e só falam de violência.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17083, 'nvi', 'Provérbios', 'pv', 24, 3, 'Com sabedoria se constrói a casa, e com discernimento se consolida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17084, 'nvi', 'Provérbios', 'pv', 24, 4, 'Pelo conhecimento os seus cômodos se enchem do que é precioso e agradável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17085, 'nvi', 'Provérbios', 'pv', 24, 5, 'O homem sábio é poderoso, e quem tem conhecimento aumenta a sua força;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17086, 'nvi', 'Provérbios', 'pv', 24, 6, 'quem sai à guerra precisa de orientação, e com muitos conselheiros se obtém a vitória.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17087, 'nvi', 'Provérbios', 'pv', 24, 7, 'A sabedoria é elevada demais para o insensato; ele não sabe o que dizer nas assembléias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17088, 'nvi', 'Provérbios', 'pv', 24, 8, 'Quem maquina o mal será conhecido como criador de intrigas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17089, 'nvi', 'Provérbios', 'pv', 24, 9, 'A intriga do insensato é pecado, e o zombador é detestado pelos homens.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17090, 'nvi', 'Provérbios', 'pv', 24, 10, 'Se você vacila no dia da dificuldade, como será limitada a sua força!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17091, 'nvi', 'Provérbios', 'pv', 24, 11, 'Liberte os que estão sendo levados para a morte; socorra os que caminham trêmulos para a matança!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17092, 'nvi', 'Provérbios', 'pv', 24, 12, 'Mesmo que você diga: "Não sabíamos o que estava acontecendo! " Não o perceberia aquele que pesa os corações? Não o saberia aquele que preserva a sua vida? Não retribuirá ele a cada um segundo o seu procedimento?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17093, 'nvi', 'Provérbios', 'pv', 24, 13, 'Coma mel, meu filho. É bom. O favo é doce ao paladar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17094, 'nvi', 'Provérbios', 'pv', 24, 14, 'Saiba que a sabedoria também será boa para a sua alma; se você a encontrar, certamente haverá futuro para você, e a sua esperança não vai decepcioná-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17095, 'nvi', 'Provérbios', 'pv', 24, 15, 'Não fique de tocaia, como faz o ímpio, contra a casa do justo, e não destrua o seu local de repouso;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17096, 'nvi', 'Provérbios', 'pv', 24, 16, 'pois ainda que o justo caia sete vezes, tornará a erguer-se, mas os ímpios são arrastados pela calamidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17097, 'nvi', 'Provérbios', 'pv', 24, 17, 'Não se alegre quando o seu inimigo cair, nem exulte o seu coração quando ele tropeçar,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17098, 'nvi', 'Provérbios', 'pv', 24, 18, 'para que o Senhor não veja isso, e se desagrade, e desvie dele a sua ira.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17099, 'nvi', 'Provérbios', 'pv', 24, 19, 'Não se aborreça por causa dos maus, nem tenha inveja dos ímpios,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17100, 'nvi', 'Provérbios', 'pv', 24, 20, 'pois não há futuro para o mau, e a lâmpada dos ímpios se apagará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17101, 'nvi', 'Provérbios', 'pv', 24, 21, 'Tema ao Senhor e ao rei, meu filho, e não se associe aos dissidentes,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17102, 'nvi', 'Provérbios', 'pv', 24, 22, 'pois terão repentina destruição, e quem pode imaginar a ruína que o Senhor e o rei podem causar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17103, 'nvi', 'Provérbios', 'pv', 24, 23, 'Aqui vão outros ditados dos sábios: Agir com parcialidade nos julgamentos não é nada bom.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17104, 'nvi', 'Provérbios', 'pv', 24, 24, 'Quem disser ao ímpio: "Você é justo", será amaldiçoado pelos povos e sofrerá a indignação das nações.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17105, 'nvi', 'Provérbios', 'pv', 24, 25, 'Mas os que condenam o culpado terão vida agradável; receberão grandes bênçãos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17106, 'nvi', 'Provérbios', 'pv', 24, 26, 'A resposta sincera é como beijo nos lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17107, 'nvi', 'Provérbios', 'pv', 24, 27, 'Termine primeiro o seu trabalho a céu aberto; deixe pronta a sua lavoura. Depois constitua família.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17108, 'nvi', 'Provérbios', 'pv', 24, 28, 'Não testemunhe sem motivo contra o seu próximo nem use os seus lábios para enganá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17109, 'nvi', 'Provérbios', 'pv', 24, 29, 'Não diga: "Farei com ele o que fez comigo; ele pagará pelo que fez".');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17110, 'nvi', 'Provérbios', 'pv', 24, 30, 'Passei pelo campo do preguiçoso, pela vinha do homem sem juízo;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17111, 'nvi', 'Provérbios', 'pv', 24, 31, 'havia espinheiros por toda parte, o chão estava coberto de ervas daninhas e o muro de pedra estava em ruínas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17112, 'nvi', 'Provérbios', 'pv', 24, 32, 'Observei aquilo, e fiquei pensando, olhei e aprendi esta lição:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17113, 'nvi', 'Provérbios', 'pv', 24, 33, '"Vou dormir um pouco", você diz. "Vou cochilar um momento; vou cruzar os braços e descansar mais um pouco",');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17114, 'nvi', 'Provérbios', 'pv', 24, 34, 'mas a pobreza lhe virá como um assaltante, e a sua miséria como um homem armado.');
