INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17115, 'nvi', 'Provérbios', 'pv', 25, 1, 'Estes são outros provérbios de Salomão, compilados pelos servos de Ezequias, rei de Judá:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17116, 'nvi', 'Provérbios', 'pv', 25, 2, 'A glória de Deus é ocultar certas coisas; tentar descobri-las é a glória dos reis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17117, 'nvi', 'Provérbios', 'pv', 25, 3, 'Assim como o céu é elevado e a terra é profunda, também o coração dos reis é insondável.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17118, 'nvi', 'Provérbios', 'pv', 25, 4, 'Quando se retira a escória da prata, nesta se tem material para o ourives;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17119, 'nvi', 'Provérbios', 'pv', 25, 5, 'quando os ímpios são retirados da presença do rei, a justiça firma o seu trono.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17120, 'nvi', 'Provérbios', 'pv', 25, 6, 'Não se engrandeça na presença do rei, e não reivindique lugar entre os homens importantes;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17121, 'nvi', 'Provérbios', 'pv', 25, 7, 'é melhor que ele lhe diga: "Suba para cá! ", do que ter que humilhá-lo diante de uma autoridade. O que você viu com os olhos');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17122, 'nvi', 'Provérbios', 'pv', 25, 8, 'não leve precipitadamente ao tribunal, pois o que você fará, se o seu próximo o desacreditar?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17123, 'nvi', 'Provérbios', 'pv', 25, 9, 'Procure resolver sua causa diretamente com o seu próximo, e não revele o segredo de outra pessoa,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17124, 'nvi', 'Provérbios', 'pv', 25, 10, 'caso contrário, quem o ouvir poderá recriminá-lo e você jamais perderá sua má reputação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17125, 'nvi', 'Provérbios', 'pv', 25, 11, 'A palavra proferida no tempo certo é como frutas de ouro incrustadas numa escultura de prata.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17126, 'nvi', 'Provérbios', 'pv', 25, 12, 'Como brinco de ouro e enfeite de ouro fino é a repreensão dada com sabedoria a quem se dispõe a ouvir.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17127, 'nvi', 'Provérbios', 'pv', 25, 13, 'Como o frescor da neve na época da colheita é o mensageiro de confiança para aqueles que o enviam; ele revigora o ânimo de seus senhores.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17128, 'nvi', 'Provérbios', 'pv', 25, 14, 'Como nuvens e ventos sem chuva é aquele que se gaba de presentes que não deu.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17129, 'nvi', 'Provérbios', 'pv', 25, 15, 'Com muita paciência pode-se convencer a autoridade, e a língua branda quebra até ossos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17130, 'nvi', 'Provérbios', 'pv', 25, 16, 'Se você encontrar mel, coma apenas o suficiente, para que não fique enjoado e vomite.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17131, 'nvi', 'Provérbios', 'pv', 25, 17, 'Não faça visitas freqüentes à casa do seu vizinho para que ele não se canse de você e passe a odiá-lo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17132, 'nvi', 'Provérbios', 'pv', 25, 18, 'Como um pedaço de pau, uma espada ou uma flecha aguda é o que dá falso testemunho contra o seu próximo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17133, 'nvi', 'Provérbios', 'pv', 25, 19, 'Como dente estragado ou pé deslocado é a confiança no hipócrita na hora da dificuldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17134, 'nvi', 'Provérbios', 'pv', 25, 20, 'Como tirar a própria roupa num dia de frio, ou derramar vinagre numa ferida, é cantar com o coração entristecido.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17135, 'nvi', 'Provérbios', 'pv', 25, 21, 'Se o seu inimigo tiver fome, dê-lhe de comer; se tiver sede, dê-lhe de beber.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17136, 'nvi', 'Provérbios', 'pv', 25, 22, 'Fazendo isso, você amontoará brasas vivas sobre a cabeça dele, e o Senhor recompensará você.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17137, 'nvi', 'Provérbios', 'pv', 25, 23, 'Como o vento norte traz chuva, assim a língua fingida traz o olhar irado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17138, 'nvi', 'Provérbios', 'pv', 25, 24, 'Melhor é viver num canto sob o telhado do que repartir a casa com uma mulher briguenta.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17139, 'nvi', 'Provérbios', 'pv', 25, 25, 'Como água fresca para a garganta sedenta é a boa notícia que chega de uma terra distante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17140, 'nvi', 'Provérbios', 'pv', 25, 26, 'Como fonte contaminada ou nascente poluída, assim é o justo que fraqueja diante do ímpio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17141, 'nvi', 'Provérbios', 'pv', 25, 27, 'Comer mel demais não é bom, nem é honroso buscar a própria honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17142, 'nvi', 'Provérbios', 'pv', 25, 28, 'Como a cidade com seus muros derrubados, assim é quem não sabe dominar-se.');
