INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17143, 'nvi', 'Provérbios', 'pv', 26, 1, 'Como neve no verão ou chuva na colheita, assim a honra é imprópria para o tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17144, 'nvi', 'Provérbios', 'pv', 26, 2, 'Como o pardal que voa em fuga, e a andorinha que esvoaça veloz, assim a maldição sem motivo justo não pega.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17145, 'nvi', 'Provérbios', 'pv', 26, 3, 'O chicote é para o cavalo, o freio, para o jumento, e a vara, para as costas do tolo!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17146, 'nvi', 'Provérbios', 'pv', 26, 4, 'Não responda ao insensato com igual insensatez, do contrário você se igualará a ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17147, 'nvi', 'Provérbios', 'pv', 26, 5, 'Responda ao insensato como a sua insensatez merece, do contrário ele pensará que é mesmo um sábio.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17148, 'nvi', 'Provérbios', 'pv', 26, 6, 'Como cortar o próprio pé ou beber veneno, assim é enviar mensagem pelas mãos do tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17149, 'nvi', 'Provérbios', 'pv', 26, 7, 'Como pendem inúteis as pernas do coxo, assim é o provérbio na boca do tolo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17150, 'nvi', 'Provérbios', 'pv', 26, 8, 'Como amarrar uma pedra na atiradeira, assim é prestar honra ao insensato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17151, 'nvi', 'Provérbios', 'pv', 26, 9, 'Como ramo de espinhos nas mãos do bêbado, assim é o provérbio na boca do insensato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17152, 'nvi', 'Provérbios', 'pv', 26, 10, 'Como o arqueiro que atira ao acaso, assim é quem contrata o tolo ou o primeiro que passa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17153, 'nvi', 'Provérbios', 'pv', 26, 11, 'Como o cão volta ao seu vômito, assim o insensato repete a sua insensatez.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17154, 'nvi', 'Provérbios', 'pv', 26, 12, 'Você conhece alguém que se julga sábio? Há mais esperança para o insensato do que para ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17155, 'nvi', 'Provérbios', 'pv', 26, 13, 'O preguiçoso diz: "Lá está um leão no caminho, um leão feroz rugindo nas ruas! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17156, 'nvi', 'Provérbios', 'pv', 26, 14, 'Como a porta gira em suas dobradiças, assim o preguiçoso se revira em sua cama.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17157, 'nvi', 'Provérbios', 'pv', 26, 15, 'O preguiçoso coloca a mão no prato, mas acha difícil demais levá-la de volta à boca.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17158, 'nvi', 'Provérbios', 'pv', 26, 16, 'O preguiçoso considera-se mais sábio do que sete homens que respondem com bom senso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17159, 'nvi', 'Provérbios', 'pv', 26, 17, 'Como alguém que pega pelas orelhas um cão qualquer, assim é quem se mete em discussão alheia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17160, 'nvi', 'Provérbios', 'pv', 26, 18, 'Como o louco que atira brasas e flechas mortais,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17161, 'nvi', 'Provérbios', 'pv', 26, 19, 'assim é o homem que engana o seu próximo e diz: "Eu estava só brincando! "');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17162, 'nvi', 'Provérbios', 'pv', 26, 20, 'Sem lenha a fogueira se apaga; sem o caluniador morre a contenda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17163, 'nvi', 'Provérbios', 'pv', 26, 21, 'O que o carvão é para as brasas e a lenha para a fogueira, o amigo de brigas é para atiçar discórdias.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17164, 'nvi', 'Provérbios', 'pv', 26, 22, 'As palavras do caluniador são como petiscos deliciosos; descem saborosos até o íntimo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17165, 'nvi', 'Provérbios', 'pv', 26, 23, 'Como uma camada de esmalte sobre um vaso de barro, os lábios amistosos podem ocultar um coração mau.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17166, 'nvi', 'Provérbios', 'pv', 26, 24, 'Quem odeia, disfarça as suas intenções com os lábios, mas no coração abriga a falsidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17167, 'nvi', 'Provérbios', 'pv', 26, 25, 'Embora a sua conversa seja mansa, não acredite nele, pois o seu coração está cheio de maldade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17168, 'nvi', 'Provérbios', 'pv', 26, 26, 'Ele pode fingir e esconder o seu ódio, mas a sua maldade será exposta em público.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17169, 'nvi', 'Provérbios', 'pv', 26, 27, 'Quem faz uma cova, nela cairá; se alguém rola uma pedra, esta rolará de volta sobre ele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17170, 'nvi', 'Provérbios', 'pv', 26, 28, 'A língua mentirosa odeia aqueles a quem fere, e a boca lisonjeira provoca a ruína.');
