INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17171, 'nvi', 'Provérbios', 'pv', 27, 1, 'Não se gabe do dia de amanhã, pois você não sabe o que este ou aquele dia poderá trazer.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17172, 'nvi', 'Provérbios', 'pv', 27, 2, 'Que outros façam elogios a você, não a sua própria boca; outras pessoas, não os seus próprios lábios.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17173, 'nvi', 'Provérbios', 'pv', 27, 3, 'A pedra é pesada e a areia é um fardo, mas a irritação causada pelo insensato é mais pesada do que as duas juntas.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17174, 'nvi', 'Provérbios', 'pv', 27, 4, 'O rancor é cruel e a fúria é destrutiva, mas quem consegue suportar a inveja?');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17175, 'nvi', 'Provérbios', 'pv', 27, 5, 'Melhor é a repreensão feita abertamente do que o amor oculto.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17176, 'nvi', 'Provérbios', 'pv', 27, 6, 'Quem fere por amor mostra lealdade, mas o inimigo multiplica beijos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17177, 'nvi', 'Provérbios', 'pv', 27, 7, 'Quem está satisfeito despreza o mel, mas para quem tem fome até o amargo é doce.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17178, 'nvi', 'Provérbios', 'pv', 27, 8, 'Como a ave que vagueia longe do ninho, assim é o homem que vagueia longe do lar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17179, 'nvi', 'Provérbios', 'pv', 27, 9, 'Perfume e incenso trazem alegria ao coração; do conselho sincero do homem nasce uma bela amizade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17180, 'nvi', 'Provérbios', 'pv', 27, 10, 'Não abandone o seu amigo nem o amigo de seu pai; quando for atingido pela adversidade não vá para a casa de seu irmão; melhor é o vizinho próximo do que o irmão distante.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17181, 'nvi', 'Provérbios', 'pv', 27, 11, 'Seja sábio, meu filho, e traga alegria ao meu coração; poderei então responder a quem me desprezar.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17182, 'nvi', 'Provérbios', 'pv', 27, 12, 'O prudente percebe o perigo e busca refúgio; o inexperiente segue adiante e sofre as conseqüências.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17183, 'nvi', 'Provérbios', 'pv', 27, 13, 'Tome-se a veste de quem serve de fiador ao estranho; sirva ela de penhor de quem dá garantia a uma mulher leviana.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17184, 'nvi', 'Provérbios', 'pv', 27, 14, 'A bênção dada aos gritos cedo de manhã, como maldição é recebida.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17185, 'nvi', 'Provérbios', 'pv', 27, 15, 'A esposa briguenta é como o gotejar constante num dia chuvoso;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17186, 'nvi', 'Provérbios', 'pv', 27, 16, 'detê-la é como deter o vento, como apanhar óleo com a mão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17187, 'nvi', 'Provérbios', 'pv', 27, 17, 'Assim como o ferro afia o ferro, o homem afia o seu companheiro.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17188, 'nvi', 'Provérbios', 'pv', 27, 18, 'Quem cuida de uma figueira comerá de seu fruto, e quem trata bem o seu senhor receberá tratamento de honra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17189, 'nvi', 'Provérbios', 'pv', 27, 19, 'Assim como a água reflete o rosto, o coração reflete quem somos nós.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17190, 'nvi', 'Provérbios', 'pv', 27, 20, 'O Sheol e a Destruição são insaciáveis, como insaciáveis são os olhos do homem.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17191, 'nvi', 'Provérbios', 'pv', 27, 21, 'O crisol é para a prata e o forno é para o ouro, mas o que prova o homem são os elogios que recebe.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17192, 'nvi', 'Provérbios', 'pv', 27, 22, 'Ainda que você moa o insensato, como trigo no pilão, a insensatez não se afastará dele.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17193, 'nvi', 'Provérbios', 'pv', 27, 23, 'Esforce-se para saber bem como suas ovelhas estão, dê cuidadosa atenção aos seus rebanhos,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17194, 'nvi', 'Provérbios', 'pv', 27, 24, 'pois as riquezas não duram para sempre, e nada garante que a coroa passe de uma geração a outra.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17195, 'nvi', 'Provérbios', 'pv', 27, 25, 'Quando o feno for retirado, surgirem novos brotos e o capim das colinas for colhido,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17196, 'nvi', 'Provérbios', 'pv', 27, 26, 'os cordeiros lhe fornecerão roupa, e os bodes lhe renderão o preço de um campo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17197, 'nvi', 'Provérbios', 'pv', 27, 27, 'Haverá fartura de leite de cabra para alimentar você e sua família, e para sustentar as suas servas.');
