INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17198, 'nvi', 'Provérbios', 'pv', 28, 1, 'O ímpio foge, embora ninguém o persiga, mas os justos são corajosos como o leão.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17199, 'nvi', 'Provérbios', 'pv', 28, 2, 'Os pecados de uma nação fazem mudar sempre os seus governantes, mas a ordem se mantém com um líder sábio e sensato.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17200, 'nvi', 'Provérbios', 'pv', 28, 3, 'O pobre que se torna poderoso e oprime os pobres é como a tempestade súbita que destrói toda a plantação.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17201, 'nvi', 'Provérbios', 'pv', 28, 4, 'Os que abandonam a lei elogiam os ímpios, mas os que obedecem à lei lutam contra eles.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17202, 'nvi', 'Provérbios', 'pv', 28, 5, 'Os homens maus não entendem a justiça, mas os que buscam ao Senhor a entendem plenamente.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17203, 'nvi', 'Provérbios', 'pv', 28, 6, 'Melhor é o pobre íntegro em sua conduta do que o rico perverso em seus caminhos.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17204, 'nvi', 'Provérbios', 'pv', 28, 7, 'Quem obedece à lei é filho sábio, mas o companheiro dos glutões envergonha o pai.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17205, 'nvi', 'Provérbios', 'pv', 28, 8, 'Quem aumenta sua riqueza com juros exorbitantes ajunta para algum outro, que será bondoso com os pobres.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17206, 'nvi', 'Provérbios', 'pv', 28, 9, 'Se alguém se recusa a ouvir a lei, até suas orações serão detestáveis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17207, 'nvi', 'Provérbios', 'pv', 28, 10, 'Quem leva o homem direito pelo mau caminho cairá ele mesmo na armadilha que preparou, mas o que não se deixa corromper terá boa recompensa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17208, 'nvi', 'Provérbios', 'pv', 28, 11, 'O rico pode até se julgar sábio, mas o pobre que tem discernimento o conhece a fundo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17209, 'nvi', 'Provérbios', 'pv', 28, 12, 'Quando os justos triunfam, há prosperidade geral, mas, quando os ímpios sobem ao poder, os homens tratam de esconder-se.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17210, 'nvi', 'Provérbios', 'pv', 28, 13, 'Quem esconde os seus pecados não prospera, mas quem os confessa e os abandona encontra misericórdia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17211, 'nvi', 'Provérbios', 'pv', 28, 14, 'Como é feliz o homem constante no temor do Senhor! Mas quem endurece o coração cairá na desgraça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17212, 'nvi', 'Provérbios', 'pv', 28, 15, 'Como um leão que ruge ou um urso feroz é o ímpio que governa um povo necessitado.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17213, 'nvi', 'Provérbios', 'pv', 28, 16, 'O governante sem discernimento aumenta as opressões, mas os que odeiam o ganho desonesto prolongarão o seu governo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17214, 'nvi', 'Provérbios', 'pv', 28, 17, 'O assassino atormentado pela culpa será fugitivo até a morte; que ninguém o proteja!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17215, 'nvi', 'Provérbios', 'pv', 28, 18, 'Quem procede com integridade viverá seguro, mas quem procede com perversidade de repente cairá.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17216, 'nvi', 'Provérbios', 'pv', 28, 19, 'Quem lavra sua terra terá comida com fartura, mas quem persegue fantasias se fartará de miséria.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17217, 'nvi', 'Provérbios', 'pv', 28, 20, 'O fiel será ricamente abençoado, mas quem tenta enriquecer-se depressa não ficará sem castigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17218, 'nvi', 'Provérbios', 'pv', 28, 21, 'Agir com parcialidade não é bom; pois até por um pedaço de pão o homem se dispõe a fazer o mal.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17219, 'nvi', 'Provérbios', 'pv', 28, 22, 'O invejoso é ávido por riquezas, e não percebe que a pobreza o aguarda.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17220, 'nvi', 'Provérbios', 'pv', 28, 23, 'Quem repreende o próximo obterá por fim mais favor do que aquele que só sabe bajular.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17221, 'nvi', 'Provérbios', 'pv', 28, 24, 'Quem rouba seu pai ou sua mãe e diz: "Não é errado" é amigo de quem destrói.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17222, 'nvi', 'Provérbios', 'pv', 28, 25, 'O ganancioso provoca brigas, mas quem confia no Senhor prosperará.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17223, 'nvi', 'Provérbios', 'pv', 28, 26, 'Quem confia em si mesmo é insensato, mas quem anda segundo a sabedoria não corre perigo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17224, 'nvi', 'Provérbios', 'pv', 28, 27, 'Quem dá aos pobres não passará necessidade, mas quem fecha os olhos para não vê-los sofrerá muitas maldições.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17225, 'nvi', 'Provérbios', 'pv', 28, 28, 'Quando os ímpios sobem ao poder, o povo se esconde; mas, quando eles sucumbem, os justos florescem.');
