INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17253, 'nvi', 'Provérbios', 'pv', 30, 1, 'Ditados de Agur, filho de Jaque; oráculo: Este homem declarou a Itiel; a Itiel e a Ucal:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17254, 'nvi', 'Provérbios', 'pv', 30, 2, '"Sou o mais tolo dos homens; não tenho o entendimento de um ser humano.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17255, 'nvi', 'Provérbios', 'pv', 30, 3, 'Não aprendi sabedoria, nem tenho conhecimento do Santo.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17256, 'nvi', 'Provérbios', 'pv', 30, 4, 'Quem subiu aos céus e desceu? Quem ajuntou nas mãos os ventos? Quem embrulhou as águas em sua capa? Quem fixou todos os limites da terra? Qual é o seu nome, e o nome do seu filho? Conte-me, se você sabe!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17257, 'nvi', 'Provérbios', 'pv', 30, 5, '"Cada palavra de Deus é comprovadamente pura; ele é um escudo para quem nele se refugia.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17258, 'nvi', 'Provérbios', 'pv', 30, 6, 'Nada acrescente às palavras dele, do contrário, ele o repreenderá e mostrará que você é mentiroso.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17259, 'nvi', 'Provérbios', 'pv', 30, 7, '"Duas coisas peço que me dês antes que eu morra:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17260, 'nvi', 'Provérbios', 'pv', 30, 8, 'Mantém longe de mim a falsidade e a mentira; Não me dês nem pobreza nem riqueza; dá-me apenas o alimento necessário.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17261, 'nvi', 'Provérbios', 'pv', 30, 9, 'Se não, tendo demais, eu te negaria e te deixaria, e diria: ‘Quem é o Senhor? ’ Se eu ficasse pobre, poderia vir a roubar, desonrando assim o nome do meu Deus.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17262, 'nvi', 'Provérbios', 'pv', 30, 10, '"Não fale mal do servo ao seu senhor; do contrário, o servo o amaldiçoará, e você levará a culpa.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17263, 'nvi', 'Provérbios', 'pv', 30, 11, '"Existem os que amaldiçoam seu pai e não abençoam sua mãe;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17264, 'nvi', 'Provérbios', 'pv', 30, 12, 'os que são puros aos seus próprios olhos e que ainda não foram purificados da sua impureza;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17265, 'nvi', 'Provérbios', 'pv', 30, 13, 'os que têm olhos altivos e olhar desdenhoso;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17266, 'nvi', 'Provérbios', 'pv', 30, 14, 'pessoas cujos dentes são espadas e cujas mandíbulas estão armadas de facas para devorarem os necessitados desta terra e os pobres da humanidade.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17267, 'nvi', 'Provérbios', 'pv', 30, 15, '"Duas filhas tem a sanguessuga. ‘Dê! Dê! ’, gritam elas. "Há três coisas que nunca estão satisfeitas, quatro que nunca dizem: ‘É o bastante! ’:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17268, 'nvi', 'Provérbios', 'pv', 30, 16, 'O Sheol, o ventre estéril, a terra, que nunca se dessedenta, e o fogo, que nunca diz: ‘É o bastante! ’');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17269, 'nvi', 'Provérbios', 'pv', 30, 17, '"Os olhos de quem zomba do pai, e, zombando, nega obediência à mãe, serão arrancados pelos corvos do vale, e serão devorados pelos filhotes do abutre.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17270, 'nvi', 'Provérbios', 'pv', 30, 18, '"Há três coisas misteriosas demais para mim, quatro que não consigo entender:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17271, 'nvi', 'Provérbios', 'pv', 30, 19, 'O caminho do abutre no céu, o caminho da serpente sobre a rocha, o caminho do navio em alto mar, e o caminho do homem com uma moça.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17272, 'nvi', 'Provérbios', 'pv', 30, 20, '"Este é o caminho da adúltera: Ela come e limpa a boca, e diz: ‘Não fiz nada de errado’.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17273, 'nvi', 'Provérbios', 'pv', 30, 21, '"Três coisas fazem tremer a terra, e quatro ela não pode suportar:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17274, 'nvi', 'Provérbios', 'pv', 30, 22, 'O escravo que se torna rei, o insensato farto de comida,');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17275, 'nvi', 'Provérbios', 'pv', 30, 23, 'a mulher desprezada que por fim se casa, e a escrava que toma o lugar de sua senhora.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17276, 'nvi', 'Provérbios', 'pv', 30, 24, '"Quatro seres da terra são pequenos, e, no entanto, muito sábios:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17277, 'nvi', 'Provérbios', 'pv', 30, 25, 'As formigas, criaturas de pouca força, contudo, armazenam sua comida no verão;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17278, 'nvi', 'Provérbios', 'pv', 30, 26, 'os coelhos, criaturas sem nenhum poder, contudo, habitam nos penhascos;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17279, 'nvi', 'Provérbios', 'pv', 30, 27, 'os gafanhotos, que não têm rei, contudo, avançam juntos em fileiras;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17280, 'nvi', 'Provérbios', 'pv', 30, 28, 'a lagartixa, que se pode apanhar com as mãos, contudo, encontra-se nos palácios dos reis.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17281, 'nvi', 'Provérbios', 'pv', 30, 29, '"Há três seres de andar elegante, quatro que se movem com passo garboso:');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17282, 'nvi', 'Provérbios', 'pv', 30, 30, 'O leão, que é poderoso entre os animais e não foge de ninguém;');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17283, 'nvi', 'Provérbios', 'pv', 30, 31, 'o galo de andar altivo; o bode; e o rei à frente do seu exército.');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17284, 'nvi', 'Provérbios', 'pv', 30, 32, '"Se você agiu como tolo e exaltou-se a si mesmo, ou se planejou o mal, tape a boca com a mão!');
INSERT INTO bible (id, version, book, abbrev, chapter, verse, text) VALUES (17285, 'nvi', 'Provérbios', 'pv', 30, 33, 'Pois assim como bater o leite produz manteiga, e assim como torcer o nariz produz sangue, também suscitar a raiva produz contenda".');
